import React, { useEffect, useState } from "react";
import Navbar from "../../../components/Navbar";
import { Link, useNavigate, useParams } from "react-router-dom";
import MeasurementsForm from "./MeasurementsForm";
import Footer from "../../../components/Footer";
import { toast } from "react-toastify";
import axios from "axios";
import { useStoreState } from "easy-peasy";

const EditMeasurement = () => {
  const { id } = useParams();
  const { measurementId } = useParams();
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [customer, setCustomer] = useState(null);
  const [measurement, setMeasurement] = useState(null);
  const userLanguage = useStoreState((state) => state.userLanguage);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/customers"
    : "https://khamco-shop-backend.onrender.com/customers";
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const [date, setDate] = useState(null);
  const [clothingOptions, setClothingOptions] = useState([]);
  const [description, setDescription] = useState("");
  const [waist, setWaist] = useState(0);
  const [naturalWaist, setNaturalWaist] = useState(0);
  const [garmentLength, setGarmentLength] = useState(0);
  const [halfAcross, setHalfAcross] = useState(0);
  const [chest, setChest] = useState(0);
  const [trouserWaistPosition, setTrouserWaistPosition] = useState(0);
  const [seat, setSeat] = useState(0);
  const [neck, setNeck] = useState(0);
  const [shoulder, setShoulder] = useState(0);
  const [sleeveLength, setSleeveLength] = useState(0);
  const [sideSeamLength, setSideSeamLength] = useState(0);
  const [bodyRise, setBodyRise] = useState(0);
  const [insideLeg, setInsideLeg] = useState(0);
  const [wrist, setWrist] = useState(0);
  const [topArm, setTopArm] = useState(0);
  const [scyeDepth, setScyeDepth] = useState(0);
  const [armSleeveHole, setArmSleeveHole] = useState(0);
  const [frontalCollar, setFrontalCollar] = useState(0);
  const [hip, setHip] = useState(0);
  const [frontRise, setFrontRise] = useState(0);
  const [backRise, setBackRise] = useState(0);
  const [thigh, setThigh] = useState(0);
  const [knee, setKnee] = useState(0);
  const [hem, setHem] = useState(0);
  const [outseam, setOutseam] = useState(0);
  const [footSize, setFootSize] = useState(0);
  const [headSize, setHeadSize] = useState(0);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      await axios
        .get(`${apiUrl}/edit/${id}/measurements/${measurementId}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setCustomer(response.data.customer);
          setMeasurement(response.data.measurement);
        })
        .catch((error) => {
          toast.error(error.message);
          navigate(
            `/customers/${customer._id}/measurements/${measurement._id}`
          );
        });
    };

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      } else {
        setWritingDirection("ltr");
      }
    };
    determineWritingDirection();
    return () => {
      fetchData();
    };
  }, [apiUrl, authKey, userLanguage]);

  const handleSubmit = async (event) => {
    setLoading(true);
    event.preventDefault();
    console.log(clothingOptions)
    // try {
    //   const response = await axios.put(
    //     `${apiUrl}/${id}/measurements/${measurementId}`,
    //     {
    //       date_created: date,
    //       description: description,
    //       clothing_options: clothingOptions,
    //       waist: waist,
    //       natural_waist: naturalWaist,
    //       garment_length: garmentLength,
    //       half_across: halfAcross,
    //       chest: chest,
    //       trouser_waist_position: trouserWaistPosition,
    //       seat: seat,
    //       neck: neck,
    //       shoulder: shoulder,
    //       sleeve_length: sleeveLength,
    //       side_seam_length: sideSeamLength,
    //       body_rise: bodyRise,
    //       inside_leg: insideLeg,
    //       wrist: wrist,
    //       top_arm: topArm,
    //       scye_depth: scyeDepth,
    //       arm_sleeve_hole: armSleeveHole,
    //       frontal_collar: frontalCollar,
    //       hip: hip,
    //       front_rise: frontRise,
    //       back_rise: backRise,
    //       thigh: thigh,
    //       knee: knee,
    //       hem: hem,
    //       outseam: outseam,
    //       foot_size: footSize,
    //       head_size: headSize,
    //     },
    //     {
    //       headers: { Authorization: `Bearer ${authKey}` },
    //     }
    //   );

    //   toast.success(response.data.message);
    //   setCustomer(response.data.customer);
    //   navigate(
    //     `/customers/${response.data.customer._id}/measurements/${response.data.measurement._id}`
    //   );
    //   setLoading(false);
    // } catch (error) {
    //   setLoading(false);
    //   if (error.response) {
    //     const { status, data } = error.response;

    //     if (status === 404) {
    //       if (data.message === "Customer not found") {
    //         toast.error("Customer not found");
    //       } else if (data.message === "Dependent not found") {
    //         toast.error("Dependent not found");
    //       } else if (data.message === "Date is required") {
    //         toast.error("The date field is required");
    //       } else {
    //         toast.error("Resource not found");
    //       }
    //     } else if (status === 400) {
    //       toast.error("Invalid ObjectId format");
    //     } else {
    //       toast.error("An error occurred while updating dependent details");
    //     }
    //   } else {
    //     toast.error("An error occurred while updating dependent details");
    //   }
    // }
  };

  return (
    <>
      <Navbar />
      <main>
        <section className="khamco-wrapper" dir={writingDirection}>
          {customer && measurement && (
            <>
              <div className="utilities">
                <Link
                  to={`/customers/${customer && customer._id}/measurements`}
                >
                  <i className="fas fa-arrow-left"></i> Measurements
                </Link>
              </div>
              <h1 className="title">{`Edit Measurements for ${new Date(
                measurement.date_created
              ).toLocaleDateString("en-US", {
                month: "long",
                day: "numeric",
                year: "numeric",
              })}`}</h1>
            </>
          )}
          <form
            onSubmit={handleSubmit}
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <div className="form-row">
              <label>Enter New Date</label>
              <input
                type="date"
                onChange={(e) => {
                  setDate(e.target.value);
                }}
              />
            </div>
            {measurement && measurement.clothing_options.length > 0 && (
              <div className="form-row">
                <h2 className="title">Previously Selected Clothing</h2>
                <div className="inline my-[2rem]">
                  {measurement.clothing_options.map((item) => (
                    <span className="bg-white text-khamco-primary">
                      {item.clothing_item}
                    </span>
                  ))}
                </div>
              </div>
            )}
            <MeasurementsForm
              clothingOptions={clothingOptions}
              setClothingOptions={setClothingOptions}
              setWaist={setWaist}
              setNaturalWaist={setNaturalWaist}
              setGarmentLength={setGarmentLength}
              setHalfAcross={setHalfAcross}
              setChest={setChest}
              setTrouserWaistPosition={setTrouserWaistPosition}
              setSeat={setSeat}
              setNeck={setNeck}
              setShoulder={setShoulder}
              setSleeveLength={setSleeveLength}
              setSideSeamLength={setSideSeamLength}
              setBodyRise={setBodyRise}
              setInsideLeg={setInsideLeg}
              setWrist={setWrist}
              setTopArm={setTopArm}
              setScyeDepth={setScyeDepth}
              setArmSleeveHole={setArmSleeveHole}
              setFrontalCollar={setFrontalCollar}
              setHip={setHip}
              setFrontRise={setFrontRise}
              setBackRise={setBackRise}
              setThigh={setThigh}
              setKnee={setKnee}
              setHem={setHem}
              setOutseam={setOutseam}
              setHeadSize={setHeadSize}
              setFootSize={setFootSize}
              setDescription={setDescription}
              loading={loading}
            />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default EditMeasurement;
