import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import { Link, useNavigate, useParams } from "react-router-dom";
import Footer from "../../components/Footer";
import axios from "axios";
import { toast } from "react-toastify";

const OrderDetails = () => {
  const { id } = useParams();
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [openDialog, setOpenDialog] = useState(false);
  const [order, setOrder] = useState({});
  const [customer, setCustomer] = useState({});
  const [users, setUsers] = useState([]);
  const [measurer, setMeasurer] = useState("");
  const [cutter, setCutter] = useState("");
  const [sewer, setSewer] = useState("");
  const [loading, setLoading] = useState(true);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000`
    : `https://khamcotailor.com`;
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();

  useEffect(() => {
    document.title = "Order Details - Khamco Shop";

    const fetchOrder = async () => {
      await axios
        .get(`${apiUrl}/orders/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setLoading(false);
          setOrder(response.data.order);
          setCustomer(response.data.customer);
          setUsers(response.data.users);
        })
        .catch((error) => {
          setOrder(null);
          setCustomer(null);
          setUsers(null);
          setLoading(true);
          toast.error(error.message);
        });
    };

    return () => {
      fetchOrder();
    };
  }, [apiUrl, authKey, id]);

  const deleteEntireOrder = async (e) => {
    e.preventDefault();
    await axios
      .delete(`${apiUrl}/orders/${order._id}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      })
      .then((response) => {
        toast.success(response.data.message);
        navigate("/orders");
      })
      .catch(() => {
        toast.error("Unable to delete order");
      });
  };

  const statusToInProgress = async (e) => {
    e.preventDefault();
    await axios.put(`${apiUrl}/${order._id}/in_progress`, {
      headers: { Authorization: `Bearer ${authKey}` },
    }).then(response => {
      setOrder(response.data.order);
    }).catch(error => {
      toast.error(error.message);
    });
  };

  return (
    <>
      <Navbar />
      <main>
        <section className="customer-details" dir={writingDirection}>
          <div className="utilities">
            <Link to="/orders">
              <i className="fas fa-arrow-left"></i> All Orders
            </Link>
          </div>
          <div className="khamco-card">
            <div className="utilities">
              <button
                className="edit-btn"
                id="openDialog"
                onClick={(e) => setOpenDialog(true)}
              >
                Edit
              </button>
              <button className="print-btn" onClick={() => window.print()}>
                Print <i className="fas fa-print"></i>
              </button>
              <form onSubmit={deleteEntireOrder}>
                <button type="submit" className="delete-btn">
                  Delete <i className="fas fa-times"></i>
                </button>
              </form>
              <form onSubmit={statusToInProgress}>
                <button
                  className={`order-btn ${
                    order.order_status === "In Progress" ? "bg-[goldenrod]" : ""
                  }`}
                  type="submit"
                >
                  In Progress
                </button>
              </form>
              <form action="/orders/<%= order.id %>/complete" method="POST">
                <button
                  className={`order-btn ${
                    order.order_status === "Complete" ? "bg-[goldenrod]" : ""
                  }`}
                >
                  Complete
                </button>
              </form>
              <form action="/orders/<%= order.id %>/awaiting" method="POST">
                <button
                  className={`order-btn ${
                    order.order_status === "Awaiting Delivery"
                      ? "bg-[goldenrod]"
                      : ""
                  }`}
                >
                  Awaiting Delivery
                </button>
              </form>
              <form action="/orders/<%= order.id %>/delivered" method="POST">
                <button
                  className={`order-btn ${
                    order.order_status === "Delivered" ? "bg-[goldenrod]" : ""
                  }`}
                >
                  {" "}
                  Delivered
                </button>
              </form>
            </div>
            <h1 className="title">Order Details</h1>
            <div className="flex flex-col gap-[1rem]">
              <span className="inline-flex justify-start gap-[2rem] text-balance">
                <strong>Order Id</strong>
                <p>{order._id}</p>
              </span>
              <span className="inline-flex justify-start gap-[2rem] text-balance">
                <strong>Customer Details</strong>
                <ul>
                  <li>
                    <Link to={`/customers/${customer._id}`} target="_blank">
                      {`${order.customer_name} ${order.customer_surname}`}
                    </Link>
                  </li>
                  {order.customers &&
                    order.customers.map((customer, index) => (
                      <li key={index}>{customer.customer_name}</li>
                    ))}
                </ul>
              </span>
              <span className="inline-flex justify-start gap-[2rem] text-balance">
                <strong>Email</strong>
                <Link to="mailto:<%= customer.email %>">{customer.email}</Link>
              </span>
              <span className="inline-flex justify-start gap-[2rem] text-balance">
                <strong>Order Details</strong>
                <ul>
                  {order.name &&
                    order.name.map((order, index) => (
                      <li key={index}>{order.choice}</li>
                    ))}
                </ul>
              </span>
              <span className="inline-flex justify-start gap-[2rem] text-balance">
                <strong>Order Status</strong>
                <p>{order.order_status}</p>
              </span>
              <span className="inline-flex justify-start gap-[2rem] text-balance">
                <strong>Phone Number</strong>
                <Link to="tel:<%= customer.phone %>">{customer.phone}</Link>
              </span>
              <span className="inline-flex justify-start gap-[2rem] text-balance">
                <strong>Address</strong>
                <p>{customer.address}</p>
              </span>
              <span className="inline-flex justify-start gap-[2rem] text-balance">
                <strong>Measurer</strong>
                <p>{order.measurer}</p>
              </span>
              <span className="inline-flex justify-start gap-[2rem] text-balance">
                <strong>Cutter</strong>
                <p>{order.cutter}</p>
              </span>
              <span className="inline-flex justify-start gap-[2rem] text-balance">
                <strong>Sewer</strong>
                <p>{order.sewer}</p>
              </span>
              <span className="inline-flex justify-start gap-[2rem] text-balance">
                <strong>Other Details</strong>
                <p>{order.order_notes}</p>
              </span>
              <span className="inline-flex justify-start gap-[2rem] text-balance">
                <strong>Order Start Date</strong>
                <p>{order.order_start_time}</p>
              </span>
              {order.in_progress_duration && (
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>In Progress:</strong>
                  <p>
                    {order.in_progress_time}
                    <strong> after </strong>
                    {order.in_progress_duration.years} years,{" "}
                    {order.in_progress_duration.months} months,{" "}
                    {order.in_progress_duration.weeks} weeks,{" "}
                    {order.in_progress_duration.days} days,{" "}
                    {order.in_progress_duration.hours} hours,{" "}
                    {order.in_progress_duration.minutes} minutes,{" "}
                    {order.in_progress_duration.seconds} seconds.
                  </p>
                </span>
              )}
              {order.delivered_time ? (
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <p className="text-green-500">
                    Order has been delivered.{" "}
                    <i className="fas fa-thumbs-up"></i>
                  </p>
                </span>
              ) : (
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <p className="text-red-500 transform-none">
                    Order is not yet in progress.
                  </p>
                </span>
              )}
              {order.completed_time && (
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Complete:</strong>{" "}
                  <p>
                    {order.completed_time}
                    <strong> after </strong> {order.completed_duration.years}{" "}
                    years, {order.completed_duration.months} months,{" "}
                    {order.completed_duration.weeks} weeks,{" "}
                    {order.completed_duration.days} days,{" "}
                    {order.completed_duration.hours} hours,{" "}
                    {order.completed_duration.minutes} minutes,{" "}
                    {order.completed_duration.seconds} seconds.
                  </p>
                </span>
              )}

              {order.delivered_time && (
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <p className="text-green-500">
                    Order has been delivered.{" "}
                    <i className="fas fa-thumbs-up"></i>
                  </p>
                </span>
              )}

              {!order.delivered_time && !order.completed_time && (
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <p className="text-red-500 transform:none">
                    Order is not yet complete.
                  </p>
                </span>
              )}
              {order.awaiting_time && (
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Awaiting Delivery:</strong>{" "}
                  <p>
                    {order.awaiting_time}
                    <strong> after </strong> {order.awaiting_duration.years}{" "}
                    years, {order.awaiting_duration.months} months,{" "}
                    {order.awaiting_duration.weeks} weeks,{" "}
                    {order.awaiting_duration.days} days,{" "}
                    {order.awaiting_duration.hours} hours,{" "}
                    {order.awaiting_duration.minutes} minutes,{" "}
                    {order.awaiting_duration.second} seconds.
                  </p>
                </span>
              )}

              {order.delivered_time && (
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <p className="text-green-500">
                    Order has been delivered.{" "}
                    <i className="fas fa-thumbs-up"></i>
                  </p>
                </span>
              )}

              {!order.awaiting_time && !order.delivered_time && (
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <p className="text-red-500 transform:none">
                    Order is not yet ready for delivery.
                  </p>
                </span>
              )}

              {order.delivered_time && (
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Delivered:</strong>{" "}
                  <p>
                    {order.delivered_time}
                    <strong> after </strong> {order.delivered_duration.years}{" "}
                    years, {order.delivered_duration.months} months,{" "}
                    {order.awaiting_duration.weeks} weeks,{" "}
                    {order.delivered_duration.days} days,{" "}
                    {order.delivered_duration.hours} hours,{" "}
                    {order.delivered_duration.minutes} minutes,{" "}
                    {order.delivered_duration.seconds} seconds.
                  </p>
                </span>
              )}
              {!order.delivered_time && (
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <p className="text-red-500 transform-none">
                    Order has not been delivered.
                  </p>
                </span>
              )}
              <span className="inline-flex justify-start gap-[2rem] text-balance">
                <strong>Order End Date</strong>
                <p>{order.order_end_time}</p>
              </span>
              {order.order_end_time && (
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Order Duration</strong>
                  <p>
                    {order.order_duration.years} years,{" "}
                    {order.order_duration.months} months,{" "}
                    {order.order_duration.weeks} weeks,{" "}
                    {order.order_duration.days} days,{" "}
                    {order.order_duration.hours} hours,{" "}
                    {order.order_duration.minutes} minutes,{" "}
                    {order.order_duration.seconds} seconds.
                  </p>
                </span>
              )}
              {!order.order_end_time && (
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <p className="text-red-500 transform:none">
                    Order is still under <strong>{order.order_status}</strong>{" "}
                    status. The duration will be calculated after delivery.
                  </p>
                </span>
              )}
            </div>
          </div>
        </section>
        <dialog
          className="fixed top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 w-2/4 rounded-[2rem] border-khamco-primary overflow-y-auto overflow-x-hidden max-h-[80vh] py-[2rem] px-0 fadeIn"
          open={openDialog}
        >
          <div className="p-[4rem] grid justify-center items-center">
            <form
              action="/orders/<%= order.id %>?_method=PUT"
              method="POST"
              className="flex flex-col justify-center items-center w-[90vw]"
            >
              <button
                className="delete-btn absolute top-[5%] left-[5%]"
                type="button"
                onClick={() => setOpenDialog(false)}
              >
                <i className="fas fa-times"></i>
              </button>
              <div className="form-row">
                <h2 className="title">Update Order Details</h2>
              </div>
              <div className="form-row">
                <label htmlFor="measurer">Measurer</label>
                <select
                  name="measurer"
                  id="measurer"
                  onChange={(e) => setMeasurer(e.target.value)}
                >
                  {users.map((user, index) => (
                    <option
                      key={index}
                      label={`${user.name} ${user.surname}`}
                      value={`${user.name} ${user.surname}`}
                    ></option>
                  ))}
                </select>
              </div>
              <div className="form-row">
                <label htmlFor="cutter">Cutter</label>
                <select
                  name="cutter"
                  id="cutter"
                  onChange={(e) => setCutter(e.target.value)}
                >
                  {users.map((user, index) => (
                    <option
                      key={index}
                      label={`${user.name} ${user.surname}`}
                      value={`${user.name} ${user.surname}`}
                    ></option>
                  ))}
                </select>
              </div>
              <div className="form-row">
                <label htmlFor="sewer">Sewer</label>
                <select
                  name="sewer"
                  id="sewer"
                  onChange={(e) => setSewer(e.target.value)}
                >
                  {users.map((user, index) => (
                    <option
                      key={index}
                      label={`${user.name} ${user.surname}`}
                      value={`${user.name} ${user.surname}`}
                    ></option>
                  ))}
                </select>
              </div>
              <div className="form-row">
                <label htmlFor="customer_name">Customer Name</label>
                <input type="text" id="customer_name" name="customer_name" />
              </div>
              <div className="form-row">
                <label htmlFor="customer_surname">Customer Surname</label>
                <input
                  type="text"
                  id="customer_surname"
                  name="customer_surname"
                />
              </div>
              <div className="form-row">
                <label htmlFor="customer_email">Email</label>
                <input type="email" name="customer_email" id="customer_email" />
              </div>
              <div className="form-row">
                <label htmlFor="customer_phone">Phone Number</label>
                <input type="tel" name="customer_phone" id="customer_phone" />
              </div>
              <div className="form-row">
                <label htmlFor="customer_address">Address</label>
                <input
                  type="text"
                  name="customer_address"
                  id="customer_address"
                />
              </div>
              <div className="form-row">
                <label htmlFor="order_notes">Other Details</label>
                <textarea
                  name="order_notes"
                  id="order_notes"
                  placeholder="Optional notes"
                ></textarea>
              </div>
              <div className="form-row">
                <button type="submit">Update</button>
              </div>
            </form>
          </div>
        </dialog>
      </main>
      <Footer />
    </>
  );
};

export default OrderDetails;
