import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { useTranslation } from "react-i18next";

const AdminInvoices = () => {
  const [invoices, setInvoices] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";
  const authKey = localStorage.getItem("jwt");
  const location = useLocation();
  const navigate = useNavigate();
  const { t } = useTranslation();
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [totalInvoices, setTotalInvoices] = useState(0);

  const fetchInvoices = async (queryParams = "") => {
    setIsLoading(true);
    try {
      const response = await axios.get(`${apiUrl}/admin/customers${queryParams}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      setInvoices(response.data.invoices);
      setTotalPages(response.data.totalPages);
      setTotalInvoices(response.data.totalInvoices);
    } catch (error) {
      if (error.response) {
        if (error.response.status === 403 || error.response.status === 400) {
          toast.warn("You are not authorized to access that page");
          navigate("/");
        } else {
          toast.error(error.response.data?.message || error.message);
        }
      } else {
        toast.error("An error occurred: " + error.message);
      }
      setInvoices([]);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    document.title = `Khamco Shop ${t("Invoices")}`;
    const searchParams = new URLSearchParams(location.search);
    searchParams.set("page", currentPage); // Add page param to the query
    fetchInvoices(`?${searchParams.toString()}`);
  }, [authKey, location.search, t, currentPage]);

  const handleSearch = (e) => {
    e.preventDefault();
    const form = new FormData(e.target);
    const searchParams = new URLSearchParams(form);
    searchParams.set("page", currentPage); // Ensure current page is included in search
    navigate(`?${searchParams.toString()}`);
  };

  const handleDelete = async (invoiceID) => {
    setIsLoading(true);
    try {
      const response = await axios.delete(`${apiUrl}/invoices/${invoiceID}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      setInvoices(response.data.invoices);
      setTotalInvoices(response.data.totalInvoices);
      setIsLoading(false);
    } catch (error) {
        setIsLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  }

  // Pagination controls
  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage((prev) => prev + 1);
    }
  };

  const handlePreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prev) => prev - 1);
    }
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[4rem]">
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              to="/invoices/new"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              {t("New Invoice")} <i className="fas fa-plus"></i>
            </Link>
          </div>
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <form
              className="w-full md:w-2/5 flex justify-between mb-8"
              onSubmit={handleSearch}
            >
              <input
                type="text"
                placeholder={`${t("Search Customer Name")}`}
                name="customer_name"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-search"></i>
              </button>
            </form>
            <form
              className="w-full md:w-2/5 flex justify-between mb-8"
              onSubmit={handleSearch}
            >
              <input
                type="text"
                placeholder={`${t("Search Invoice ID")}`}
                name="uniqueId"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-search"></i>
              </button>
            </form>
            <form
              className="w-full md:w-2/5 flex justify-between mb-8"
              onSubmit={handleSearch}
            >
              <input
                type="date"
                name="date"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-check"></i>
              </button>
            </form>
            <form
              className="w-full md:w-2/5 flex justify-between mb-8"
              onSubmit={handleSearch}
            >
              <select
                name="payment_status"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              >
                <option value="">-- Search Payment Status --</option>
                <option value="Unpaid">Unpaid</option>
                <option value="Partially Paid">Partially Paid</option>
                <option value="Paid">Paid</option>
                <option value="Other">Other</option>
              </select>
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-check"></i>
              </button>
            </form>
            <form
              className="w-full md:w-2/5 flex justify-between mb-8"
              onSubmit={handleSearch}
            >
              <select
                name="payment_type"
                id="payment_type"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              >
                <option value="K-Net">K-Net</option>
                <option value="Cash">Cash</option>
                <option value="Other">Other</option>
              </select>
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-check"></i>
              </button>
            </form>
            <form
              className="w-full md:w-2/5 flex justify-between mb-8"
              onSubmit={handleSearch}
            >
              <input
                type="tel"
                name="phone"
                placeholder={`${t("Phone Number")}`}
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className="w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] rounded-r-lg cursor-pointer hover:opacity-90"
              >
                <i className="fas fa-check"></i>
              </button>
            </form>
          </div>
          <h1 className="text-center text-[2rem] font-minimoBold">{`${t(
            "All Invoices"
          )} (${totalInvoices})`}</h1>
          <div className="flex gap-[0.5rem] items-center">
            <button
              disabled={currentPage === 1}
              onClick={handlePreviousPage}
              className={`${
                currentPage === 1 ? "bg-[#999] opacity-90" : "bg-khamco-primary"
              }  text-[white] p-[0.5rem] rounded-md hover:opacity-80`}
            >
              Previous
            </button>
            <span className="text-[black] p-[0.5rem] font-bold">
              {currentPage} of {totalPages}
            </span>
            <button
              disabled={currentPage === totalPages}
              onClick={handleNextPage}
              className={`${
                currentPage === totalPages
                  ? "bg-[#999] opacity-90"
                  : "bg-khamco-primary"
              }  text-[white] p-[0.5rem] rounded-md hover:opacity-80`}
            >
              Next
            </button>
          </div>
          {isLoading &&
            new Array(3)
              .fill(null)
              .map((_, index) => (
                <div
                  key={`placeholder-${index}`}
                  className="w-full bg-[#ccc] animate-pulse py-[3rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-evenly items-center gap-[2rem]"
                ></div>
              ))}
          <p>(Showing {invoices.length} results)</p>
          {invoices.length > 0 ? (
            invoices.map((invoice, index) => (
              <div
                className="w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-start md:justify-evenly flex-wrap items-center gap-[2rem]"
                dir="auto"
                key={index}
              >
                <p className="capitalize">
                  <strong>{t("Name")}</strong>: {invoice.customer_name}{" "}
                  {invoice.customer_surname}
                </p>
                <p className="capitalize">
                  <strong>{t("Last Measurement")}</strong>:{" "}
                  {new Date(invoice.date).toLocaleDateString("en-us", {
                    month: "long",
                    year: "numeric",
                    day: "numeric",
                  })}
                </p>
                <Link
                  to={`/invoices/${invoice._id}`}
                  className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90 no-underline w-full md:w-[5rem] text-center"
                >
                  {t("View")}
                </Link>
                <button
                  className="py-[0.5rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[red] hover:opacity-90 w-full md:w-[4rem] text-center"
                  onClick={() => handleDelete(invoice._id)}
                >
                  {!isLoading ? t("Delete") : ""}
                  {isLoading ? <i className="fas fa-spinner animate-spin"></i> : ""}
                </button>
              </div>
            ))
          ) : (
            <p className="text-center">No invoice data.</p>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminInvoices;
