import { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";

const OrderForm = ({
  loading,
  setMeasurer,
  setComments,
  authorizedUser,
}) => {
  const [measurerUsers, setMeasurerUsers] = useState([]);
  const devBaseUrl = "http://localhost:5000";
  const prodBaseUrl = "https://khamco-shop-backend.onrender.com";
  const currentBaseUrl =
    window.location.hostname === "localhost" ? devBaseUrl : prodBaseUrl;
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();

  useEffect(() => {
    const getAllUsers = async () => {
      await axios
        .get(`${currentBaseUrl}/orders/create/new-order`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          const users = response.data.users || [];    
          setMeasurerUsers(
            users.filter((user) => Array.isArray(user.role) && user.role.some(r => r.type === "Measurer"))
          );
        })
        .catch((error) => {
          if (error.response) {
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response.data?.message || error.message);
            }
          } else {
            // Handle network errors or errors without a response object
            toast.error("An error occurred: " + error.message);
          }
        });
    };

    getAllUsers();
  }, [authKey, currentBaseUrl, navigate]);

  return (
    <>
    <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Assign Measurer
        </label>
        <select
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          onChange={(e) => setMeasurer(e.target.value)}
        >
          <option label=""></option>
          {measurerUsers &&
            measurerUsers.map((user, index) => (
              <option
                key={index}
                label={`${user.name} ${user.surname}`}
                value={`${user.name} ${user.surname}`}
              ></option>
            ))}
        </select>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Comments
        </label>
        <textarea
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          placeholder="Comments (Optional)"
          onChange={(e) =>
            setComments({
              user: authorizedUser,
              text: e.target.value,
              timestamp: new Date(),
            })
          }
        ></textarea>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <button
          type="submit"
          disabled={loading}
          className="text-center bg-[blue] w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
        >
          {loading ? <i className="fas fa-spinner animate-spin"></i> : "Submit"}
        </button>
      </div>
    </>
  );
};

export default OrderForm;
