import { useState, useEffect } from "react";

const MeasurementList = ({ measurement }) => {
  const [currentUnit, setCurrentUnit] = useState(measurement.unitType);
  const [convertedMeasurement, setConvertedMeasurement] = useState({
    ...measurement,
  });
  const path = window.location.pathname;
  const pathSegments = path.split("/");

  // Assuming the path is structured like /customers/:customerId/measurements/:measurementId
  const isMeasurementPage = pathSegments.includes("measurements");

  // Conversion factor: 1 inch = 2.54 cm
  const convertUnits = (value, toUnit) => {
    if (toUnit === "Centimeters") {
      return (value * 2.54).toFixed(2); // Inches to cm
      setCurrentUnit()
    } else {
      return (value / 2.54).toFixed(2); // cm to inches
    }
  };

  const convertAllValues = (unitType) => {
    const converted = { ...measurement };

    const keysToConvert = [
      "dishdasha_garment_length",
      "dishdasha_front_height",
      "dishdasha_back_height",
      "shirt_sleeve",
      "garment_length",
      "pants_garment_length",
      "shorts_garment_length",
      "dress_garment_length",
      "collar",
      "shoulders",
      "sleeve",
      "dress_sleeve",
      "sleepwear_sleeve",
      "chest",
      "belly",
      "waist",
      "hips",
      "arm_hole",
      "bicep_tricep",
      "elbow",
      "wrist",
      "inseam",
      "thighs",
      "calf",
      "leg_foot",
      "back_width",
      "back_height",
      "front_chest_width",
    ];

    // Convert all values based on the unit type
    keysToConvert.forEach((key) => {
      if (converted[key] > 0) {
        converted[key] = convertUnits(converted[key], unitType);
      }
    });

    setConvertedMeasurement(converted);
  };

  // Automatically convert when the measurement.unit_type changes
  useEffect(() => {
    convertAllValues(measurement.unit_type);
  }, [measurement.unit_type]);

  return (
    <>
      <span className="gap-[1rem] flex">
        <strong className="w-1/4">Clothing Options: </strong>
        <div>
          {measurement.clothing_options &&
          measurement.clothing_options.length > 0 ? (
            measurement.clothing_options.map((item, index) => (
              <span key={index}> {item.clothing_item},</span>
            ))
          ) : (
            <p className="text-[red]">No clothing items selected.</p>
          )}
        </div>
      </span>
      {measurement.unit_type && (
        <span className="inline-flex justify-end items-center gap-[2rem] text-balance flex-wrap">
          <strong>Units: </strong>
          {measurement.unit_type}
          {isMeasurementPage && (
            <button
              className="p-2 my-4 bg-khamco-secondary text-white rounded-md"
              onClick={() =>
                convertAllValues(
                  // measurement.unit_type === "Inches" ? "Centimeters" : "Inches"
                  currentUnit === "Inches" ? "Centimeters" : "Inches"
                )
              }
            >
              Change to{" "}
              {/* {measurement.unit_type === "Inches" ? "Centimeters" : "Inches"} */}
              {currentUnit === "Inches" ? "Centimeters" : "Inches"}
            </button>
          )}
        </span>
      )}
      {/* Garment data - convertedMeasurement contains converted values */}
      {convertedMeasurement.dishdasha_garment_length > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Dishdasha Garment Length: </strong>
          {convertedMeasurement.dishdasha_garment_length}
        </span>
      )}
      {convertedMeasurement.dishdasha_front_height > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Dishdasha Front Height: </strong>
          {convertedMeasurement.dishdasha_front_height}
        </span>
      )}
      {convertedMeasurement.dishdasha_back_height > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Dishdasha Back Height: </strong>
          {convertedMeasurement.dishdasha_back_height}
        </span>
      )}
      {convertedMeasurement.shirt_sleeve > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Shirt Sleeve: </strong>
          {convertedMeasurement.shirt_sleeve}
        </span>
      )}
      {convertedMeasurement.garment_length > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Garment Length: </strong>
          {convertedMeasurement.garment_length}
        </span>
      )}
      {convertedMeasurement.pants_garment_length > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Pants Length: </strong>
          {convertedMeasurement.pants_garment_length}
        </span>
      )}
      {convertedMeasurement.shorts_garment_length > 0 && (
        <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
          <strong>Shorts Length: </strong>
          {convertedMeasurement.shorts_garment_length}
        </span>
