import { useEffect, useRef, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import logo from "../../assets/images/logo.jpg";
import { useTranslation } from "react-i18next";
import { QRCodeCanvas } from "qrcode.react";
import Pending from "../../components/Pending";

const ShowInvoice = () => {
  const { id } = useParams();
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";
  const authKey = localStorage.getItem("jwt");
  const [dialogState, setDialogState] = useState(false);
  const [order, setOrder] = useState([]);
  const [invoice, setInvoice] = useState();
  const [customer, setCustomer] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const [paymentStatus, setPaymentStatus] = useState("");
  const [amountPaid, setAmountPaid] = useState("");
  const [paymentType, setPaymentType] = useState("");
  const invoiceRef = useRef(null);
  const { t, i18n } = useTranslation();
  const [qrCodeData, setQrCodeData] = useState("");

  const generatePdfAfterQrCodeRendered = async (customer) => {
    if (!customer) {
      console.error("Customer data is missing");
      return;
    }

    try {
      const opt = {
        margin: 0.5,
        filename: `${customer.name}-${customer.surname}-invoice.pdf`, // Ensure customer exists here
        html2canvas: {
          scale: 4,
        },
        jsPDF: {
          unit: "in",
          format: "a4",
          orientation: "portrait",
        },
      };

      const html2pdfModule = await import("html2pdf.js");
      const html2pdf = html2pdfModule.default;

      const pdfBlob = await html2pdf()
        .from(invoiceRef.current) // Your reference to the invoice element
        .set(opt)
        .outputPdf("blob");

      const formData = new FormData();
      formData.append(
        "file",
        pdfBlob,
        `${customer.name}-${customer.surname}-invoice.pdf` // Ensure customer exists here
      );

      const response = await fetch(`${apiUrl}/invoices/upload`, {
        method: "POST",
        body: formData,
      });

      const { fileUrl } = await response.json();
      return fileUrl; // Return the uploaded PDF URL
    } catch (error) {
      console.error("Error generating PDF or sharing: ", error);
      toast.error(error?.response?.data?.message || error.message);
    }
  };

  const observeQrCodeRender = async () => {
    return new Promise((resolve) => {
      const qrCodeElement = document.getElementById("qr-code");

      if (qrCodeElement) {
        const observer = new MutationObserver(() => {
          if (qrCodeElement && qrCodeElement.children.length > 0) {
            observer.disconnect(); // Stop observing when QR code is rendered
            resolve();
          }
        });

        observer.observe(qrCodeElement, { childList: true, subtree: true });
      } else {
        resolve(); // If no QR code element found, resolve immediately
      }
    });
  };

  const generatePdfLinkFromServer = async (customer) => {
    if (!customer) {
      console.error("Customer data is missing");
      return;
    }

    try {
      const opt = {
        margin: 0.5,
        filename: `${customer.name}-${customer.surname}-invoice.pdf`, // Ensure customer exists here
        html2canvas: {
          scale: 4,
        },
        jsPDF: {
          unit: "in",
          format: "a4",
          orientation: "portrait",
        },
      };

      const html2pdfModule = await import("html2pdf.js");
      const html2pdf = html2pdfModule.default;
      const pdfBlob = await html2pdf()
        .from(invoiceRef.current)
        .set(opt)
        .outputPdf("blob");

      const formData = new FormData();
      formData.append(
        "file",
        pdfBlob,
        `${customer.name}-${customer.surname}-invoice.pdf` // Ensure customer exists here
      );

      const response = await fetch(`${apiUrl}/invoices/upload`, {
        method: "POST",
        body: formData,
      });

      const { fileUrl } = await response.json();
      return fileUrl;
    } catch (error) {
      console.error("Error generating PDF or sharing: ", error);
      toast.error(error?.response?.data?.message || error.message);
    }
  };

  useEffect(() => {
    let isMounted = true;

    const fetchInvoiceAndGeneratePdf = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`${apiUrl}/invoices/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });

        if (isMounted) {
          const fetchedCustomer = response.data.customer;
          const fetchedInvoice = response.data.invoice;

          if (fetchedCustomer && fetchedInvoice) {
            setCustomer(fetchedCustomer);
            setInvoice(fetchedInvoice);
            setAmountPaid(fetchedInvoice.amount_paid);
            setPaymentStatus(fetchedInvoice.payment_status);
            setPaymentType(fetchedInvoice.payment_type);

            const pdfLink = await generatePdfLinkFromServer(fetchedCustomer);

            if (pdfLink) {
              setQrCodeData(pdfLink); // Set the QR code data

              // Wait for the QR code to render before generating the PDF
              await observeQrCodeRender();

              // Once QR code is rendered, generate the final PDF
              await generatePdfAfterQrCodeRendered(fetchedCustomer);
            }
          } else {
            toast.error("Customer or Invoice data is missing");
          }

          setLoading(false);
        }
      } catch (error) {
        setLoading(false);
        if (error.response) {
          navigate("/");
          toast.error(error.response.data?.message || error.message);
        } else {
          toast.error("An error occurred: " + error.message);
        }
      }
    };

    fetchInvoiceAndGeneratePdf();

    return () => {
      isMounted = false;
    };
  }, [id, navigate, authKey, apiUrl]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.put(
        `${apiUrl}/invoices/${id}`,
        {
          payment_type: paymentType,
          amount_paid: amountPaid,
          payment_status: paymentStatus,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      setInvoice(response.data.invoice);
      setCustomer(response.data.customer);
      setOrder(response.data.order);
      toast.success(response.data.message);
      setDialogState(false);
      setLoading(false);
    } catch (error) {
      setLoading(false);
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const handlePrint = () => {
    const content = invoiceRef.current.innerHTML;
    const printWindow = window.open("", "", "width=800,height=600");

    printWindow.document.write(`
      <html>
        <head>
          <title>Invoice</title>
          <style>
            /* Add necessary styles for printing */
            body {
              font-family: Arial, sans-serif;
              margin: 20px;
            }
            table {
              width: 100%;
              border-collapse: collapse;
              margin-bottom: 2.5rem;
            }
            table, th, td {
              border: 4px solid blue;
            }
            th, td {
              padding: 10px;
              text-align: left;
            }
            .invoice-data{
              display: flex;
              flex-wrap;
              gap: 2rem;
              align-items: center;
              margin: 1rem 0;
              padding: 0 0.2rem; 
            }
            .invoice-data strong {
              color: blue;
              font-size: 1.5rem;
            }
            .invoice-data input {
              border: 0.2rem solid black;
              padding: 0.2rem;
              font-size: 1.25rem;
            }
          </style>
        </head>
        <body>
          ${content}
        </body>
      </html>
    `);

    printWindow.document.write();

    // Close the document and trigger the print
    printWindow.document.close();
    printWindow.focus();
    printWindow.print();
  };

  // Function to handle WhatsApp sharing
  const handleWhatsAppShare = async () => {
    // Encode the URL for WhatsApp sharing
    const whatsappMessage = `Check your invoice: ${qrCodeData}`;
    const encodedMessage = encodeURIComponent(whatsappMessage);

    // Open WhatsApp with the URL and message
    window.open(
      `https://api.whatsapp.com/send?text=${encodedMessage}`,
      "_blank"
    );
  };

  return (
    <>
      {loading && <Pending />}
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]">
          <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              to="/invoices"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              <i className="fas fa-arrow-left"> </i> {t("Invoices")}
            </Link>
          </div>
          <div className="flex justify-start gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <button
              title="Share to Whatsapp"
              className="bg-[limegreen] py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer"
              onClick={handleWhatsAppShare}
              disabled={loading}
            >
              <i className="fab fa-whatsapp fa-2x"></i>
            </button>
            <button
              className="bg-[#666] py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer hover:opacity-90"
              onClick={handlePrint}
            >
              {t("Print")} <i className="fas fa-print"></i>
            </button>
            {order ? (
              <Link
                to={`/orders/${order._id}`}
                className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90"
              >
                {t("View Order")}
              </Link>
            ) : (
              <p className="message">No Orders</p>
            )}
            <button
              className="bg-[gray] py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer hover:bg-[goldenrod]"
              onClick={() => setDialogState(true)}
            >
              {t("Change Payment Details")}: {invoice?.payment_status}{" "}
              <i className="fas fa-money-check"></i>
            </button>
          </div>
          {invoice && (
            <>
              <div
                className="w-full m-auto shadow-md shadow-[#666] p-[1rem]"
                ref={invoiceRef}
              >
                <div className="bg-white m-auto px-0">
                  <div>
                    <span className="w-full flex justify-evenly items-center m-auto">
                      <img src={logo} alt="Logo" />
                    </span>
                  </div>
                  <div>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data`}
                    >
                      <strong className=" text-khamco-secondary text-[0.9rem] md:text-2xl">
                        {t("Invoice No")}
                      </strong>
                      <span className=" p-[0.2rem] text-[0.9rem] md:text-lg">
                        {`${invoice.uniqueId}`}
                      </span>
                    </div>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data`}
                    >
                      <strong className=" text-khamco-secondary text-[0.9rem] md:text-2xl">
                        {t("Date")}
                      </strong>
                      <span className=" p-[0.2rem] text-[0.9rem] md:text-lg">
                        {`${new Date(invoice.date).toLocaleDateString("en-us", {
                          month: "long",
                          day: "2-digit",
                          year: "numeric",
                        })}`}
                      </span>
                    </div>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data`}
                    >
                      <strong className="text-khamco-secondary text-[0.9rem] md:text-2xl">
                        {t("Customer")} ID
                      </strong>
                      <span className=" p-[0.2rem] text-[0.9rem] md:text-lg">
                        {`${customer && customer.uniqueId}`}
                      </span>
                    </div>
                    <div className="overflow-auto">
                      <table className="border-collapse table-fixed w-full my-[1rem] m-auto text-[0.9rem] md:text-[1.2rem]">
                        <thead className="text-khamco-primary font-bold">
                          <tr className="border-[0.2rem] border-khamco-secondary border-t-0">
                            <td className="bg-[#ccc] text-center border-r-2 border-khamco-secondary">
                              {t("Item")}
                            </td>
                            <td className="bg-[#ccc] text-center border-r-2 border-khamco-secondary">
                              {t("Price")}
                            </td>
                            <td className="bg-[#ccc] text-center border-r-2 border-khamco-secondary">
                              {t("Quantity")}
                            </td>
                            <td className="bg-[#ccc] text-center border-r-2 border-khamco-secondary">
                              {t("Total")}
                            </td>
                          </tr>
                        </thead>
                        <tbody>
                          {invoice.details &&
                            invoice.details.map((item, index) => (
                              <tr
                                className="border-[0.2rem] border-khamco-secondary border-t-0"
                                key={index}
                              >
                                <td className="text-center border-r-2 border-khamco-secondary">
                                  {item.name || "--"}
                                </td>
                                <td className="text-center border-r-2 border-khamco-secondary">
                                  {item.price || "--"}
                                </td>
                                <td className="text-center border-r-2 border-khamco-secondary">
                                  {item.quantity || "--"}
                                </td>
                                <td className="text-center border-r-2 border-khamco-secondary">
                                  {item.quantity * item.price}
                                </td>
                              </tr>
                            ))}
                        </tbody>
                      </table>
                    </div>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data`}
                    >
                      <strong className="text-khamco-secondary text-[0.9rem] md:text-2xl">
                        {t("Subtotal")}
                      </strong>
                      <span className=" p-[0.2rem] text-[0.9rem] md:text-lg">
                        {new Intl.NumberFormat("kw-KW", {
                          style: "currency",
                          currency: "KWD",
                          minimumFractionDigits: 3,
                          maximumFractionDigits: 3,
                        }).format(invoice.total_price)}
                      </span>
                    </div>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data`}
                    >
                      <strong className="text-khamco-secondary text-[0.9rem] md:text-2xl">
                        {t("Total")}
                      </strong>
                      <span className=" p-[0.2rem] text-[0.9rem] md:text-lg">
                        {new Intl.NumberFormat("kw-KW", {
                          style: "currency",
                          currency: "KWD",
                          minimumFractionDigits: 3,
                          maximumFractionDigits: 3,
                        }).format(invoice.total_price)}
                      </span>
                    </div>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data`}
                    >
                      <strong className="text-khamco-secondary text-[0.9rem] md:text-2xl">
                        {t("Amount Paid")}
                      </strong>
                      <span className=" p-[0.2rem] text-[0.9rem] md:text-lg">
                        {new Intl.NumberFormat("kw-KW", {
                          style: "currency",
                          currency: "KWD",
                          minimumFractionDigits: 3,
                          maximumFractionDigits: 3,
                        }).format(invoice.amount_paid)}
                      </span>
                    </div>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data`}
                    >
                      <strong className="text-khamco-secondary text-[0.9rem] md:text-2xl">
                        {t("Amount Remaining")}
                      </strong>
                      <span className=" p-[0.2rem] text-[0.9rem] md:text-lg">
                        {new Intl.NumberFormat("kw-KW", {
                          style: "currency",
                          currency: "KWD",
                          minimumFractionDigits: 3,
                          maximumFractionDigits: 3,
                        }).format(invoice.amount_remaining)}
                      </span>
                    </div>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } gap-[2rem] items-center my-[1rem] mx-0 py-0 px-[0.2rem] invoice-data`}
                    >
                      <strong className="text-khamco-secondary text-[0.9rem] md:text-2xl">
                        {t("Payment Method")}
                      </strong>
                      <span className=" p-[0.2rem] text-[0.9rem] md:text-lg">
                        {`${invoice.payment_type}`}
                      </span>
                    </div>
                    <div
                      className={`flex ${
                        i18n.resolvedLanguage === "ar" ||
                        i18n.resolvedLanguage === "ur"
                          ? "justify-end"
                          : "justify-start"
                      } flex justify-start flex-wrap items-center gap-[2rem] invoice-data`}
                    >
                      <QRCodeCanvas
                        value={qrCodeData} // PDF link for the QR code
                        size={128} // Size of the QR code
                        bgColor="#ffffff"
                        fgColor="#000000"
                        level="L" // Error correction level
                        includeMargin={true}
                      />
                    </div>
                    <div className="flex justify-center flex-wrap items-center gap-[2rem] invoice-data">
                      <p className="text-[#666] mx-[0.5rem]">
                        {t("Invoice-Disclaimer")} Please read{" "}
                        <a
                          href="https://www.kham.co/"
                          target="_blank"
                          rel="noreferrer"
                          className="text-[blue] no-underline hover:underline cursor-pointer"
                        >
                          Khamco Terms & Conditions
                        </a>{" "}
                        on the website for more information.
                      </p>
                    </div>
                    <div className="flex justify-center flex-wrap items-center gap-[2rem] my-[2rem] invoice-data">
                      <address className="text-[#666] mx-[0.5rem]">
                        Khamco Company, LLC - PO Box 21353, Safat 13074, Qibla,
                        Kuwait
                      </address>
                    </div>
                    <div className="flex justify-start md:justify-center flex-wrap items-center gap-[2rem] invoice-data">
                      <div className="flex items-center gap-[1rem] flex-wrap">
                        <span>Phone:</span>
                        <a
                          href="tel:22400650"
                          className="flex items-center gap-[0.5rem] text-khamco-primary no-underline hover:underline cursor-pointer"
                        >
                          22400650{" "}
                          <i className="fas fa-phone-volume text-4xl"></i>
                        </a>
                      </div>
                      <div className="flex items-center gap-[1rem] flex-wrap">
                        <span>Mobile:</span>
                        <a
                          href="tel:55627017"
                          className="flex items-center gap-[0.5rem] text-khamco-primary no-underline hover:underline cursor-pointer"
                        >
                          55627017{" "}
                          <i className="fas fa-phone-volume text-4xl"></i>
                        </a>
                      </div>
                      <div className="flex items-center gap-[1rem] flex-wrap">
                        <span>Email:</span>
                        <a
                          href="mailto:info@kham.co"
                          className="flex items-center gap-[0.5rem] text-khamco-primary no-underline hover:underline cursor-pointer"
                        >
                          info@kham.co
                          <i className="fas fa-envelope text-4xl"></i>
                        </a>
                      </div>
                      <div className="flex items-center gap-[1rem] flex-wrap">
                        <span>Website:</span>
                        <a
                          href="www.kham.co"
                          className="flex items-center gap-[0.5rem] text-khamco-primary no-underline hover:underline cursor-pointer"
                        >
                          www.kham.co <i className="fas fa-globe text-4xl"></i>
                        </a>
                      </div>
                      <div className="flex items-center gap-[1rem] flex-wrap">
                        <span>Social:</span>
                        <a
                          href="www.kham.co"
                          className="flex items-center gap-[0.5rem] text-khamco-primary no-underline hover:underline cursor-pointer"
                        >
                          @khamco.kw <i className="fas fa-message text-4xl"></i>
                        </a>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </>
          )}
        </section>
        <dialog
          className="fixed w-[90%] top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 md:w-2/4 rounded-[2rem] border-khamco-primary overflow-y-auto; overflow-x-hidden max-h-[80vh] py-[2rem] px-0 shadow-lg shadow-[#444]"
          open={dialogState}
        >
          <div className="p-[4rem] grid justify-center items-center">
            <form
              className="flex flex-col justify-center items-center w-[80vw] md:w-[90vw]"
              onSubmit={handleSubmit}
            >
              <button
                className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer absolute top-[5%] left-[5%] hover:opacity-90"
                type="button"
                onClick={() => setDialogState(false)}
              >
                <i className="fas fa-times"></i>
              </button>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <h2 className="text-center text-[2rem] font-minimoBold">
                  {t("Change Payment Details")}
                </h2>
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label
                  className="text-lg my-2 select-none self-start font-medium"
                  htmlFor="amount_paid"
                >
                  {t("Amount Paid")}
                </label>
                <input
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step="0.1"
                  placeholder={`${t("Amount Paid")}`}
                  value={amountPaid}
                  onChange={(e) => setAmountPaid(e.target.value)}
                />
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label
                  className="text-lg my-2 select-none self-start font-medium"
                  htmlFor="payment_status"
                >
                  {t("Payment Status")}
                </label>
                <select
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  value={paymentStatus}
                  onChange={(e) => setPaymentStatus(e.target.value)}
                >
                  <option value="-- Select payment status --">
                    -- Select payment status --
                  </option>
                  <option value="Unpaid">{t("Unpaid")}</option>
                  <option value="Partially Paid">{t("Partially Paid")}</option>
                  <option value="Paid">{t("Paid")}</option>
                  <option value="Other">{t("Other")}</option>
                </select>
              </div>

              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <label
                  className="text-lg my-2 select-none self-start font-medium"
                  htmlFor="payment_type"
                >
                  {t("Payment Type")}
                </label>
                <select
                  className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  value={paymentType}
                  onChange={(e) => setPaymentType(e.target.value)}
                >
                  <option value="-- Select payment type --">
                    -- Select payment type --
                  </option>
                  <option value="K-Net">K-Net</option>
                  <option value="Cash">Cash</option>
                  <option value="Other">Other</option>
                </select>
              </div>
              <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                <button
                  type="submit"
                  className="text-center bg-[blue] w-full md:w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
                >
                  Submit
                </button>
              </div>
            </form>
          </div>
        </dialog>
      </main>
      <Footer />
    </>
  );
};

export default ShowInvoice;
