import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import { Link, useLocation, useNavigate } from "react-router-dom";
import Footer from "../../components/Footer";
import axios from "axios";
import { Buffer } from "buffer";
import { toast } from "react-toastify";
import { useTranslation } from "react-i18next";
import { apiUrl, authKey } from "../../utilities";

const Products = () => {
  const [products, setProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [totalProducts, setTotalProducts] = useState(0);
  const location = useLocation();
  const navigate = useNavigate();
  const { t, i18n } = useTranslation();

  const getProducts = async (queryParams = "") => {
    setIsLoading(true);
    try {
      const response = await axios.get(`${apiUrl}/products${queryParams}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });

      setIsLoading(false);
      if (response.data.products) {
        setProducts(response.data.products);
        setTotalPages(response.data.totalPages);
      setTotalProducts(response.data.totalProducts);
      } else {
        setProducts([]);
      }
    } catch (error) {
      if (error.response) {
        if (error.response.status === 403) {
          toast.warn("You are not authorized to access that page");
          navigate("/");
        } else {
          toast.error(error.response.data?.message || error.message);
        }
      } else {
        // Handle network errors or errors without a response object
        toast.error("An error occurred: " + error.message);
      }
      setProducts([]);
      setIsLoading(false);
    }
  };

  useEffect(() => {
    document.title = "Products Khamco Shop";
    getProducts(location.search);
  }, [location.search]);

  const handleSearch = (e) => {
    e.preventDefault();
    const form = new FormData(e.target);
    const searchParams = new URLSearchParams(form).toString();
    navigate(`?${searchParams}`);
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section
          id="inventory"
          className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]"
        >
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
            <Link
              to="/products/new"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              {t("Add New Product")} <i className="fas fa-cart-plus"></i>
            </Link>
          </div>
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
            <form
              onSubmit={handleSearch}
              className="w-full md:w-2/5 flex justify-between mb-8"
            >
              <input
                type="text"
                placeholder="Search product name"
                name="name"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className={`w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] ${
                  i18n.resolvedLanguage === "ar" ||
                  i18n.resolvedLanguage === "ur"
                    ? "rounded-l-lg"
                    : "rounded-r-lg"
                } cursor-pointer hover:opacity-90`}
              >
                <i className="fas fa-search"></i>
              </button>
            </form>
            <form
              onSubmit={handleSearch}
              className="w-full md:w-2/5 flex justify-between mb-8"
            >
              <input
                type="date"
                name="date"
                id="date-search"
                className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              />
              <button
                type="submit"
                className={`w-1/5 border-none outline-none bg-khamco-primary text-white text-[1.5rem] ${
                  i18n.resolvedLanguage === "ar" ||
                  i18n.resolvedLanguage === "ur"
                    ? "rounded-l-lg"
                    : "rounded-r-lg"
                } cursor-pointer hover:opacity-90`}
              >
                <i className="fas fa-check"></i>
              </button>
            </form>
          </div>
          <h1 className="text-center text-[2rem] font-minimoBold">
            {t("All Products")} ({products.length || 0})
          </h1>
          {isLoading &&
            new Array(3)
              .fill(null)
              .map((_, index) => (
                <div
                  key={`placeholder-${index}`}
                  className="w-full bg-[#ccc] animate-pulse py-[6rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-evenly items-center gap-[2rem]"
                ></div>
              ))}
          {products.length > 0 &&
            products.map((product, index) => (
              <div
                key={index}
                className="flex items-center w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] justify-evenly flex-wrap gap-[2rem] my-[2rem]"
                dir="auto"
              >
                <img
                  src={`data:${product.photoType};base64, ${Buffer.from(
                    product.picture.data
                  ).toString("base64")}`}
                  alt={product.name}
                  loading="lazy"
                  className="w-full md:w-[10vw] h-[20vh]"
                />
                <p className="capitalize">
                  <strong>{t("Name")}</strong>: {product.name}
                </p>
                <p className="capitalize">
                  <strong>{t("Available")}</strong>: {product.quantity.toFixed(2)}
                </p>
                <Link
                  to={`/products/${product._id}`}
                  className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90 transform-none w-full md:w-[5rem] text-center"
                >
                  {t("View")}
                </Link>
              </div>
            ))}

          {products.length <= 0 && !isLoading && (
            <p className="text-center">No product data.</p>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Products;
