import { useEffect, useState } from "react";
import Clothing from "../../../components/Clothing";
import {
  clothingList,
  dishdashaLength,
  dressLength,
  garmentLength,
  hasDressSleeve,
  hasOther,
  hasShirt,
  hasShirtSleeve,
  hasSleepwearSleeve,
  hasSpecialOrder,
  upperMeasurements,
  needsSleeve,
  shortsLength,
  lowerMeasurements,
  isBottom,
  hasBackWidth,
  hasAlterations,
  isCoverall,
} from "../../../utilities";
import { useTranslation } from "react-i18next";
import UnitInput from "../../../components/UnitInput";

const MeasurementsForm = ({
  authorizedUser,
  clothingOptions,
  setClothingOptions,
  setDishdashaLength,
  setDishdashaFrontHeight,
  setDishdashaBackHeight,
  shirtType,
  setShirtType,
  setGarmentLength,
  // setCoverallLength,
  setPantsLength,
  setShortsLength,
  setDressLength,
  setCollar,
  setShoulders,
  setSleeve,
  setShirtSleeve,
  setDressSleeve,
  setDescription,
  setSleepwearSleeve,
  setChest,
  setBelly,
  setWaist,
  setHips,
  setArmHole,
  setBicepTricep,
  loading,
  setElbow,
  setWrist,
  setInseam,
  setThigh,
  setCalf,
  setLegFoot,
  setBackWidth,
  setBackHeight,
  setFrontChestWidth,
  setSpecialOrder,
  setAlterations,
  setNotes,
}) => {
  const { t } = useTranslation();
  const [unitType, setUnitType] = useState(false);
  const [values, setValues] = useState({});

  const handleInputChange = (name, value) => {
    setValues((prevValues) => ({
      ...prevValues,
      [name]: value,
    }));
    console.log(values);
  };

  useEffect(() => {
    const numberInputs = document.querySelectorAll("input [type='number']");

    numberInputs.forEach((element) => {
      if (unitType === "Inches") {
        element.value *= 2.54;
      } else {
        element.value /= 2.54;
      }
    });
  }, [unitType]);

  return (
    <>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <h1 className="text-center text-[2rem] font-minimoBold mb-8">
          Click to select/deselect an item
        </h1>
        <div className="relative flex justify-center md:justify-start gap-[1rem] md:gap-[0.5rem] flex-wrap h-[70vh] overflow-y-scroll">
          {clothingList &&
            clothingList.map((item, index) => (
              <Clothing
                key={index}
                label={item.label}
                imageUrl={item.imageUrl}
                setOptions={setClothingOptions}
              />
            ))}
        </div>
      </div>
      {clothingOptions.length > 0 && (
        <>
          <p className="bg-khamco-primary text-white text-[1.2rem] rounded-full py-1 px-2 w-10 text-center">
            {clothingOptions.length}
          </p>
          <p className="my-4 text-[#444] text-lg">Enter measurements below</p>
          <button
            type="button"
            className="p-2 my-4 bg-khamco-secondary text-white rounded-md fixed z-50 top-0 md:top-2/4 md:right-[8rem] md:-translate-y-2/4"
            onClick={() =>
              setUnitType(unitType === "Inches" ? "Centimeters" : "Inches")
            }
          >
            Change to {unitType === "Inches" ? "Centimeters" : "Inches"}
          </button>
        </>
      )}
      {clothingOptions.length > 0 && hasShirt(clothingOptions) && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <h1 className="text-center text-[1.5rem] font-minimoBold mb-8">
            Select Shirt Type
          </h1>
          <div className="flex gap-4 w-full justify-center flex-wrap">
            <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
              <label
                className="text-xl my-2 select-none self-start font-medium cursor-pointer"
                htmlFor="long-sleeve"
              >
                Long Sleeve{" "}
              </label>
              <input
                className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
                type="radio"
                id="long-sleeve"
                name="shirt-type"
                value="Long Sleeve"
                checked={shirtType === "Long Sleeve"}
                onChange={() => {
                  setShirtType(() => "Long Sleeve");
                }}
              />
            </span>
            <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
              <label
                className="text-xl my-2 select-none self-start font-medium cursor-pointer"
                htmlFor="short-sleeve"
              >
                Short Sleeve{" "}
              </label>
              <input
                className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
                type="radio"
                id="short-sleeve"
                name="shirt-type"
                checked={shirtType === "Short Sleeve"}
                onChange={() => {
                  setShirtType(() => "Short Sleeve");
                }}
              />
            </span>
            <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
              <label
                className="text-xl my-2 select-none self-start font-medium cursor-pointer"
                htmlFor="polo-shirt"
              >
                Polo{" "}
              </label>
              <input
                className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
                id="polo-shirt"
                name="shirt-type"
                type="radio"
                checked={shirtType === "Polo"}
                onChange={() => {
                  setShirtType(() => "Polo");
                }}
              />
            </span>
            <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
              <label
                className="text-xl my-2 select-none self-start font-medium cursor-pointer"
                htmlFor="t-shirt"
              >
                T-Shirt{" "}
              </label>
              <input
                className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
                type="radio"
                value="T-Shirt"
                id="t-shirt"
                name="shirt-type"
                checked={shirtType === "T-Shirt"}
                onChange={() => {
                  setShirtType(() => "T-Shirt");
                }}
              />
            </span>
          </div>
        </div>
      )}
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <div className="flex w-full justify-between flex-wrap">
          {dishdashaLength(clothingOptions) && (
            <>
              <UnitInput
                label="Dishdasha Length"
                name="dishdasha_garment_length"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <UnitInput
                label="Dishdasha Front Height"
                name="dishdasha_front_height"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <UnitInput
                label="Dishdasha Back Height"
                name="dishdasha_back_height"
                unitType={unitType}
                onChange={handleInputChange}
              />
            </>
          )}
          {garmentLength(clothingOptions) && (
            <UnitInput
              label="Garment Length"
              name="garment_length"
              unitType={unitType}
              onChange={handleInputChange}
            />
          )}
          {isCoverall(clothingOptions) && (
            <UnitInput
              label="Coverall length"
              name="coverall_length"
              unitType={unitType}
              onChange={handleInputChange}
            />
          )}
          {isBottom(clothingOptions) && (
            <>
              <UnitInput
                label="Pants length"
                name="pants_garment_length"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <UnitInput
                label="Inseam"
                name="inseam"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <UnitInput
                label="Thigh"
                name="thigh"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <UnitInput
                label="Calf"
                name="calf"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <UnitInput
                label="Leg-Foot"
                name="leg_foot"
                unitType={unitType}
                onChange={handleInputChange}
              />
            </>
          )}
          {shortsLength(clothingOptions) && (
            <UnitInput
              label="Shorts length"
              name="shorts_garment_length"
              unitType={unitType}
              onChange={handleInputChange}
            />
          )}
          {dressLength(clothingOptions) && (
            <UnitInput
              label="Dress Length"
              name="dress_garment_length"
              unitType={unitType}
              onChange={handleInputChange}
            />
          )}
          {hasDressSleeve(clothingOptions) && (
            <UnitInput
              label="Dress sleeve"
              name="dress_sleeve"
              unitType={unitType}
              onChange={handleInputChange}
            />
          )}
          {needsSleeve(clothingOptions) && (
            <UnitInput
              label="Sleeve"
              name="sleeve"
              unitType={unitType}
              onChange={handleInputChange}
            />
          )}
          {hasShirtSleeve(clothingOptions) && (
            <UnitInput
              label="Shirt sleeve"
              name="shirt_sleeve"
              unitType={unitType}
              onChange={handleInputChange}
            />
          )}
          {hasSleepwearSleeve(clothingOptions) && (
            <UnitInput
              label="Sleepwear sleeve"
              name="sleepwear_sleeve"
              unitType={unitType}
              onChange={handleInputChange}
            />
          )}
          {upperMeasurements(clothingOptions) && (
            <>
              <UnitInput
                label="Collar"
                name="collar"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <UnitInput
                label="Shoulders"
                name="shoulders"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <UnitInput
                label="Chest"
                name="chest"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <UnitInput
                label="Belly"
                name="belly"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <UnitInput
                label="Arm hole"
                name="arm_hole"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <UnitInput
                label="Bicep/Tricep"
                name="bicep_tricep"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <UnitInput
                label="Elbow"
                name="elbow"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <UnitInput
                label="Wrist"
                name="wrist"
                unitType={unitType}
                onChange={handleInputChange}
              />
            </>
          )}
          {lowerMeasurements(clothingOptions) && (
            <>
              <UnitInput
                label="Waist"
                name="waist"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <UnitInput
                label="Hips"
                name="hips"
                unitType={unitType}
                onChange={handleInputChange}
              />
            </>
          )}
          {hasBackWidth(clothingOptions) && (
            <>
              <UnitInput
                label="Back width"
                name="back_width"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <UnitInput
                label="Back height"
                name="back_width"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <div className="flex flex-col">
                <label className="text-center"></label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Back height"
                  onChange={(e) => setBackHeight(e.target.value)}
                />
              </div>
              <div className="flex flex-col">
                <label className="text-center">Front-Chest-Width</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Front-Chest-Width"
                  onChange={(e) => setFrontChestWidth(e.target.value)}
                />
              </div>
            </>
          )}
        </div>
      </div>
      {hasSpecialOrder(clothingOptions) && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label>Special order details</label>
          <textarea
            className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
            placeholder="Special order details"
            onChange={(e) => setSpecialOrder(e.target.value)}
          ></textarea>
        </div>
      )}
      {hasAlterations(clothingOptions) && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label>Alterations</label>
          <textarea
            className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
            placeholder="Alterations"
            onChange={(e) => setAlterations(e.target.value)}
          ></textarea>
        </div>
      )}
      {hasOther(clothingOptions) && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label>Other</label>
          <textarea
            className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
            placeholder="Enter a description"
            onChange={(e) => setDescription(e.target.value)}
          ></textarea>
        </div>
      )}
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label>Extra notes</label>
        <textarea
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          placeholder="Extra notes"
          onChange={(e) =>
            setNotes({
              user: authorizedUser,
              text: e.target.value,
              timestamp: new Date(),
            })
          }
        ></textarea>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <button
          type="submit"
          className="text-center bg-[blue] w-full md:w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
          disabled={loading}
        >
          {loading ? (
            <i className="fas fa-spinner animate-spin"></i>
          ) : (
            `${t("Submit")}`
          )}
        </button>
      </div>
    </>
  );
};

export default MeasurementsForm;
