import React from "react";

const ServiceForm = ({ setService, setPrice, setDate, loading }) => {
  return (
    <>
      <div className="form-row">
        <label htmlFor="service">Service</label>
        <input type="text" onChange={(e) => setService(e.target.value)} />
      </div>
      <div className="form-row">
        <label htmlFor="price">Price</label>
        <input
          type="number"
          step="0.01"
          onChange={(e) => setPrice(e.target.value)}
        />
      </div>
      <div className="form-row">
        <label htmlFor="date">Date</label>
        <input
          type="date"
          onChange={(e) =>
            setDate(new Date(e.target.value).toISOString().split("T")[0])
          }
        />
      </div>
      <div className="form-row">
        <button type="submit">{loading ? <i className="fas fa-rotate anim"></i>: "Submit"}</button>
      </div>
    </>
  );
};

export default ServiceForm;
