import { useEffect, useRef, useState } from "react";
import Footer from "../../components/Footer";
import Navbar from "../../components/Navbar";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import { useTranslation } from "react-i18next";

const CustomerDetails = () => {
  const { id } = useParams();
  const [customer, setCustomer] = useState(null);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/customers/${id}`
    : `https://khamco-shop-backend.onrender.com/customers/${id}`;
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const { t } = useTranslation();
  // const detailsRef = useRef(null);
  const detailsRef = useRef<HTMLDivElement>(null);
  const reactToPrintFn = useReactToPrint({ deta });

  useEffect(() => {
    let isMounted = true;

    document.title = `${t("Customer Details")} - ${
      customer ? `${customer._id} -` : ""
    } Khamco Shop`;

    const fetchCustomerData = async () => {
      try {
        const response = await axios.get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        });
        if (isMounted) {
          setLoading(false);
          setCustomer(response.data.customer);
        }
      } catch (error) {
        if (isMounted) {
          setLoading(false);
          if (error.response) {
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response.data?.message || error.message);
            }
          } else {
            // Handle network errors or errors without a response object
            toast.error("An error occurred: " + error.message);
          }
        }
      }
    };

    fetchCustomerData();

    return () => {
      isMounted = false;
    };
  }, [apiUrl, authKey, customer, navigate, t]);

  const updateMembership = async () => {
    try {
      const response = await axios.put(
        `${apiUrl}/membership`,
        {},
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      );
      toast.success(response.data.message);
      setCustomer(response.data.customer);
    } catch (error) {
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const handlePrint = () => {
    const content = detailsRef.current.innerHTML;
    const printWindow = window.open("", "", "width=800,height=600");

    printWindow.document.write(`
      <html>
        <head>
          <title>Customer Details</title>
          <style>
            /* Add necessary styles for printing */
            body {
              font-family: Arial, sans-serif;
              margin: 20px;
            }
            div{
              display: flex;
              flex-direction: column;
              gap: [1rem];
            }
            
            span {
              margin: 1rem 0;
              display: inline-flex;
              align-items: center;
              gap: 2rem; 
              text-wrap: balance; 
              flex-wrap: wrap;
            }
          </style>
        </head>
        <body>
          ${content}
        </body>
      </html>
    `);

    printWindow.document.write();

    // Close the document and trigger the print
    printWindow.document.close();
    printWindow.focus();
    printWindow.print();
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section>
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] mx-auto w-full flex-wrap">
            <Link
              to="/customers"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              <i className="fas fa-arrow-left"></i> {t("Customers")}
            </Link>
          </div>
          {loading && (
            <>
              <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
                <div className="flex justify-between gap-[2rem] items-center my-[2rem] mx-auto w-full flex-wrap">
                  {new Array(6).fill(null).map((_, index) => (
                    <Link
                      key={`placeholder-${index}`}
                      to={``}
                      className="py-[2rem] px-[4rem] outline-none border-none bg-[#666] rounded-[0.5rem] animate-pulse"
                    ></Link>
                  ))}
                  <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem] w-2/4 bg-[#666] animate-pulse m-auto h-[100vh] my"></div>
                </div>
              </div>
            </>
          )}
          {customer && (
            <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
              <div className="flex flex-col md:flex-row justify-center gap-[2rem] items-center mx-auto w-full flex-wrap">
                <Link
                  to={`/customers/edit/${customer._id}`}
                  className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90 w-full md:w-1/4 text-center"
                >
                  {t("EditButton")}
                </Link>
                <Link
                  to={`/customers/${customer._id}/invoices`}
                  className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90 w-full md:w-1/4 text-center"
                  target="_blank"
                >
                  {t("All Invoices")}
                </Link>
                <Link
                  to={`/invoices/new/${customer._id}`}
                  className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[green] hover:opacity-90 w-full md:w-1/4 text-center"
                >
                  {t("New Invoice")}
                </Link>
                <Link
                  to={`/customers/create-dependent/${customer._id}`}
                  className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90 w-full md:w-1/4 text-center"
                >
                  {t("Add Dependent")}
                </Link>
                <button
                  className="bg-[#666] py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer hover:opacity-90 w-full md:w-1/4 text-center"
                  title="Print this document"
                  onClick={handlePrint}
                >
                  {t("Print")} <i className="fas fa-print"></i>
                </button>
                <button
                  className="bg-[#666] py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer hover:opacity-90 w-full md:w-1/4 text-center"
                  title="Click to change customer membership"
                  onClick={updateMembership}
                >
                  {t("Membership")}{" "}
                  {customer.membership_status === "VIP" && (
                    <i className="fas fa-star text-[gold] animate-pulse"></i>
                  )}
                </button>
              </div>
              <div ref={detailsRef}>
                <h1 className="text-center text-[2rem] font-minimoBold my-[2rem]">
                  {t("Customer Details")}
                </h1>
                <div className="flex flex-col gap-[1rem]">
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong className="text-khamco-secondary">
                      {t("ID Number")}
                    </strong>
                    <p>{customer.uniqueId}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong className="text-khamco-secondary">
                      {t("Name")}
                    </strong>
                    <p>{`${customer.name} ${customer.surname}`}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong className="text-khamco-secondary">
                      {t("Date Created")}
                    </strong>
                    <p>
                      {new Date(customer.date_created).toLocaleDateString(
                        "en-us",
                        {
                          month: "long",
                          year: "numeric",
                          day: "numeric",
                        }
                      )}
                    </p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong className="text-khamco-secondary">
                      {t("Phone Number")}
                    </strong>
                    <a
                      className="text-[blue] no-underline hover:underline cursor-pointer"
                      href={`tel:${customer.phone}`}
                    >
                      {customer.phone}
                    </a>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong className="text-khamco-secondary">
                      {t("Last Measurement")}
                    </strong>
                    <p>
                      {new Date().toLocaleDateString("en-us", {
                        month: "long",
                        year: "numeric",
                        day: "numeric",
                      })}
                    </p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong className="text-khamco-secondary">
                      {t("Address")}
                    </strong>
                    <p>{customer.address}</p>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong className="text-khamco-secondary">
                      {t("Email")}
                    </strong>{" "}
                    <a
                      className="text-[blue] no-underline hover:underline cursor-pointer"
                      href={`mailto:${customer.email}`}
                    >
                      {customer.email}
                    </a>
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong className="text-khamco-secondary">
                      {t("Military Status")}
                    </strong>
                    {customer.military_status}
                  </span>
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <strong className="text-khamco-secondary">
                      {t("Membership Status")}
                    </strong>
                    {customer.membership_status === "VIP" ? (
                      <p>
                        {customer.membership_status}{" "}
                        <i className="fas fa-star text-[gold] animate-pulse"></i>
                      </p>
                    ) : (
                      <p>{customer.membership_status}</p>
                    )}
                  </span>
                </div>
                {customer.military_status === "Military" && (
                  <>
                    <div className="flex flex-col gap-[1rem]">
                      <h2 className="text-center text-[2rem] font-minimoBold">
                        {t("Military Details")}
                      </h2>
                    </div>
                    <div className="flex flex-col gap-[1rem] ">
                      <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                        <strong className="text-khamco-secondary">
                          {t("ID Number")}
                        </strong>
                        <p>
                          {customer.military_idnumber
                            ? customer.military_idnumber
                            : "--"}
                        </p>
                      </span>
                      <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                        <strong className="text-khamco-secondary">
                          {t("Rank")}
                        </strong>
                        <p>
                          {customer.military_rank
                            ? customer.military_rank
                            : "--"}
                        </p>
                      </span>
                      <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                        <strong className="text-khamco-secondary">
                          {t("Department")}
                        </strong>
                        <p>
                          {customer.military_department
                            ? customer.military_department
                            : "--"}
                        </p>
                      </span>
                    </div>
                  </>
                )}
                <div className="flex flex-col gap-[1rem] my-[2rem]">
                  <h2 className="text-center text-[2rem] font-minimoBold">
                    {t("Measurements")}
                  </h2>
                </div>
                <div className="flex justify-center gap-[2rem] items-center my-[2rem] mx-auto w-full flex-wrap ">
                  <Link
                    to={`/customers/${customer._id}/measurements`}
                    className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90"
                    target="_blank"
                  >
                    {t("Previous Measurements")} ({customer.measurements.length}
                    ) <i className="fas fa-external-link"></i>
                  </Link>
                </div>
                <div className="flex flex-col gap-[1rem] my-[2rem]">
                  <h2 className="text-center text-[2rem] font-minimoBold">
                    {t("Dependents Details")}
                  </h2>
                </div>
                <div className="flex flex-col gap-[1rem] ">
                  {customer.dependents && customer.dependents.length > 0 ? (
                    customer.dependents.map((dependent, index) => (
                      <span
                        key={index}
                        className="inline-flex justify-start items-center gap-[2rem] text-balance flex-wrap border-b-[gray] border-b-[0.2rem] pb-[1rem]"
                      >
                        <strong className="text-khamco-secondary">Name</strong>
                        <p>{`${dependent.name} ${dependent.surname}`}</p>
                        <Link
                          to={`/customers/${customer._id}/dependents/${dependent._id}`}
                          className="py-[0.5rem] px-[1rem] text-center outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90"
                        >
                          View
                        </Link>
                      </span>
                    ))
                  ) : (
                    <p className="empty">
                      {t("This customer has no dependents.")}
                    </p>
                  )}
                </div>
              </div>
            </div>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default CustomerDetails;
