import React, { useEffect, useState } from 'react'
import Navbar from '../../components/Navbar'
import AdminPanel from '../../components/AdminPanel'
import Footer from '../../components/Footer';
import axios from "axios";

const AdminInvoiceData = () => {
    const [message, setMessage] = useState("");
    const [invoiceData, setInvoiceData] = useState([]);
    const apiUrl = "http://localhost:5000/admin/invoice_data";
    const [isLoading, setIsLoading] = useState(true);

    useEffect(()=> {
        const fetchInvoiceData = async()=> {
            await axios.get(apiUrl).then(response => {
                console.log(response.data);
                setIsLoading(false);
            }).catch(error => {
                setIsLoading(true);
                
            });
        }

        return () => {
            fetchInvoiceData();
        }
    }, []);

  return (
    <><Navbar /><main>
          <AdminPanel />
          <section class="admin-dashboard">
              <p class="message">
                  {message}
              </p>
              <table>
                  <caption>Recent Invoice Data</caption>
                  <thead>
                      <tr>
                          <td>Customer</td>
                          <td>Date</td>
                          <td>Invoices Created</td>
                          <td>Invoices Deleted</td>
                      </tr>
                  </thead>
                  <tbody>
                      {invoiceData && invoiceData.map(entry => (
                          <tr>
                              <td>
                                  {entry.customer_name} {entry.customer_surname}
                              </td>
                              <td>
                                  {new Date(entry.date).toLocaleDateString("en-US", { month: "long", day: "numeric", year: "numeric" })}
                              </td>
                              <td>
                                  {entry.total_invoices_created}
                              </td>
                              <td>{entry.total_invoices_deleted}</td>
                          </tr>
                      ))}
                  </tbody>
              </table>
          </section>
      </main><Footer /></>
  )
}

export default AdminInvoiceData
