import { Link, useNavigate, useParams } from "react-router-dom";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { useEffect, useState } from "react";
import { toast } from "react-toastify";
import axios from "axios";

const UserProfile = () => {
  const { id } = useParams();
  const [isLoading, setIsLoading] = useState(true);
  const [user, setUser] = useState({});
  const serverUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/admin"
    : "https://khamco-shop-backend.onrender.com/admin";
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();

  useEffect(() => {
    let isMounted = true;

    document.title = "Profile Management";

    const fetchData = async () => {
      await axios
        .get(`${serverUrl}/users/profile/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          if (isMounted) {
            setIsLoading(false);
            setUser(response.data.user);
            document.title = `Profile Management - ${response.data.user.name}`;
          }
        })
        .catch((error) => {
          if (isMounted) {
            setIsLoading(false);
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response?.data?.message || error.message);
            }
            return;
          }
        });
    };

    fetchData();

    return () => {
      isMounted = false;
    };
  }, [authKey, id, navigate, serverUrl]);

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section className="admin-dashboard font-arial">
          <h1 className="text-center text-[2rem] font-minimoBold">Dashboard</h1>
          {isLoading && (
            <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
              <div className="text-center p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="text-center p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="text-center p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="text-center p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
            </div>
          )}
          {user && (
            <>
              <section className="flex justify-evenly my-[2rem]">
                <div>
                  <span className="flex">
                    <strong>Name: </strong>
                    {`${user.name} ${user.surname}`}
                  </span>
                  <span className="flex">
                    <strong>Email: </strong>
                    {user.email}
                  </span>
                  <span className="flex">
                    <strong>Position: </strong>
                    {user.role}
                  </span>
                  <span className="flex">
                    <strong>ID No: </strong>
                    {user._id}
                  </span>
                  <span>
                    <strong>Date Joined: </strong>
                    {user.date}
                  </span>
                  <span className="flex">
                    <strong>Preferred Language: </strong>
                    {user.language === "en" && "English"}
                    {user.language === "ur" && "Urdu"}
                    {user.language === "ar" && "Arabic"}
                    {user.language === "hi" && "Hindi"}
                  </span>
                </div>
                <img src="https://image.png" alt="User"/>
              </section>
              <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] my-[6rem] m-auto">
                <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    User Details <i className="fas fa-user"></i>
                  </h2>
                  <Link
                    to={`/admin/users/${id}`}
                    className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90"
                  >
                    Details
                  </Link>
                </div>
                <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    My Orders <i className="fas fa-bookmark"></i>
                  </h2>
                  <Link
                    to={`/my-orders/${id}`}
                    className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90"
                  >
                    Details
                  </Link>
                </div>
              </div>
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default UserProfile;
