import React, { useState } from 'react'
import Footer from '../../components/Footer';
import Navbar from '../../components/Navbar';
import OrderForm from './OrderForm';

const NewOrder = () => {
    const [message, setMessage] = useState("Hello World");
    const [writingDirection, setWritingDirection] = useState("ltr");
    const order = {
        name: {
                choice: "Ham",
                choice: "Ham",
                choice: "Ham"
        }
    }
    const [products, setProducts] = useState([
        {
            name: "Nike"
        },
        {
            name: "Nike"
        },
        {
            name: "Nike"
        },
    ]);
    const [services, setServices] = useState([
        {
            choice: "Tailoring"
        },
        {
            choice: "Tailoring"
        },
        {
            choice: "Tailoring"
        },
    ]);
    const [customers, setCustomers] = useState([
        {
            name: "John"
        },
        {
            name: "John"
        },
        {
            name: "John"
        },
    ]);
  return (
    <>
    <Navbar />
    <main className='p-[4rem]'>
          <section className="p-[4rem] grid justify-center items-center" dir={writingDirection}>
              <div className="row">
                  <a href="/orders"><i className="fas fa-arrow-left"></i> Orders</a>
              </div>
              <h1 className="title">New Order</h1>
              <p className="message">
                  {message}
              </p>
              {/* {order.name && */}
                  <div className="row">
                      <h1 className="text-center">Order Details</h1>
                      </div>
                          {order.name.map(order => (
                              <form action="/orders/<%= order.id %>/names/<%= name.id %>?_method=DELETE" method="POST" className='flex flex-col justify-center items-center w-[90vw]'>
                                  <div className="row">
                                      <div className="inline">
                                          <label className="border-b-[0.1rem] border-[gray] p-b-[0.5rem] w-[85%]">{order.choice}</label>
                                          <button type="submit" className="delete-btn width-[10%]" title="Remove this item"><i className="fas fa-minus"></i></button>
                                      </div>
                                  </div>
                              </form>
                          ))}
                          }
                          <form action="/orders/<%= order.id %>/name?_method=PUT" method="POST" className='flex flex-col justify-center items-center w-[90vw]'>
                              <div className="row">
                                  <div className="inline">
                                      <select name="order_name" id="order_name" style="width: 85%;">
                                          <option label="-- Products --" selected disabled></option>
                                          {products.map(product => (
                                              <option value={product.name} label={product.name}></option>
                                          ))}
                                          <option label="-- Services --" disabled></option>
                                          {services.map(service => (
                                              <option value={service.service} label={service.service}></option>
                                          ))})
                                      </select>
                                      <button type="submit" className="edit-btn" title="Add another item" style="width: 10%;"><i className="fas fa-plus"></i></button>
                                  </div>
                              </div>
                          </form>
                          {order.customers &&
                              <div className="row">
                                  <h1 style="text-align: center;">Customer Details</h1>
                              </div>}
                          {order.customers.map(customer => (
                              <form action="/orders/<%= order.id %>/customers/<%= customer.id %>?_method=DELETE" method="POST" className='flex flex-col justify-center items-center w-[90vw]'>
                                  <div className="row">
                                      <div className="inline">
                                          <label className="border-b-[0.1rem] border-b-gray pb-[0.5rem] w-[85%]">{customer.customer_name}</label>
                                          <button type="submit" className="delete-btn w-[10%]" title="Remove this item"><i className="fas fa-minus"></i></button>
                                      </div>
                                  </div>
                              </form>
                          ))}
                          <form action="/orders/<%= order.id %>/customers?_method=PUT" method="POST" className='flex flex-col justify-center items-center w-[90vw]'>
                              <div className="row">
                                  <select name="customer_name" id="customer_name">
                                      <option label="-- Select customer --" selected disabled></option>
                                      {customers.map(customer => (
                                          <option value={customer.name} label={customer.name}>
                                          </option>
                                      ))}
                                  </select>
                              </div>
                              <div className="row">
                                  <label for="dependents">Dependent Name</label>
                                  <div className="inline">
                                      <select name="dependent_name" id="dependents" className="w-[85%]">
                                      </select>
                                      <button type="submit" className="edit-btn" title="Add another customer" style="width: 10%;"><i className="fas fa-plus"></i></button>
                                  </div>
                              </div>
                          </form>
                          <form action="/orders/<%= order.id%>?_method=PUT" method="POST" className='flex flex-col justify-center items-center w-[90vw]'>
                              <OrderForm/>
                          </form>
                          <form action="/orders/<%= order.id%>?_method=DELETE" method="POST" className='flex flex-col justify-center items-center w-[90vw]'>
                              <button type="submit" className="delete-btn">Delete <i className="fas fa-times"></i></button>
                          </form>
                      </section>
                  </main>
                  <Footer />
                </>
              )
              }

              export default NewOrder
