import React, { useEffect, useRef, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import AdminPanel from "../../components/AdminPanel";
import axios from "axios";
import { toast } from "react-toastify";
import { Link, useNavigate } from "react-router-dom";
import { Chart, registerables } from "chart.js";
Chart.register(...registerables);

const AdminRevenue = () => {
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000"
    : "https://khamco-shop-backend.onrender.com";
  const authKey = localStorage.getItem("jwt");
  const [financeData, setFinanceData] = useState([]);
  const [dailyRevenue, setDailyRevenue] = useState([]);
  const [weeklyRevenue, setWeeklyRevenue] = useState([]);
  const [monthlyRevenue, setMonthlyRevenue] = useState([]);
  const [yearlyRevenue, setYearlyRevenue] = useState([]);
  const [products, setProducts] = useState([]);
  const [services, setServices] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState("Recent Activity");
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);
  const chartRef = useRef(null);

  useEffect(() => {
    let isMounted = true;

    document.title = "Admin Revenue Data - Khamco Shop";

    const fetchData = async () => {
      await axios
        .get(`${apiUrl}/admin/revenue`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setFinanceData(response.data.finances);
          setDailyRevenue(response.data.daily_revenue);
          setWeeklyRevenue(response.data.weekly_revenue);
          setMonthlyRevenue(response.data.monthly_revenue);
          setYearlyRevenue(response.data.yearly_revenue);
          setProducts(response.data.products);
          setServices(response.data.services);
          setIsLoading(false);
        })
        .catch((error) => {
          if (isMounted) {
            setIsLoading(false);
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response?.data?.message || error.message);
            }
            return;
          }
        });
    };

    fetchData();

    return () => {
      isMounted = false;
    };
  }, [navigate]);

  useEffect(() => {
    const ctx = chartRef.current.getContext("2d");

    const chartData = {
      labels: ["Daily", "Weekly", "Monthly", "Yearly"],
      datasets: [
        {
          label: "Revenue in KWD",
          data: [
            dailyRevenue[0]?.totalAmount || 0,
            weeklyRevenue[0]?.totalAmount || 0,
            monthlyRevenue[0]?.totalAmount || 0,
            yearlyRevenue[0]?.totalAmount || 0,
          ],
          backgroundColor: [
            "rgba(75, 192, 192, 0.6)",
            "rgba(54, 162, 235, 0.6)",
            "rgba(255, 206, 86, 0.6)",
            "rgba(153, 102, 255, 0.6)",
          ],
          borderColor: [
            "rgba(75, 192, 192, 1)",
            "rgba(54, 162, 235, 1)",
            "rgba(255, 206, 86, 1)",
            "rgba(153, 102, 255, 1)",
          ],
          borderWidth: 1,
        },
      ],
    };

    const options = {
      scales: {
        y: {
          beginAtZero: true,
          ticks: {
            callback: function (value) {
              return new Intl.NumberFormat("kw-KW", {
                style: "currency",
                currency: "KWD",
                minimumFractionDigits: 3,
                maximumFractionDigits: 3,
              }).format(value);
            },
          },
        },
      },
    };

    const chart = new Chart(ctx, {
      type: "bar",
      data: chartData,
      options: options,
    });

    return () => {
      chart.destroy();
    };
  }, [dailyRevenue, monthlyRevenue, weeklyRevenue, yearlyRevenue]);

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <AdminPanel />
        <section className="font-arial">
          {isLoading && (
            <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
              <div className="text-center p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="text-center p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="text-center p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="text-center p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
            </div>
          )}

          <div className="w-full md:w-2/3 mx-auto my-8">
            <h2 className="text-3xl font-bold text-center mb-6">
              Revenue Overview
            </h2>
            <canvas
              id="revenueChart"
              width="400"
              height="150"
              ref={chartRef}
            ></canvas>
          </div>

          <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
            <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
              <h2 className="text-khamco-secondary text-2xl font-bold">
                Annual Revenue
              </h2>
              <p>
                from{" "}
                {new Date(
                  `01/01/${new Date().getFullYear()}`
                ).toLocaleDateString("en-US", {
                  month: "long",
                  day: "numeric",
                  year: "numeric",
                })}
              </p>
              <h2 className="text-xl font-semibold text-green-500">
                {new Intl.NumberFormat("kw-KW", {
                  style: "currency",
                  currency: "KWD",
                  minimumFractionDigits: 3,
                  maximumFractionDigits: 3,
                }).format(yearlyRevenue[0]?.totalAmount) || 0}
              </h2>
            </div>
            <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
              <h2 className="text-khamco-secondary text-2xl font-bold">
                Monthly Revenue
              </h2>
              <p>
                from{" "}
                {new Date(
                  `${new Date().getMonth() + 1}/01/${new Date().getFullYear()}`
                ).toLocaleDateString("en-US", {
                  month: "long",
                  day: "numeric",
                  year: "numeric",
                })}
              </p>
              <h2 className="text-xl font-semibold text-green-500">
                {new Intl.NumberFormat("kw-KW", {
                  style: "currency",
                  currency: "KWD",
                  minimumFractionDigits: 3,
                  maximumFractionDigits: 3,
                }).format(monthlyRevenue[0]?.totalAmount) || 0}
              </h2>
            </div>
            <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
              <h2 className="text-khamco-secondary text-2xl font-bold">
                Weekly Revenue
              </h2>
              <p>
                from{" "}
                {new Date(
                  new Date() - 7 * 24 * 60 * 60 * 1000
                ).toLocaleDateString("en-US", {
                  month: "long",
                  day: "numeric",
                  year: "numeric",
                })}
              </p>
              <h2 className="text-xl font-semibold text-green-500">
                {new Intl.NumberFormat("kw-KW", {
                  style: "currency",
                  currency: "KWD",
                  minimumFractionDigits: 3,
                  maximumFractionDigits: 3,
                }).format(weeklyRevenue[0]?.totalAmount) || 0}
              </h2>
            </div>
            <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
              <h2 className="text-khamco-secondary text-2xl font-bold">
                Daily Revenue
              </h2>
              <p>
                from{" "}
                {`${new Date(
                  `${
                    new Date().getMonth() + 1
                  }/${new Date().getDate()}/${new Date().getFullYear()}`
                ).toLocaleDateString("en-US", {
                  month: "long",
                  day: "numeric",
                  year: "numeric",
                })} at 12:00 AM`}
              </p>
              <h2 className="text-xl font-semibold text-green-500">
                KWD{" "}
                {new Intl.NumberFormat("kw-KW", {
                  style: "currency",
                  currency: "KWD",
                  minimumFractionDigits: 3,
                  maximumFractionDigits: 3,
                }).format(dailyRevenue[0]?.totalAmount) || 0}
              </h2>
            </div>
          </div>
          <div className="w-full md:w-2/5 flex justify-between mb-8 mt-[4rem]">
            <select
              className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              onChange={(e) => setSelectedIndex(e.target.value)}
            >
              <option>-- Select Report --</option>
              <option>Recent Activity</option>
              <option>Profit Margins</option>
            </select>
          </div>
          {selectedIndex === "Recent Activity" && (
            <div className="overflow-auto">
            <table className="w-full my-8 border-collapse text-[1.2rem]">
              <caption className="text-khamco-primary font-bold mb-4">
                Recent Activity
              </caption>
              <thead className="text-khamco-primary font-bold bg-[#ddd]">
                <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                  <td className="text-center p-2">Amount Paid</td>
                  <td className="text-center p-2">Payment Type</td>
                  <td className="text-center p-2">Customer</td>
                  <td className="text-center p-2">Date</td>
                </tr>
              </thead>
              <tbody>
                {financeData &&
                  financeData.map((finance, index) => (
                    <tr
                      className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                      key={index}
                    >
                      <td className="text-center p-2">
                        {new Intl.NumberFormat("kw-KW", {
                          style: "currency",
                          currency: "KWD",
                          minimumFractionDigits: 3,
                          maximumFractionDigits: 3,
                        }).format(finance.amount_paid)}
                      </td>
                      <td className="text-center p-2">
                        {finance.payment_type}
                      </td>
                      <td className="text-center p-2">
                        {finance.customer_name} {finance.customer_surname}
                      </td>
                      <td className="text-center p-2">
                        {new Date(finance.date).toLocaleDateString("en-US", {
                          month: "short",
                          day: "numeric",
                          year: "numeric",
                        })}
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
            </div>
          )}
          {selectedIndex === "Profit Margins" && (
            <>
            <div className="overflow-auto">
              <table className="w-full my-8 border-collapse text-[1.2rem]">
                <caption className="text-khamco-primary font-bold mb-4">
                  Profit Margins
                </caption>
                <thead className="text-khamco-primary font-bold bg-[#ddd]">
                  <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                    <td className="text-center p-2">Product Name</td>
                    <td className="text-center p-2">Price</td>
                    <td className="text-center p-2">Cost</td>
                    <td className="text-center p-2">Margin</td>
                  </tr>
                </thead>
                <tbody>
                  {products &&
                    products.map((product, index) => (
                      <tr
                        className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                        key={index}
                      >
                        <td className="text-center p-2">
                          <Link
                            to={`/products/${product._id}`}
                            className="py-[0.5rem] px-[1rem] outline-none border-none text-[blue] text-lg rounded-md cursor-pointer hover:underline"
                            target="_blank"
                          >
                            {product.name}{" "}
                            <i className="fas fa-external-link"></i>
                          </Link>
                        </td>
                        <td className="text-center p-2">
                          {new Intl.NumberFormat("kw-KW", {
                            style: "currency",
                            currency: "KWD",
                            minimumFractionDigits: 3,
                            maximumFractionDigits: 3,
                          }).format(product.price)}
                        </td>
                        <td className="text-center p-2">
                          {product.cost
                            ? new Intl.NumberFormat("kw-KW", {
                                style: "currency",
                                currency: "KWD",
                                minimumFractionDigits: 3,
                                maximumFractionDigits: 3,
                              }).format(product.cost)
                            : "--"}
                        </td>
                        <td
                          className={`text-center p-2 ${
                            product.margin > 0
                              ? "text-green-500"
                              : "text-red-500"
                          } font-bold`}
                        >
                          {product.margin ? `${product.margin} %` : "--"}
                        </td>
                      </tr>
                    ))}
                </tbody>
                <thead className="text-khamco-primary font-bold bg-[#ddd]">
                  <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                    <td className="text-center p-2">Service Name</td>
                    <td className="text-center p-2">Price</td>
                    <td className="text-center p-2"></td>
                    <td className="text-center p-2">Margin</td>
                  </tr>
                </thead>
                <tbody>
                  {services &&
                    services.map((service, index) => (
                      <tr
                        className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                        key={index}
                      >
                        <td className="text-center p-2">{service.service}</td>
                        <td className="text-center p-2">
                          {new Intl.NumberFormat("kw-KW", {
                            style: "currency",
                            currency: "KWD",
                            minimumFractionDigits: 3,
                            maximumFractionDigits: 3,
                          }).format(service.price)}
                        </td>
                        <td className="text-center p-2"></td>
                        <td className="text-center p-2 text-green-500 font-bold">
                          100%
                        </td>
                      </tr>
                    ))}
                </tbody>
              </table>
            </div>
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminRevenue;
