import MeasurementsForm from "./measurements/MeasurementsForm";
import { selectArea } from "../../functions";

const CustomerForm = ({
  defaultMilitary,
  setDefaultMilitary,
  defaultMembership,
  setDefaultMembership,
  customerName,
  setCustomerName,
  setCustomerSurname,
  setCustomerEmail,
  setCustomerPhone,
  setHouseUnit,
  setGovernorate,
  setArea,
  setCountry,
  militaryStatus,
  setMilitaryStatus,
  setMilitaryNumber,
  setMilitaryRank,
  setMilitaryDivision,
  setMembershipStatus,
  setDate,
  areas,
  setAreas,
  clothingOptions,
  setClothingOptions,
  setWaist,
  setNaturalWaist,
  setGarmentLength,
  setHalfAcross,
  setChest,
  setTrouserWaistPosition,
  setSeat,
  setNeck,
  setShoulder,
  setSleeveLength,
  setSideSeamLength,
  setBodyRise,
  setInsideLeg,
  setWrist,
  setTopArm,
  setScyeDepth,
  setArmSleeveHole,
  setFrontalCollar,
  setHip,
  setFrontRise,
  setBackRise,
  setThigh,
  setKnee,
  setHem,
  setOutseam,
  setHeadSize,
  setFootSize,
  setDescription,
  loading,
}) => {
  return (
    <>
      <div className="form-row">
        <label>Customer Name</label>
        <input
          type="text"
          name="name"
          placeholder="Customer name"
          value={customerName ? customerName : null}
          onChange={(e) => setCustomerName(e.target.value)}
        />
      </div>
      <div className="form-row">
        <label>Customer Surname</label>
        <input
          type="text"
          placeholder="Customer surname"
          onChange={(e) => setCustomerSurname(e.target.value)}
        />
      </div>
      <div className="form-row">
        <label>Email</label>
        <input
          type="email"
          placeholder="(Optional)"
          onChange={(e) => setCustomerEmail(e.target.value)}
        />
      </div>
      <div className="form-row">
        <label>Phone Number</label>
        <input
          type="text"
          placeholder="Customer phone number e.g. +965000000000"
          onChange={(e) => setCustomerPhone(e.target.value)}
        />
      </div>
      <div className="form-row">
        <label>House Unit</label>
        <input
          type="text"
          placeholder="Enter street address"
          onChange={(e) => setHouseUnit(e.target.value)}
        />
      </div>
      <div className="form-row">
        <label>Governorate</label>
        <select
          onChange={(e) => {
            selectArea(e.target, setGovernorate, setAreas);
          }}
        >
          <option value="Al Aasima">Al Aasima</option>
          <option value="Hawalli">Hawalli</option>
          <option value="Mubarak Al-Kabeer">Mubarak Al-Kabeer</option>
          <option value="Ahmadi">Ahmadi</option>
          <option value="Farwaniya">Farwaniya</option>
          <option value="Jahra">Jahra</option>
        </select>
      </div>
      <div className="form-row">
        <label>Area</label>
        <select onChange={(e) => setArea(e.target.value)}>
          {areas &&
            areas.map((area, index) => <option key={index}>{area}</option>)}
        </select>
      </div>
      <div className="form-row">
        <label>Country</label>
        <select
          onChange={(e) => {
            setCountry(e.target.value);
          }}
        >
          <option disabled>-- Select country --</option>
          <option value="Kuwait">Kuwait</option>
          <option value="Pakistan">Pakistan</option>
        </select>
      </div>
      <div className="form-row">
        <label>Status</label>
        <div className="inline">
          <span>
            <label htmlFor="civilian" className="cursor-pointer">
              Civilian
            </label>
            <input
              type="checkbox"
              value="Civilian"
              id="civilian"
              checked={defaultMilitary === "Civilian"}
              onChange={(e) => {
                setMilitaryStatus("Civilian");
                setDefaultMilitary("Civilian");
              }}
            />
          </span>
          <span>
            <label htmlFor="military" className="cursor-pointer">
              Military
            </label>
            <input
              type="checkbox"
              value="Military"
              id="military"
              checked={defaultMilitary === "Military"}
              onChange={(e) => {
                setMilitaryStatus("Military");
                setDefaultMilitary("Military");
              }}
            />
          </span>
        </div>
      </div>
      {militaryStatus === "military" && (
        <div className="w-full bg-khamco-ribbon text-white transition-all fadeIn">
          <h4 className="title">Military Details</h4>
          <div className="form-row">
            <label>ID Number</label>
            <input
              type="text"
              onChange={(e) => setMilitaryNumber(e.target.value)}
              className="text-black"
            />
          </div>
          <div className="form-row">
            <label>Position/Rank</label>
            <input
              type="text"
              onChange={(e) => setMilitaryRank(e.target.value)}
              className="text-black"
            />
          </div>
          <div className="form-row">
            <label>Division</label>
            <select
              defaultValue={"airforce"}
              onChange={(e) => setMilitaryDivision(e.target.value)}
              className="text-black"
            >
              <option disabled>-- Select military division --</option>
              <option value="airforce">Air Force</option>
              <option value="ground">Ground Base</option>
              <option value="navy">Navy</option>
              <option value="specialops">Special Operations</option>
            </select>
          </div>
        </div>
      )}
      <div className="form-row">
        <label>Membership Status</label>
        <div className="inline">
          <span>
            <label htmlFor="basic" className="cursor-pointer">
              Basic
            </label>
            <input
              type="checkbox"
              value="Basic"
              id="basic"
              checked={defaultMembership === "Basic"}
              onChange={(e) => {
                setDefaultMembership("Basic");
                setMembershipStatus("Basic");
              }}
            />
          </span>
          <span>
            <label htmlFor="VIP" className="cursor-pointer">
              VIP <i className="fas fa-star blink"></i>
            </label>
            <input
              type="checkbox"
              value="VIP"
              id="VIP"
              checked={defaultMembership === "VIP"}
              onChange={(e) => {
                setDefaultMembership("VIP");
                setMembershipStatus("VIP");
              }}
            />
          </span>
        </div>
      </div>
      <div className="form-row">
        <label>Date</label>
        <input
          type="date"
          onChange={(e) => {
            setDate(e.target.value);
          }}
        />
      </div>
      <MeasurementsForm
        clothingOptions={clothingOptions}
        setClothingOptions={setClothingOptions}
        setWaist={setWaist}
        setNaturalWaist={setNaturalWaist}
        setGarmentLength={setGarmentLength}
        setHalfAcross={setHalfAcross}
        setChest={setChest}
        setTrouserWaistPosition={setTrouserWaistPosition}
        setSeat={setSeat}
        setNeck={setNeck}
        setShoulder={setShoulder}
        setSleeveLength={setSleeveLength}
        setSideSeamLength={setSideSeamLength}
        setBodyRise={setBodyRise}
        setInsideLeg={setInsideLeg}
        setWrist={setWrist}
        setTopArm={setTopArm}
        setScyeDepth={setScyeDepth}
        setArmSleeveHole={setArmSleeveHole}
        setFrontalCollar={setFrontalCollar}
        setHip={setHip}
        setFrontRise={setFrontRise}
        setBackRise={setBackRise}
        setThigh={setThigh}
        setKnee={setKnee}
        setHem={setHem}
        setOutseam={setOutseam}
        setHeadSize={setHeadSize}
        setFootSize={setFootSize}
        setDescription={setDescription}
        loading={loading}
      />
    </>
  );
};

export default CustomerForm;
