const ProductsForm = ({
  setProductName,
  setProductQuantity,
  setProductCategory,
  setProductPhoto,
  setProductPrice,
  setProductDate,
  productName,
  productQuantity,
  productCategory,
  productPhoto,
  productPrice,
  productDate,
  date,
}) => {
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const day = String(date.getDate()).padStart(2, "0");
    return `${year}-${month}-${day}`;
  };

  return (
    <>
      <div className="form-row">
        <label for="name">Product Name</label>
        <input
          type="text"
          placeholder="Enter name of product"
          value={productName}
          onChange={(e) => setProductName(e.target.value)}
        />
      </div>
      <div className="form-row">
        <label for="quantity">Quantity</label>
        <input
          type="number"
          onChange={(e) => setProductQuantity(e.target.value)}
          value={productQuantity}
        />
      </div>
      <div className="form-row">
        <label for="category">Category</label>
        <select
          onChange={(e) => setProductCategory(e.target.value)}
        >
          <option value="cotton" selected={productCategory === "cotton"}>Cotton</option>
          <option value="linen" selected={productCategory === "linen"}>Linen</option>
          <option value="wool" selected={productCategory === "wool"}>Wool</option>
          <option value="polyester" selected={productCategory === "polyester"}>Polyester</option>
          <option value="other" selected={productCategory === "other"}>Other</option>
        </select>
      </div>
      <div className="form-row">
        <label for="picture">Product Photo</label>
        <input
          type="file"
          name="cover"
          onChange={(e) => setProductPhoto(e.target.files[0])}
          value={productPhoto}
        />
      </div>
      <div className="form-row">
        <label for="price">Price</label>
        <input
          type="number"
          step="0.01"
          onChange={(e) => setProductPrice(e.target.value)}
          value={productPrice}
        />
      </div>
      <div className="form-row">
        <label for="date">Date</label>
        <input
          type="date"
          onChange={(e) =>
            setProductDate(new Date(e.target.value).toISOString().split("T")[0])
          }
          value={date ? formatDate(date) : null}
        />
      </div>
      <div className="form-row">
        <button type="submit">Submit</button>
      </div>
    </>
  );
};

export default ProductsForm;
