import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import CustomerForm from "./CustomerForm";
import { useTranslation } from "react-i18next";
import { areaNames } from "../../utilities";

const EditCustomer = () => {
  const { id } = useParams();
  const [customer, setCustomer] = useState(null);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/customers/${id}`
    : `https://khamco-shop-backend.onrender.com/customers/${id}`;
  const authKey = localStorage.getItem("jwt");
  const [customerName, setCustomerName] = useState("");
  const [customerSurname, setCustomerSurname] = useState("");
  const [customerEmail, setCustomerEmail] = useState("");
  const [customerPhone, setCustomerPhone] = useState("");
  const [houseUnit, setHouseUnit] = useState("");
  const [governorate, setGovernorate] = useState("Al Aasima");
  const [area, setArea] = useState("Abdulla Al-Salem");
  const [country, setCountry] = useState("Kuwait");
  const [militaryStatus, setMilitaryStatus] = useState("Civilian");
  const [membershipStatus, setMembershipStatus] = useState("Basic");
  const [militaryNumber, setMilitaryNumber] = useState("");
  const [militaryRank, setMilitaryRank] = useState("");
  const [militaryDivision, setMilitaryDivision] = useState("");
  const [date, setDate] = useState(null);
  const [clothingOptions, setClothingOptions] = useState([]);
  // Measurement fields
  const [specialOrder, setSpecialOrder] = useState("");
  const [alterations, setAlterations] = useState("");
  const [notes, setNotes] = useState("");
  const [areas, setAreas] = useState(areaNames);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const { t } = useTranslation();

  useEffect(() => {
    const fetchData = async () => {
      await axios
        .get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setCustomer(response.data.customer);
          setMilitaryStatus(response.data.customer.military_status);
          document.title = `${t("Edit Customer Details")} - ${
            response.data.customer._id
          } Khamco Shop`;
        })
        .catch((error) => {
          if (error.response) {
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response.data?.message || error.message);
            }
          } else {
            // Handle network errors or errors without a response object
            toast.error("An error occurred: " + error.message);
          }
          setCustomer(null);
        });
    };

    fetchData();
  }, [apiUrl, authKey, navigate, t]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);

    await axios
      .put(
        apiUrl,
        {
          name: customerName,
          surname: customerSurname,
          email: customerEmail,
          phone: customerPhone,
          house_unit: houseUnit,
          governorate: governorate,
          area: area,
          country: country,
          military_status: militaryStatus,
          membership_status: membershipStatus,
          date_created: date,
          military_idnumber: militaryNumber,
          military_rank: militaryRank,
          military_department: militaryDivision,
          clothing_options: clothingOptions,
          dishdasha_garment_length: dishdashaLength,
          dishdasha_front_height: dishdashaFrontHeight,
          dishdasha_back_height: dishdashaBackHeight,
          shirt_type: shirtType,
          garment_length: garmentLength,
          pants_garment_length: pantsLength,
          shorts_garment_length: shortsLength,
          dress_garment_length: dressLength,
          sleeve: sleeve,
          shirt_sleeve: shirtSleeve,
          dress_sleeve: dressSleeve,
          sleepwear_sleeve: sleepwearSleeve,
          chest: chest,
          collar: collar,
          shoulders: shoulders,
          belly: belly,
          waist: waist,
          hips: hips,
          arm_hole: armHole,
          bicep_tricep: bicep_tricep,
          elbow: elbow,
          wrist: wrist,
          inseam: inseam,
          thighs: thigh,
          calf: calf,
          leg_foot: leg_foot,
          back_width: backWidth,
          back_height: backHeight,
          front_chest_width: frontChestWidth,
          special_order: specialOrder,
          alterations: alterations,
          description: description,
          notes: notes,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        toast.success(response.data.message);
        navigate(`/customers/${response.data.customer._id}`);
        setLoading(false);
      })
      .catch((error) => {
        setLoading(false);
        toast.error(error.response?.data?.message || error.message);
      });
  };

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section
          className="p-[4rem] grid justify-center items-center"
        >
          {customer && (
            <>
              <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
                <Link
                  to={`/customers/${customer._id}`}
                  className="text-[blue] no-underline hover:underline cursor-pointer"
                >
                  <i className="fas fa-arrow-left"></i> Customer {customer.name}{" "}
                  {customer.surname}
                </Link>
              </div>
              <h1 className="text-center text-[2rem] font-minimoBold">
                {t("Edit Customer Details")}
              </h1>
              <form
                onSubmit={handleSubmit}
                className="flex flex-col justify-center items-center w-[90vw]"
              >
                <CustomerForm
                  isCreateCustomer={false}
                  customer={customer}
                  membershipStatus={membershipStatus}
                  setCustomerName={setCustomerName}
                  setCustomerSurname={setCustomerSurname}
                  setCustomerEmail={setCustomerEmail}
                  setCustomerPhone={setCustomerPhone}
                  setHouseUnit={setHouseUnit}
                  setGovernorate={setGovernorate}
                  setArea={setArea}
                  setCountry={setCountry}
                  militaryStatus={militaryStatus}
                  setMilitaryStatus={setMilitaryStatus}
                  setMilitaryNumber={setMilitaryNumber}
                  setMilitaryRank={setMilitaryRank}
                  setMilitaryDivision={setMilitaryDivision}
                  setMembershipStatus={setMembershipStatus}
                  setDate={setDate}
                  clothingOptions={clothingOptions}
                  setClothingOptions={setClothingOptions}
                  setDishdashaLength={setDishdashaLength}
                  setDishdashaFrontHeight={setDishdashaFrontHeight}
                  setDishdashaBackHeight={setDishdashaBackHeight}
                  shirtType={shirtType}
                  setShirtType={setShirtType}
                  setGarmentLength={setGarmentLength}
                  setPantsLength={setPantsLength}
                  setShortsLength={setShortsLength}
                  setDressLength={setDressLength}
                  setCollar={setCollar}
                  setShoulders={setShoulders}
                  setSleeve={setSleeve}
                  setShirtSleeve={setShirtSleeve}
                  setDressSleeve={setDressSleeve}
                  setDescription={setDescription}
                  setSleepwearSleeve={setSleepwearSleeve}
                  setChest={setChest}
                  setBelly={setBelly}
                  setWaist={setWaist}
                  setHips={setHips}
                  setArmHole={setArmHole}
                  setBicepTricep={setBicepTricep}
                  loading={loading}
                  areas={areas}
                  setElbow={setElbow}
                  setWrist={setWrist}
                  setInseam={setInseam}
                  setThigh={setThigh}
                  setCalf={setCalf}
                  setLegFoot={setLegFoot}
                  setBackWidth={setBackWidth}
                  setBackHeight={setBackHeight}
                  setFrontChestWidth={setFrontChestWidth}
                  setSpecialOrder={setSpecialOrder}
                  setAlterations={setAlterations}
                  setNotes={setNotes}
                  setAreas={setAreas}
                />
                <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
                  <button
                    type="submit"
                    className="text-center bg-[blue] w-full md:w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
                  >
                    {loading ? (
                      <i className="fas fa-spinner animate-spin"></i>
                    ) : (
                      `${t("Submit")}`
                    )}
                  </button>
                </div>
              </form>
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default EditCustomer;
