import "./App.css";
import { Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import NotFound from "./pages/404";
import { Suspense, useEffect } from "react";
import { Slide, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useStoreActions } from "easy-peasy";
import { jwtDecode } from "jwt-decode";
import { appRoutes } from "./routes";

function App() {
  const authKey = localStorage.getItem("jwt");
  const setUser = useStoreActions((state) => state.setUser);

  useEffect(() => {
    document.title = "Khamco Shop";

    const getUser = () => {
      if (authKey) {
        try {
          const decoded = jwtDecode(authKey);
          setUser({
            id: decoded.id,
            email: decoded.email,
            role: decoded.role,
            language: decoded.language,
          });
        } catch (error) {
          console.error(error);
        }
      }
    };
    getUser();
  }, [authKey, setUser]);

  return (
    <Suspense
      fallback={
        <div className="w-screen h-screen flex items-center justify-center">
          <i className="text-[5rem] text-khamco-ribbon fas fa-spinner animate-spin"></i>
        </div>
      }
    >
      <Routes>
        <Route exact path="/users/login" element={<Login />} />
        {appRoutes.map((route) => (
          <Route
            key={route.path}
            exact
            path={route.path}
            element={
              authKey ? <route.component /> : <Navigate to="/users/login" />
            }
          />
        ))}
        {/* Misc Routes */}
        <Route exact path="/unauthorized" element={<NotFound />} />
        <Route exact path="*" element={<NotFound />} />
      </Routes>
      <ToastContainer
        position="top-right"
        hideProgressBar={true}
        newestOnTop={true}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
        transition={Slide}
        autoClose={false}
      />
    </Suspense>
  );
}

export default App;
