import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import ProductsForm from "./ProductsForm";
import { Link, useParams } from "react-router-dom";
import axios from "axios";

const Edit = () => {
  const { id } = useParams();
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [productName, setProductName] = useState("");
  const [productQuantity, setProductQuantity] = useState("");
  const [productCategory, setProductCategory] = useState("");
  const [productPhoto, setProductPhoto] = useState("");
  const [productPrice, setProductPrice] = useState("");
  const [productDate, setProductDate] = useState("");
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/services/${id}`
    : `https://khamcotailor.com/services/${id}`;
  const authKey = localStorage.getItem("jwt");

  useEffect(() => {
    document.title = "Edit Product Details -  Khamco Shop";

    const fetchProduct = async () => {
      try {
        const response = await axios.get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        });
        setPro(response.data.service.service);
        setPrice(response.data.service.price);
        setDate(response.data.service.date);
      } catch (error) {
        setService({});
        toast.error(error.message);
      }
    };

    fetchProduct();
  }, []);
  return (
    <>
      <Navbar />
      <main>
        <section
          className="p-[4rem] grid justify-center items-center"
          dir={writingDirection}
        >
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
            <Link to={`/products/${product.id}`}>
              <i className="fas fa-arrow-left"></i> {product.name}
            </Link>
          </div>
          <h1 className="title">Edit Product Details</h1>
          <p className="message">{message}</p>
          <form
            action="/products/<%= product.id %>?_method=PUT"
            method="POST"
            className="flex flex-col justify-center items-center w-[90vw]"
          >
            <ProductsForm />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Edit;
