import React, { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import Navbar from "../../../components/Navbar";
import Footer from "../../../components/Footer";
import axios from "axios";
import { toast } from "react-toastify";
import { useTranslation } from "react-i18next";
import {apiUrl, authKey} from "../../../utilities";

const Measurements = () => {
  const { id } = useParams();
  const [customer, setCustomer] = useState(null);
  const [measurements, setMeasurements] = useState(null);
  // const apiUrl = window.location.origin.includes("localhost")
  //   ? `http://localhost:5000/customers/${id}/measurements`
  //   : `https://khamco-shop-backend.onrender.com/customers/${id}/measurements`;
  // const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true);
  const { t } = useTranslation();

  useEffect(() => {
    const fetchData = async () => {
      await axios
        .get(`${apiUrl}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setIsLoading(false);
          setCustomer(response.data.customer);
          setMeasurements(response.data.measurements);
          document.title = "All Measurements";
        })
        .catch((error) => {
          setIsLoading(false);
          if (error.response) {
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response.data?.message || error.message);
            }
          } else {
            // Handle network errors or errors without a response object
            toast.error("An error occurred: " + error.message);
          }
        });
    };

    fetchData();
  }, [apiUrl, authKey, navigate]);

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <section className="customer-details">
          {isLoading && (
            <>
              <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
                <div className="flex justify-between gap-[2rem] items-center my-[2rem] mx-auto w-full flex-wrap">
                  {new Array(6).fill(null).map((_, index) => (
                    <Link
                      key={`placeholder-${index}`}
                      to={``}
                      className="py-[2rem] px-[4rem] outline-none border-none bg-[#666] rounded-[0.5rem] animate-pulse"
                    ></Link>
                  ))}
                  <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem] w-2/4 bg-[#666] animate-pulse m-auto h-[100vh] my"></div>
                </div>
              </div>
            </>
          )}
          {customer && measurements && (
            <>
              <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                <Link
                  to={`/customers/${customer._id}`}
                  className="text-[blue] no-underline hover:underline cursor-pointer"
                >
                  <i className="fas fa-arrow-left"></i>
                </Link>
              </div>
              <h1 className="text-center text-[2rem] font-minimoBold">
                {t("All Measurements")}
              </h1>
              <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem] grid-cols-1">
                <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                  <Link
                    to={`/customers/${customer._id}/create-measurement`}
                    className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90 m-auto"
                  >
                    {t("New Measurement")} <i className="fas fa-plus"></i>
                  </Link>
                </div>
                {measurements && measurements.length > 0 ? (
                  measurements
                    .sort(
                      (a, b) =>
                        new Date(b.date_created) - new Date(a.date_created)
                    )
                    .map((measurement, index) => (
                      <div
                        className="flex flex-col gap-[2rem] w-full md:w-2/4 m-auto p-[1rem] rounded-[1rem] shadow-md shadow-[#333]"
                        key={index}
                      >
                        <span className="inline-flex justify-start gap-[0.5rem] text-balance flex-wrap">
                          <strong>{t("Date Created")}:</strong>
                          {new Date(
                            measurement.date_created
                          ).toLocaleDateString("en-us", {
                            month: "long",
                            year: "numeric",
                            day: "numeric",
                          })}
                        </span>
                        <span className="inline-flex justify-start gap-[0.5rem] text-balance flex-wrap">
                          <strong>{t("Created By")}:</strong>
                          <p>{measurement.created_by || "--"}</p>
                        </span>
                        <span className="inline-flex justify-start gap-[0.5rem] text-balance flex-wrap">
                          <strong>{t("Description")}:</strong>
                          <p>{measurement.description || "--"}</p>
                        </span>
                        <Link
                          to={`/customers/${customer._id}/measurements/${measurement._id}`}
                          className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90 w-2/4 m-auto text-center"
                        >
                          {t("Details")}
                        </Link>
                      </div>
                    ))
                ) : (
                  <p>No measurements for this customer</p>
                )}
              </div>
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Measurements;
