import React, { useState } from "react";
import Ribbon from "../components/Ribbon";
import { Link } from "react-router-dom";

const Login = () => {
  const [message, setMessage] = useState("Hello");
  const [checkMaster, setCheckMaster] = useState(false);
  return (
    <>
      <Ribbon />
      <main>
        <section className="p-[4rem] grid justify-center items-center">
          <h1 className="title">User Login</h1>
          <form action="/login" method="POST" className="flex flex-col justify-center items-center w-[90vw]">
            <div className="form-row">
              <p className="message">{message}</p>
            </div>
            {checkMaster && (
              <div className="form-row">
                <Link href="/users/create-master" className="alert flash">
                  Click here to register a new{" "}
                  <strong>Master Administrator</strong>. A user with these
                  privileges has access to all resources.{" "}
                  <i className="fas fa-user-plus"></i>
                </Link>
              </div>
            )}
            <div className="form-row">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                placeholder="Enter your email address"
              ></input>
            </div>
            <div className="form-row">
              <label htmlFor="accept">Password</label>
              <input
                type="password"
                name="password"
                id="password"
                placeholder="Enter your password"
              />
              <button id="showHide" type="button" className="show-hide">
                Show
              </button>
            </div>
            <div className="flex-wrapper">
              <a href="/users/forgot-password">
                Forgotten your password? Reset it now.
              </a>
            </div>
            <div className="row">
              <button type="submit">Submit</button>
            </div>
          </form>
        </section>
      </main>
      {/* <script>
        var showHide = document.getElementById("showHide"); var password =
        document.getElementById("password"); showHide.addEventListener("click",
        function (e) {e.preventDefault()}; if (password.getAttribute("type") ===
        "password") {password.setAttribute("type", "text")};
        showHide.textContent = "Hide"; } else{" "}
        {password.setAttribute("type", "password")}
        showHide.textContent = "Show"; } });
      </script> */}
    </>
  );
};

export default Login;
