import { useState } from "react";

const UserForm = ({
  setFirstName,
  setLastName,
  setRole,
  setLanguage,
  setUserPhoto,
  setEmail,
  setOldPassword,
  setPassword,
  setConfirmPassword,
  loading,
  firstName,
  lastName,
  role,
  language,
  email,
  page,
}) => {
  const [oldPasswordType, setOldPasswordType] = useState(false);
  const [passwordType, setPasswordType] = useState(false);
  const [password2Type, setPassword2Type] = useState(false);

  const handleRoleChange = (e) => {
    const value = e.target.value;
    setRole(
      (prevRoles) =>
        prevRoles.includes(value)
          ? prevRoles.filter((role) => role !== value) // Deselect
          : [...prevRoles, value] // Select
    );
    console.log("Roles: ", role);
  };

  return (
    <>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          First Name
        </label>
        <input
          className="text-lg my-2 p-2 border border-gray-800 rounded-md outline-none font-sans focus:border-[blue]"
          type="text"
          placeholder="Enter your first name"
          value={firstName || undefined}
          onChange={(e) => setFirstName(e.target.value)}
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Last Name
        </label>
        <input
          className="text-lg my-2 p-2 border border-gray-800 rounded-md outline-none font-sans focus:border-[blue]"
          type="text"
          placeholder="Enter your last name"
          value={lastName || undefined}
          onChange={(e) => setLastName(e.target.value)}
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Role
        </label>
        <div className="flex gap-4 w-full justify-center flex-wrap my-[2rem]">
          <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
            <label
              className="text-2xl my-2 select-none self-start font-medium cursor-pointer"
              htmlFor="Administrator"
            >
              Administrator{" "}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 accent-khamco-primary cursor-pointer"
              id="Administrator"
              type="checkbox"
              value="Administrator"
              onChange={handleRoleChange}
            />
          </span>
          <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
            <label
              className="text-2xl my-2 select-none self-start font-medium cursor-pointer"
              htmlFor="Measurer"
            >
              Measurer{" "}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 accent-khamco-primary cursor-pointer"
              id="Measurer"
              type="checkbox"
              value="Measurer"
              onChange={handleRoleChange}
            />
          </span>
          <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
            <label
              className="text-2xl my-2 select-none self-start font-medium cursor-pointer"
              htmlFor="Cutter"
            >
              Cutter{" "}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 accent-khamco-primary cursor-pointer"
              id="Cutter"
              type="checkbox"
              value="Cutter"
              onChange={handleRoleChange}
            />
          </span>
          <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
            <label
              className="text-2xl my-2 select-none self-start font-medium cursor-pointer"
              htmlFor="Tailor"
            >
              Tailor{" "}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 accent-khamco-primary cursor-pointer"
              id="Tailor"
              type="checkbox"
              value="Tailor"
              onChange={handleRoleChange}
            />
          </span>
          <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
            <label
              className="text-2xl my-2 select-none self-start font-medium cursor-pointer"
              htmlFor="Driver"
            >
              Driver{" "}
            </label>
            <input
              className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 accent-khamco-primary cursor-pointer"
              id="Driver"
              type="checkbox"
              value="Driver"
              onChange={handleRoleChange}
            />
          </span>
          <UserRole
            label={"Driver"}
            options={role}
            setOptions={setRole}
          />
        </div>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Language
        </label>
        <select
          className="text-lg my-2 p-2 border border-gray-800 rounded-md outline-none font-sans focus:border-[blue]"
          onChange={(e) => setLanguage(e.target.value)}
          value={language || undefined}
        >
          <option value="">-- Select user language --</option>
          <option value="en" label="English"></option>
          <option value="hi" label="Hindi"></option>
          <option value="ur" label="Urdu"></option>
          <option value="ar" label="Arabic"></option>
        </select>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          User Photo
        </label>
        <input
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="file"
          name="cover"
          onChange={(e) => setUserPhoto(e.target.files[0])}
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Email
        </label>
        <input
          className="text-lg my-2 p-2 border border-gray-800 rounded-md outline-none font-sans focus:border-[blue]"
          type="email"
          placeholder="Enter your email address"
          value={email || undefined}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>
      {page === "edit" && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label className="text-lg my-2 select-none self-start font-medium">
            Old Password
          </label>
          <input
            className="text-lg my-2 p-2 border border-gray-800 rounded-md outline-none font-sans focus:border-[blue]"
            type={oldPasswordType ? "text" : "password"}
            placeholder="Enter your old password"
            onChange={(e) => setOldPassword(e.target.value)}
          />
          <button
            type="button"
            className="absolute w-[12%] right-0 top-[60%] translate-y-[-50%] cursor-pointer capitalize hover:text-khamco-primary my-2"
            onClick={() => setOldPasswordType(!oldPasswordType)}
          >
            {oldPasswordType ? "Hide" : "Show"}
          </button>
        </div>
      )}
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          New Password
        </label>
        <input
          className="text-lg my-2 p-2 border border-gray-800 rounded-md outline-none font-sans focus:border-[blue]"
          type={passwordType ? "text" : "password"}
          placeholder="Enter your desired password"
          onChange={(e) => setPassword(e.target.value)}
        />
        <button
          type="button"
          className="absolute w-[12%] right-0 top-[60%] translate-y-[-50%] cursor-pointer capitalize hover:text-khamco-primary my-2"
          onClick={() => setPasswordType(!passwordType)}
        >
          {passwordType ? "Hide" : "Show"}
        </button>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Confirm Password
        </label>
        <input
          className="text-lg my-2 p-2 border border-gray-800 rounded-md outline-none font-sans focus:border-[blue]"
          type={password2Type ? "text" : "password"}
          placeholder="Confirm your password"
          onChange={(e) => setConfirmPassword(e.target.value)}
        />
        <button
          type="button"
          className="absolute w-[12%] right-0 top-[60%] translate-y-[-50%] cursor-pointer capitalize hover:text-khamco-primary my-2"
          onClick={() => setPassword2Type(!password2Type)}
        >
          {password2Type ? "Hide" : "Show"}
        </button>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <button
          type="submit"
          className="bg-[blue] w-1/2 mx-auto p-4 text-white rounded-full border-none outline-none hover:bg-blue-900"
          disabled={loading}
        >
          {loading ? <i className="fas fa-spinner animate-spin"></i> : "Submit"}
        </button>
      </div>
    </>
  );
};

export const UserRole = ({ label, options, setOptions }) => {
  const [isChecked, setIsChecked] = useState(false);
  // Generate a unique ID for each Clothing component
  const inputId = `name_${label.replace(/\s+/g, "_").toLowerCase()}`;

  const add = () => {
    console.log(options);
    if (isChecked) {
      setOptions((prevArray) =>
        prevArray.filter((item) => item.name !== label)
      );
    } else {
      setOptions((prevArray) => {
        // Check if item already exists
        const exists = prevArray.some((item) => item.name === label);
        if (!exists) {
          return [...prevArray, { name: label }];
        }

        return prevArray;
      });
    }
  };
  return (
    <>
      <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
        <label
          className={`p-2 w-[10rem] md:w-[8rem] flex flex-col items-center gap-2 cursor-pointer rounded-xl border-2 border-khamco-primary text-center font-medium ${
          isChecked ? "bg-[#ccc]" : ""
        }`}
          htmlFor={inputId}
          onClick={add}
        >
          {label}
        </label>
        <input
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 accent-khamco-primary cursor-pointer"
          id={inputId}
          type="checkbox"
          onChange={() => {
            setIsChecked(!isChecked);
          }}
        />
      </span>
    </>
  );
};

export default UserForm;
