import axios from "axios";
import React, { useEffect, useRef, useState } from "react";
import { useTranslation } from "react-i18next";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { apiUrl, authKey } from "../../utilities";
import { Chart, registerables } from "chart.js";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import Footer from "../../components/Footer";
import Pending from "../../components/Pending";
Chart.register(...registerables);

const AdminExpenditures = () => {
  const navigate = useNavigate();
  const { t } = useTranslation();
  const chartRef = useRef(null);
  const [salaries, setSalaries] = useState(0);
  const [costs, setCosts] = useState(0);
  const [rent, setRent] = useState(0);
  const [isLoading, setIsLoading] = useState(true);
  const [isPending, setIsPending] = useState(false);

  useEffect(() => {
    let isMounted = true;

    document.title = "Admin Dashboard - Khamco Shop";

    const fetchData = async () => {
      try {
        const response = await axios.get(`${apiUrl}/admin/dashboard`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });
        if (isMounted) {
          setSalaries(response.data.salaries);
          setCosts(response.data.costs);
          setRent(response.data.rent);
          setIsLoading(false);
        }
      } catch (error) {
        if (isMounted) {
          setIsLoading(true);
          if (error.response) {
            if (
              error.response.status === 403 ||
              error.response.status === 400
            ) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response.data?.message || error.message);
            }
          } else {
            // Handle network errors or errors without a response object
            toast.error("An error occurred: " + error.message);
          }
        }
      }
    };

    fetchData();

    return () => {
      isMounted = false;
    };
  }, [navigate]);

  useEffect(() => {
    const ctx = chartRef.current.getContext("2d");

    const chartData = {
      labels: ["Employee Salaries", "Product Costs", "Rent"],
      datasets: [
        {
          label: "Business Expenditures",
          data: [salaries || 0, costs || 0, rent || 0],
          backgroundColor: ["#ed1965", "#283688", "rgba(255, 206, 86, 0.6)"],
          borderColor: ["black", "black", "black"],
          borderWidth: 1,
        },
      ],
    };

    const options = {
      scales: {
        y: {
          beginAtZero: true,
          ticks: {
            callback: function (value) {
              return new Intl.NumberFormat("kw-KW", {
                style: "currency",
                currency: "KWD",
                minimumFractionDigits: 3,
                maximumFractionDigits: 3,
              }).format(value);
            },
          },
        },
      },
    };

    const chart = new Chart(ctx, {
      type: "bar",
      data: chartData,
      options: options,
    });

    return () => {
      chart.destroy();
    };
  }, [salaries, costs, rent]);

  return (
    <>
      {isLoading && <Pending />}
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <AdminPanel />
        <section className="admin-dashboard font-arial">
          <div className="flex justify-end gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <button className="text-[blue] no-underline hover:underline cursor-pointer">
              Manage <i className="fas fa-business-time"></i>
            </button>
          </div>
          <div className="w-full md:w-2/3 mx-auto my-8">
            <h1 className="text-center text-[2rem] font-minimoBold">
              Expenditure Overview
            </h1>
            <canvas
              id="revenueChart"
              width="400"
              height="150"
              ref={chartRef}
            ></canvas>
          </div>
        </section>
      </main>
      <Footer />
      <dialog
        className="fixed top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 w-[90%] md:w-2/4 rounded-[2rem] shadow-md shadow-[#444] overflow-y-auto overflow-x-hidden max-h-[80vh] py-[2rem] px-0"
        open={openDialog}
      >
        <div className="p-[4rem] grid justify-center items-center w-full">
          <form
            className="flex flex-col justify-center items-center w-[80vw]"
            onSubmit={submitNotes}
          >
            <button
              className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer hover:opacity-90 absolute top-[5%] left-[5%]"
              type="button"
              onClick={() => setOpenDialog(false)}
            >
              <i className="fas fa-times"></i>
            </button>
            <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
              <h2 className="text-center text-[2rem] font-minimoBold">
                {t("Add Note")}
              </h2>
            </div>
            <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
              <label className="text-lg my-2 select-none self-start font-medium">
                {t("Notes")}
              </label>
              <textarea
                className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                placeholder="Enter your note here"
              ></textarea>
            </div>
            <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
              <button
                type="submit"
                className="text-center bg-[blue] w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
              >
                {isPending ? (
                  <i className="fas fa-spinner animate-spin"></i>
                ) : (
                  t("Submit")
                )}
              </button>
            </div>
          </form>
        </div>
      </dialog>
    </>
  );
};

export default AdminExpenditures;
