import { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import FormFields from "./Form_Fields";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { useStoreState } from "easy-peasy";

const CreateCustomer = () => {
  const [defaultMilitary, setDefaultStatus] = useState("civilian");
  const [defaultMembership, setDefaultMembership] = useState("Basic");
  const [customerName, setCustomerName] = useState("");
  const [customerSurname, setCustomerSurname] = useState("");
  const [customerEmail, setCustomerEmail] = useState("");
  const [customerPhone, setCustomerPhone] = useState("");
  const [houseUnit, setHouseUnit] = useState("");
  const [governorate, setGovernorate] = useState("");
  const [area, setArea] = useState("");
  const [country, setCountry] = useState("");
  const [militaryStatus, setMilitaryStatus] = useState(false);
  const [membershipStatus, setMembershipStatus] = useState(false);
  const [militaryNumber, setMilitaryNumber] = useState(false);
  const [militaryRank, setMilitaryRank] = useState(false);
  const [militaryDivision, setMilitaryDivision] = useState(false);
  const [date, setDate] = useState(null);
  const [clothingOptions, setClothingOptions] = useState([]);
  const [description, setDescription] = useState("");
  const [areas, setAreas] = useState([
    "Abdulla Al-Salem",
    "Adailiya",
    "Bnaid Al-Qar",
    "Daʿiya",
    "Dasma",
    "Doha",
    "Doha Port",
    "Faiha'",
    "Failaka",
    "Granada",
    "Jaber Al-Ahmad City",
    "Jibla",
    "Kaifan",
    "Khaldiya",
    "Mansūriya",
    "Mirgab",
    "Nahdha",
    "North West Sulaibikhat",
    "Nuzha",
    "Qadsiya",
    "Qurtuba",
    "Rawda",
    "Shamiya",
    "Sharq",
    "Shuwaikh",
    "Shuwaikh Industrial Area",
    "Shuwaikh Port",
    "Sulaibikhat",
    "Surra",
    "Umm an Namil Island",
    "Yarmouk",
  ]);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/customers"
    : "https://khamcotailor.com/customers";
  const authKey = localStorage.getItem("jwt");
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [message, setMessage] = useState("");
  const [customer, setCustomer] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    document.title = "New Customer | Khamco Shop";

    console.log(clothingOptions);

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();
  }, [clothingOptions, userLanguage]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    console.log({
      name: customerName,
      surname: customerSurname,
      email: customerEmail,
      phone: customerPhone,
      house_unit: houseUnit,
      governorate: governorate,
      area: area,
      country: country,
      status: militaryStatus,
      membership_status: membershipStatus,
      date_created: date,
      military_idnumber: militaryNumber,
      military_rank: militaryRank,
      military_department: militaryDivision,
      clothing_options: clothingOptions,
      description: description,
    });
    // await axios
    //   .post(apiUrl, {
    //     customer_name: customerName
    //   },{
    //     headers: { Authorization: `Bearer ${authKey}` },
    //   })
    //   .then((response) => {
    //     navigate(`/customers/dependents/dependents-prompt/${customer._id}`);
    //     setCustomer(response.data.customer);
    //   })
    //   .catch((error) => {
    //     setMessage(error.message);
    //   });
  };

  const hasTop = () => {
    return clothingOptions.includes("T-Shirt") || clothingOptions.includes("Girl T-Shirt") || clothingOptions.includes("Polo Shirt") || clothingOptions.includes("Shirt") || clothingOptions.includes("Long Shirt") || clothingOptions.includes("Jacket") || clothingOptions.includes("Girl Hoodie") || clothingOptions.includes("Suit") || clothingOptions.includes("Dress") || clothingOptions.includes("Tank Top") || clothingOptions.includes("Bra");
  }

  const hasBottom = () => {
    return clothingOptions.includes("Pants") || clothingOptions.includes("Shorts") || clothingOptions.includes("Ragged Shorts") || clothingOptions.includes("Suit") || clothingOptions.includes("Girl Dress");
  }

  const hasHeadWear = () => {
    return clothingOptions.includes("Beanie") || clothingOptions.includes("Snapback");
  }

  const isVest = () => {
    return clothingOptions.includes("")
  }

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          className="p-[4rem] grid justify-center items-center"
          dir={writingDirection}
        >
          <div className="row">
            <Link to="/customers">
              <i className="fas fa-arrow-left"></i> Customers
            </Link>
          </div>
          <h1 className="title">New Customer</h1>
          <p className="message">{message}</p>
          <form
            className="flex flex-col justify-center items-center w-[90vw]"
            onSubmit={handleSubmit}
          >
            <FormFields
              defaultMilitary={defaultMilitary}
              setDefaultStatus={setDefaultStatus}
              defaultMembership={defaultMembership}
              setDefaultMembership={setDefaultMembership}
              setCustomerName={setCustomerName}
              setCustomerSurname={setCustomerSurname}
              setCustomerEmail={setCustomerEmail}
              setCustomerPhone={setCustomerPhone}
              setHouseUnit={setHouseUnit}
              setGovernorate={setGovernorate}
              setArea={setArea}
              setCountry={setCountry}
              militaryStatus={militaryStatus}
              setMilitaryStatus={setMilitaryStatus}
              setMilitaryNumber={setMilitaryNumber}
              setMilitaryRank={setMilitaryRank}
              setMilitaryDivision={setMilitaryDivision}
              setMembershipStatus={setMembershipStatus}
              setDate={setDate}
              clothingOptions={clothingOptions}
              setClothingOptions={setClothingOptions}
              setDescription={setDescription}
              areas={areas}
              setAreas={setAreas}
            />
            {clothingOptions.length > 0 && (
              <>
                {/* Tops and Pants */}
                {hasTop() && (
                  <div className="form-row">
                    <input type="number" step={0.1} placeholder="Waist" />
                  </div>
                )}
                {/* Shirts (long sleeve and short) */}
                <div className="form-row">
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Natural Waist Length"
                  />
                </div>
                <div className="form-row">
                  <h4 className="title mb-8">Measurements</h4>
                  <div className="inline">
                    <input
                      type="number"
                      step={0.1}
                      placeholder="Garment Length"
                    />
                    <input
                      type="number"
                      step={0.1}
                      placeholder="Half Across Back"
                    />
                  </div>
                  <div className="inline">
                    <input type="number" step={0.1} placeholder="Chest" />
                    <input
                      type="number"
                      step={0.1}
                      placeholder="Trouser Waist Position"
                    />
                  </div>
                  <div className="inline">
                    <input type="number" step={0.1} placeholder="Seat" />
                    <input type="number" step={0.1} placeholder="Neck" />
                  </div>
                  <div className="inline">
                    <input type="number" step={0.1} placeholder="Shoulder" />
                    <input
                      type="number"
                      step={0.1}
                      placeholder="Sleeve Length"
                    />
                  </div>
                  <div className="inline">
                    <input
                      type="number"
                      step={0.1}
                      placeholder="Side Seam Length"
                    />
                    <input type="number" step={0.1} placeholder="Body Rise" />
                  </div>
                  <div className="inline">
                    <input type="number" step={0.1} placeholder="Inside Leg" />
                    <input type="number" step={0.1} placeholder="Wrist" />
                  </div>
                  <div className="inline">
                    <input type="number" step={0.1} placeholder="Top Arm" />
                    <input type="number" step={0.1} placeholder="Scye Depth" />
                  </div>
                  {/* Vests only */}
                  <div className="inline">
                    <input type="number" step={0.1} placeholder="Sleeve Hole" />
                    <input
                      type="number"
                      step={0.1}
                      placeholder="Collar Opening"
                    />
                  </div>
                </div>
                {/* Pants/Trousers: */}
                <div className="form-row">
                  <input type="number" step={0.1} placeholder="Hip" />
                </div>
                <div className="form-row">
                  <div className="inline">
                    <input type="number" step={0.1} placeholder="Front Rise" />
                    <input type="number" step={0.1} placeholder="Back Rise" />
                  </div>
                  <div className="inline">
                    <input type="number" step={0.1} placeholder="Thigh" />
                    <input type="number" step={0.1} placeholder="Knee" />
                  </div>
                  <div className="inline">
                    <input type="number" step={0.1} placeholder="Hem" />
                    <input type="number" step={0.1} placeholder=" Outseam" />
                  </div>
                </div>
                {/* Hats */}
                <div className="form-row">
                  <input type="number" step={0.1} placeholder="Head Size" />
                </div>
                {/* Footwear */}
                <div className="form-row">
                  <input type="number" step={0.1} placeholder="Foot Size" />
                </div>
              </>
            )}
            <div className="form-row">
              <textarea
                placeholder="Extra notes (optional)"
                onChange={(e) => setDescription(e.target.value)}
              ></textarea>
            </div>
            <div className="form-row">
              <button type="submit">Submit</button>
            </div>
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default CreateCustomer;
