import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import UserForm from "./UserForm";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

const CreateMaster = () => {
  const navigate = useNavigate();
  const [checkMaster, setCheckMaster] = useState(null);
  const [authorizedUser, setAuthorizedUser] = useState(null);
  const authKey = localStorage.getItem("jwt");
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/admin/register-master"
    : "https://khamco-shop-backend.onrender.com/admin/register-master";
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [role, setRole] = useState("");
  const [language, setLanguage] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");

  useEffect(() => {
    document.title = "Master User Registration - Khamco Shop";
  }, []);

  const createMaster = async (e) => {
    e.preventDefault();
    console.log({
      name: firstName,
      surname: lastName,
      email: email,
      role: role,
      language: language,
      password: password,
      password2: confirmPassword,
    });
    await axios
      .post(
        apiUrl,
        {
          name: firstName,
          surname: lastName,
          email: email,
          role: role,
          language: language,
          password: password,
          password2: confirmPassword,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        toast(response.data.message);
        setAuthorizedUser(response.data.authorizedUser);
        setCheckMaster(response.data.checkMaster);
        navigate("/");
      })
      .catch((error) => {
        toast.error(error.response?.data?.message || error.message);
        console.error(error);
      });
  };

  return (
    <>
      <Navbar />
      <main className="p-16">
        <section>
          <h1 className="text-center text-[2rem] font-minimoBold">
            New Master Admin
          </h1>
          <form
            className="flex flex-col justify-center items-center w-[90vw]"
            onSubmit={createMaster}
          >
            <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
              <p className="text-[red] animate-pulse">
                There can only be one <strong>Master Administrator</strong>. A
                user with these privileges has access to all resources.{" "}
                <i className="fas fa-user-plus"></i>
              </p>
            </div>
            <UserForm
              page="create-master"
              checkMaster={checkMaster}
              authorizedUser={authorizedUser}
              setFirstName={setFirstName}
              setLastName={setLastName}
              setRole={setRole}
              setLanguage={setLanguage}
              setEmail={setEmail}
              setPassword={setPassword}
              setConfirmPassword={setConfirmPassword}
            />
          </form>
        </section>
      </main>
    </>
  );
};

export default CreateMaster;
