import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import { Link } from "react-router-dom";
import Footer from "../../components/Footer";
import axios from "axios";

const AdminDashboard = () => {
  const serverUrl = "http://localhost:5000/admin/dashboard";
  const [availableFabric, setAvailableFabric] = useState(null);
  const [averageServiceTime, setAverageServiceTime] = useState(null);
  const [customers, setCustomers] = useState([{}]);
  const [invoiceData, setInvoiceData] = useState([]);
  const [message, setMessage] = useState("Hello World");
  const [totalRevenue, setTotalRevenue] = useState(null);
  useEffect(() => {
    const fetchData = async () => {
      await axios
        .get(serverUrl)
        .then((response) => {
          console.log(response.data);
          setAvailableFabric(response.data.available_fabric);
          setAverageServiceTime(response.data.average_service_time);
          setCustomers(response.data.customers);
          setInvoiceData(response.data.invoice_data);
          setMessage(response.data.message);
          setTotalRevenue(response.data.total_revenue);
        })
        .catch((error) => console.error(error));
    };

    fetchData();
  }, []);
  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <AdminPanel />
