import React, { useEffect, useRef, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import Footer from "../../components/Footer";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import { Chart, registerables } from "chart.js";
import { apiUrl, authKey } from "../../utilities";
Chart.register(...registerables);

const CustomersData = () => {
  const [militaryCustomers, setMilitaryCustomers] = useState([]);
  const [civilianCustomers, setCivilianCustomers] = useState([]);
  const [averageWaist, setAverageWaist] = useState(0);
  const [averageShoulder, setAverageShoulders] = useState(0);
  const [popularAreas, setPopularAreas] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [invoices, setInvoices] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState("Geography Report");
  const chartRef = useRef(null);
  const measurementsChartRef = useRef(null);
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    let isMounted = true;

    document.title = "Admin Customer Data - Khamco Shop";

    const fetchCustomerData = async () => {
      await axios
        .get(`${apiUrl}/admin/customers_data`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          if (isMounted) {
            setCustomers(response.data.customers);
            setMilitaryCustomers(response.data.militaryCustomers);
            setCivilianCustomers(response.data.civilianCustomers);
            setInvoices(response.data.invoices);
            setPopularAreas(response.data.popularAreas);
            setAverageWaist(response.data.averageWaist);
            setAverageShoulders(response.data.shoulderAverage);
            setIsLoading(false);
          }
        })
        .catch((error) => {
          if (isMounted) {
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error("There was a problem with the fetch operation.");
            }
            return;
          }
        });
    };

    fetchCustomerData();

    return () => {
      isMounted = false;
    };
  }, [navigate]);

  useEffect(() => {
    const ctx = chartRef.current.getContext("2d");
    const ctx2 = measurementsChartRef.current.getContext("2d");

    // Flatten the clothing options for customers and their dependents
    const clothingOptions = customers
      ? customers.flatMap((customer) => {
          // Extract clothing options for the customer
          const customerClothingOptions = customer.measurements.flatMap(
            (measurement) =>
              measurement.clothing_options.map((option) => option.clothing_item)
          );

          // Extract clothing options for the dependents
          const dependentsClothingOptions = customer.dependents.flatMap(
            (dependent) =>
              dependent.measurements.flatMap((measurement) =>
                measurement.clothing_options.map(
                  (option) => option.clothing_item
                )
              )
          );

          return [...customerClothingOptions, ...dependentsClothingOptions];
        })
      : [];

    // Count the frequency of each clothing item
    const clothingFrequency = clothingOptions.reduce((acc, item) => {
      acc[item] = (acc[item] || 0) + 1;
      return acc;
    }, {});

    // Prepare data for the chart
    const chartData = {
      labels: Object.keys(clothingFrequency),
      datasets: [
        {
          label: "Clothing Items",
          data: Object.values(clothingFrequency),
          backgroundColor: "#283688",
          borderColor: "#EC1664",
          borderWidth: 1,
        },
      ],
    };

    // Extract measurements for the scatter plot
    const scatterData = customers.map((customer) => ({
      x: customer?.measurements[0]?.chest || 0,
      y: customer?.measurements[0]?.collar || 0,
      name: customer.name,
      surname: customer.surname,
    }));

    const measurementChartData = {
      datasets: [
        {
          label: "Customer Measurements",
          data: scatterData,
          backgroundColor: "#283688",
          borderColor: "#EC1664",
          borderWidth: 1,
          pointRadius: 8, // Increase the point size
          pointHoverRadius: 12,
        },
      ],
    };

    const chart = new Chart(ctx, {
      type: "bar",
      data: chartData,
      options: {
        scales: {
          y: {
            title: {
              display: true,
              text: "Frequency",
              color: "black",
              font: {
                size: 16,
              },
            },
            grid: {
              color: "#EC1664",
            },
            ticks: {
              color: "blue",
              font: {
                size: 14,
              },
            },
          },
          x: {
            title: {
              display: true,
              text: "Clothing Items",
              color: "black",
              font: {
                size: 16,
              },
            },
            grid: {
              color: "#EC1664",
            },
            ticks: {
              color: "blue",
              font: {
                size: 14,
              },
            },
          },
        },
        plugins: {
          legend: {
            display: true,
            labels: {
              color: "black",
              font: {
                size: 20,
              },
            },
          },
        },
      },
    });

    const measurementsChart = new Chart(ctx2, {
      type: "scatter",
      data: measurementChartData,
      options: {
        scales: {
          x: {
            title: {
              display: true,
              text: "Chest",
              color: "black",
              font: {
                size: 16,
              },
            },
            grid: {
              color: "#EC1664",
            },
            ticks: {
              color: "blue",
              font: {
                size: 14,
              },
            },
          },
          y: {
            title: {
              display: true,
              text: "Collar",
              color: "black",
              font: {
                size: 16,
              },
            },
            grid: {
              color: "#EC1664",
            },
            ticks: {
              color: "blue",
              font: {
                size: 14,
              },
            },
          },
        },
        plugins: {
          tooltip: {
            callbacks: {
              // Custom tooltip to display the customer name
              label: (context) => {
                const { x, y } = context.raw;
                const customerName = context.raw.name;
                const customerSurname = context.raw.surname;
                return `${customerName} ${customerSurname}: Chest - ${x}, Collar - ${y}`;
              },
            },
            bodyFont: {
              size: 18, // Increase tooltip font size
            },
            titleFont: {
              size: 20, // Increase tooltip title font size
            },
          },
          legend: {
            display: true,
            labels: {
              color: "#ed1965",
              font: {
                size: 20,
              },
            },
          },
        },
      },
    });

    return () => {
      chart.destroy();
      measurementsChart.destroy();
    };
  }, [customers]);

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <AdminPanel />
        {isLoading && (
          <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
            <div className="shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
            <div className="shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
            <div className="shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
            <div className="shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem] bg-[#ccc] shadow-none animate-pulse"></div>
          </div>
        )}
        <section className="admin-dashboard font-arial">
          <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
            <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
              {militaryCustomers && (
                <>
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    Military Customers
                  </h2>
                  <h2 className="text-xl font-semibold text-green-500">
                    {militaryCustomers.length}
                  </h2>
                </>
              )}
            </div>
            <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
              {civilianCustomers && (
                <>
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    Civilian Customers
                  </h2>
                  <h2 className="text-xl font-semibold text-green-500">
                    {civilianCustomers.length}
                  </h2>
                </>
              )}
            </div>
            <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
              {averageWaist && (
                <>
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    Average Waist Size
                  </h2>
                  <p>Inches</p>
                  <h2 className="text-xl font-semibold text-green-500">
                    {averageWaist.toFixed(2)}
                  </h2>
                </>
              )}
            </div>
            <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
              {averageShoulder && (
                <>
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    Average Shoulder Size
                  </h2>
                  <p>Inches</p>
                  <h2 className="text-xl font-semibold text-green-500">
                    {averageShoulder.toFixed(2)}
                  </h2>
                </>
              )}
            </div>
            <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
              {popularAreas[0] && (
                <>
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    Popular Governorates
                  </h2>
                  <p>{popularAreas[0].total} customers</p>
                  <h2 className="text-xl font-semibold text-green-500">
                    {popularAreas[0]._id}
                  </h2>
                </>
              )}
            </div>
            <div className="text-center shadow-lg shadow-[gray] p-8 rounded-2xl flex flex-col gap-4 min-h-[10rem]">
              {popularAreas[0] && (
                <>
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    Popular Areas
                  </h2>
                  <p>{popularAreas[0].areas[0].count} customers</p>
                  <h2 className="text-xl font-semibold text-green-500">
                    {popularAreas[0].areas[0].area}
                  </h2>
                </>
              )}
            </div>
          </div>
          <div className="w-full md:w-2/5 flex justify-between mb-8 mt-[4rem]">
            <select
              className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
              onChange={(e) => setSelectedIndex(e.target.value)}
            >
              <option disabled>-- Select a report --</option>
              <option value="Geography Report">Geography Report</option>
              <option value="Customer Report">Customer Report</option>
              <option value="Invoice Report">Invoice Report</option>
              <option value="Clothing Report">Clothing Report</option>
              <option value="Measurements Chart">Measurements Chart</option>
            </select>
          </div>
          {selectedIndex === "Geography Report" && (
            <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
              <caption className="text-khamco-primary font-bold mb-4">
                Geography Report
              </caption>
              <thead className="text-khamco-primary font-bold">
                <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                  <td className="text-center p-2 bg-[#ddd]">Governorate</td>
                  <td className="text-center p-2 bg-[#ddd]">Country</td>
                  <td className="text-center p-2 bg-[#ddd]">Total Customers</td>
                </tr>
              </thead>
              <tbody>
                {popularAreas &&
                  popularAreas.map((area, index) => (
                    <tr
                      className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                      key={index}
                    >
                      <td className="text-center p-2">{area._id}</td>
                      <td className="text-center p-2">Kuwait</td>
                      <td className="text-center p-2">
                        {area.total} customer(s)
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          )}
          {selectedIndex === "Customer Report" && (
            <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
              <caption className="text-khamco-primary font-bold mb-4">
                Customer Data
              </caption>
              <thead className="text-khamco-primary font-bold">
                <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                  <td className="text-center p-2 bg-[#ddd]">Customer</td>
                  <td className="text-center p-2 bg-[#ddd]">Military Status</td>
                  <td className="text-center p-2 bg-[#ddd]">Creation Date</td>
                  <td className="text-center p-2 bg-[#ddd]">Membership</td>
                  <td className="text-center p-2 bg-[#ddd]">
                    Total Dependents
                  </td>
                  <td className="text-center p-2 bg-[#ddd]">Total Spent</td>
                </tr>
              </thead>
              <tbody>
                {customers &&
                  customers.map((customer, index) => (
                    <tr
                      className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                      key={index}
                    >
                      <td className="text-center p-2">
                        <Link
                          to={`/customers/${customer._id}`}
                          target="_blank"
                          className="text-[blue] hover:underline"
                        >
                          {customer.name} {customer.surname}{" "}
                          <i className="fas fa-external-link"></i>
                        </Link>
                      </td>
                      <td className="text-center p-2 capitalize">
                        {customer.military_status}
                      </td>
                      <td className="text-center p-2">
                        {new Date(customer.date_created).toLocaleDateString(
                          "en-US",
                          {
                            month: "long",
                            year: "numeric",
                            day: "numeric",
                          }
                        )}
                      </td>
                      {customer.membership_status === "VIP" ? (
                        <td className="text-center p-2">
                          {customer.membership_status}{" "}
                          <i className="fas fa-star text-[gold] animate-pulse"></i>
                        </td>
                      ) : (
                        <td className="text-center p-2">
                          {customer.membership_status}
                        </td>
                      )}
                      <td className="text-center p-2">
                        {customer.dependents.length}
                      </td>
                      <td className="text-center p-2">
                        KWD{" "}
                        {new Intl.NumberFormat("en-US").format(
                          customer.amount_spent
                        )}
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          )}
          {selectedIndex === "Invoice Report" && (
            <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
              <caption className="text-khamco-primary font-bold mb-4">
                Recent Activity
              </caption>
              <thead className="text-khamco-primary font-bold">
                <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                  <td className="text-center p-2 bg-[#ddd]">Invoice ID</td>
                  <td className="text-center p-2 bg-[#ddd]">Customer</td>
                  <td className="text-center p-2 bg-[#ddd]">Date</td>
                  <td className="text-center p-2 bg-[#ddd]">Contact</td>
                  <td className="text-center p-2 bg-[#ddd]">Payment Status</td>
                  <td className="text-center p-2 bg-[#ddd]">Amount Paid</td>
                </tr>
              </thead>
              <tbody>
                {invoices &&
                  invoices.map((invoice, index) => (
                    <tr
                      className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                      key={index}
                    >
                      <td className="text-center p-2">
                        <Link
                          to={`/invoices/${invoice._id}`}
                          target="_blank"
                          rel="noreferrer"
                          className="text-[blue] hover:underline"
                        >
                          {invoice.uniqueId}{" "}
                          <i className="fas fa-external-link"></i>
                        </Link>
                      </td>
                      <td className="text-center p-2">
                        {invoice.customer_name} {invoice.customer_surname}
                      </td>
                      <td className="text-center p-2">
                        {new Date(invoice.date).toLocaleDateString("en-US", {
                          month: "long",
                          day: "numeric",
                          year: "numeric",
                        })}
                      </td>
                      <td className="text-center p-2">
                        <a href={`tel:${invoice.customer_phone}`}>
                          {invoice.customer_phone}
                        </a>
                      </td>
                      <td
                        className={`text-center p-2 ${
                          invoice.payment_status === "Paid"
                            ? "text-green-500"
                            : invoice.payment_status === "Partially Paid"
                            ? "text-orange-500"
                            : "text-red-500"
                        }`}
                      >
                        {invoice.payment_status}
                      </td>
                      <td className="text-center p-2">
                        KWD {invoice.amount_paid}
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          )}
          <div
            className={`${
              selectedIndex === "Clothing Report" ? "block" : "hidden"
            }`}
          >
            <canvas
              id="customerChart"
              width="400"
              height="150"
              ref={chartRef}
            ></canvas>
          </div>
          <div
            className={`${
              selectedIndex === "Measurements Chart" ? "block" : "hidden"
            }`}
          >
            <canvas
              id="measurementChart"
              width="400"
              height="150"
              ref={measurementsChartRef}
            ></canvas>
            <table className="w-full my-8 border-collapse text-[1.2rem] overflow-auto">
              <caption className="text-khamco-primary font-bold mb-4">
                Data Table
              </caption>
              <thead className="text-khamco-primary font-bold">
                <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                  <th className="text-center p-2 bg-[#ddd]">Customer ID No</th>
                  <th className="text-center p-2 bg-[#ddd]">Customer Name</th>
                  <th className="text-center p-2 bg-[#ddd]">Collar Diameter</th>
                  <th className="text-center p-2 bg-[#ddd]">Chest</th>
                  <th className="text-center p-2 bg-[#ddd]">
                    Dishdasha Height
                  </th>
                  <th className="text-center p-2 bg-[#ddd]">Belly</th>
                </tr>
              </thead>
              <tbody>
                {customers &&
                  customers.map((customer, index) => (
                    <tr
                      key={index}
                      className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                    >
                      <td className="text-center p-2">{customer.uniqueId}</td>
                      <td className="text-center p-2">{`${customer.name} ${customer.surname}`}</td>
                      <td className="text-center p-2">
                        {customer?.measurements[0]?.collar || "--"}
                      </td>
                      <td className="text-center p-2">
                        {customer?.measurements[0]?.chest || "--"}
                      </td>
                      <td className="text-center p-2">
                        {customer?.measurements[0]?.dishdasha_front_height ||
                          "--"}
                      </td>
                      <td className="text-center p-2">
                        {customer?.measurements[0]?.belly || "--"}
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </div>
        </section>
      </main>
      <Footer />
      <script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
    </>
  );
};

export default CustomersData;
