import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import { Link, useLocation } from "react-router-dom";
import Footer from "../../components/Footer";
import axios from "axios";
import { Buffer } from "buffer";

const Products = () => {
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [message, setMessage] = useState("");
  const [products, setProducts] = useState([]);
  const serverUrl = "http://localhost:5000";
  const [productName, setProductName] = useState("");
  const authKey = localStorage.getItem("jwt");
  const [isLoading, setIsLoading] = useState(true);
  const location = useLocation();
  const navigate = useNavigate();

  const getProducts = async () => {
    await axios
      .get(`${serverUrl}/products`, {
        headers: { Authorization: `Bearer ${authKey}` },
      })
      .then((response) => {
        setIsLoading(false);
        if (response.data.products) {
          setProducts(response.data.products);
        } else {
          setProducts([]);
        }
      })
      .catch((error) => {
        setMessage(error.message);
        setProducts([]);
        setIsLoading(false);
      });
  };

  useEffect(() => {
    document.title = "Products Khamco Shop";
    getProducts();
  }, [location.search]);

  const filteredProducts = products.filter((product) =>
    product.name.toLowerCase().includes(productName.toLowerCase())
  );

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          id="inventory"
          className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]"
          dir={writingDirection}
        >
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
            <Link to="/products/new">
              Add New Product <i className="fas fa-cart-plus"></i>
            </Link>
            <p className="message">{message}</p>
          </div>
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
            <form action="/products" method="GET" className="search-engine">
              <input
                type="text"
                placeholder="Search product name"
                name="name"
                className="search-input"
                onChange={(e) => setProductName(e.target.value)}
              />
              <button type="submit" className="search-button">
                <i className="fas fa-search"></i>
              </button>
            </form>
            <form
              action="/products"
              method="GET"
              className="search-engine"
              id="date-form"
            >
              <input
                type="date"
                name="date"
                id="date-search"
                className="search-input"
              />
              <button type="submit" className="search-button">
                <i className="fas fa-check"></i>
              </button>
            </form>
          </div>
          <h1 className="title">All Products</h1>
          {isLoading &&
            new Array(3).fill(
              <div
                className="w-full bg-[#ccc] animate-pulse py-[6rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-evenly items-center gap-[2rem]"
                dir={writingDirection}
              ></div>
            )}
          {filteredProducts.length > 0 &&
            filteredProducts.map((product, index) => (
              <div
                className="flex items-center w-full bg-[#bebebe80] p-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] justify-evenly gap-[2rem] my-[2rem]"
                dir="auto"
                key={index}
              >
                <img
                  src={`data:${product.photoType};base64, ${Buffer.from(
                    product.picture.data
                  ).toString("base64")}`}
                  alt={product.name}
                  loading="lazy"
                  className="w-[15vw] h-[30vh]"
                />
                <p className="capitalize">
                  <strong>Name</strong>: {product.name}
                </p>
                <p className="capitalize">
                  <strong>Category</strong>: {product.category}
                </p>
                <Link
                  to={`/products/${product._id}`}
                  className="edit-btn transform-none"
                >
                  View
                </Link>
              </div>
            ))}

          {filteredProducts.length <= 0 && !isLoading && (
            <p className="text-center">No product data.</p>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Products;
