import React, { useEffect, useState } from "react";
import Navbar from "../../../components/Navbar";
import { useStoreState } from "easy-peasy";
import { Link, useNavigate, useParams } from "react-router-dom";
import { toast } from "react-toastify";
import axios from "axios";
import MeasurementList from "./MeasurementList";

const MeasurementDetails = () => {
  const { id } = useParams();
  const { measurementId } = useParams();
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [customer, setCustomer] = useState(null);
  const [measurement, setMeasurement] = useState(null);
  const [orders, setOrders] = useState(null);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/customers/${id}/measurements/${measurementId}`
    : `https://khamco-shop-backend.onrender.com/customers/${id}/measurements/${measurementId}`;
  const authKey = localStorage.getItem("jwt");
  const [ordersToggle, setOrdersToggle] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      await axios
        .get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setCustomer(response.data.customer);
          setMeasurement(response.data.measurement);
          setOrders(response.data.orders);
        })
        .catch((error) => {
          toast.error(error.response?.data?.message || error.message);
          setCustomer(null);
          setMeasurement(null);
        });
    };

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      } else {
        setWritingDirection("ltr");
      }
    };
    determineWritingDirection();
    return () => {
      fetchData();
    };
  }, [apiUrl, authKey, userLanguage]);

  const handleDelete = async (e) => {
    e.preventDefault();
    await axios
      .delete(
        apiUrl,
        {
          personId: customer._id,
          measurementId: measurement._id,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then(() => {
        navigate("/customers");
      })
      .catch((error) => {
        toast.error(error.response?.data?.message || error.message);
      });
  };

  return (
    <>
      <Navbar />
      <main className="p-16">
        <section className="customer-details" dir={writingDirection}>
          {customer && measurement && (
            <>
              <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                <Link
                  to={`/customers/${customer._id}/measurements`}
                  className="text-[blue] no-underline hover:underline cursor-pointer"
                >
                  <i className="fas fa-arrow-left"></i>{" "}
                  {`${customer.name} ${customer.surname}`}'s measurements
                </Link>
              </div>
              <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
                <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                  <Link
                    to={`/customers/edit/${customer._id}/measurements/${measurement._id}`}
                    className="py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer bg-[blue] hover:opacity-90"
                  >
                    Edit
                  </Link>
                  <form onSubmit={handleDelete}>
                    <button
                      type="submit"
                      className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer"
                    >
                      Delete
                    </button>
                  </form>
                  <button
                    className="bg-[#666] py-[0.5rem] px-[1rem] outline-none border-none text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                    onClick={() => window.print()}
                  >
                    Print <i className="fas fa-print"></i>
                  </button>
                </div>
                <div className="flex flex-col gap-[1rem]">
                  <h2 className="text-center text-[2rem] font-minimoBold">
                    {`Measurements of ${customer.name} ${customer.surname}`} for{" "}
                    {new Date(measurement.date_created).toLocaleDateString(
                      "en-us",
                      {
                        month: "long",
                        year: "numeric",
                        day: "numeric",
                      }
                    )}
                  </h2>
                  <MeasurementList measurement={measurement} />
                </div>
                <div
                  className={`flex flex-col gap-[1rem] ${
                    ordersToggle ? "flex" : "hidden"
                  }`}
                >
                  <div className="flex flex-col gap-[1rem]">
                    <h2 className="text-center text-[2rem] font-minimoBold">
                      Orders
                    </h2>
                  </div>
                  {orders &&
                    orders.map(
                      (order, index) => (
                        (
                          <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                            <strong>Order</strong>
                            <p>{order.order_name}</p>
                          </span>
                        ),
                        (
                          <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                            <strong>Measurer</strong>
                            <p>{order.measurer}</p>
                          </span>
                        ),
                        (
                          <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                            <strong>Cutter</strong>
                            <p>{order.cutter}</p>
                          </span>
                        ),
                        (
                          <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                            <strong>Sewer</strong>
                            <p>{order.sewer}</p>
                          </span>
                        ),
                        (
                          <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                            <strong>Order Status</strong>
                            <p>{order.order_status}</p>
                          </span>
                        ),
                        (
                          <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                            <strong>Order Start Date</strong>
                            <p>
                              {new Date(
                                order.order_start_time
                              ).toLocaleDateString("en-us", {
                                month: "long",
                                year: "numeric",
                                day: "numeric",
                              })}
                            </p>
                          </span>
                        ),
                        (
                          <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                            <strong>Order End Date</strong>
                            <p>
                              {new Date(
                                order.order_end_time
                              ).toLocaleDateString("en-us", {
                                month: "long",
                                year: "numeric",
                                day: "numeric",
                              })}
                            </p>
                          </span>
                        ),
                        (
                          <span
                            className="inline-flex justify-start gap-[2rem] text-balance flex-wrap"
                            style={{
                              borderBottom: "0.1rem solid gray",
                              paddingBottom: "1rem",
                            }}
                          >
                            <strong>Order Duration</strong>
                            {order.order_end_time ? (
                              `${order.order_duration.years} years, ${order.order_duration.months} months, ${order.order_duration.weeks} weeks, ${order.order_duration.days} days, ${order.order_duration.hours} hours`
                            ) : (
                              <p className="text-[red]">
                                Order is still open. Close the order to
                                calculate duration.
                              </p>
                            )}
                          </span>
                        )
                      )
                    )}
                </div>
                <div className="inline">
                  <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                    <label
                      for="measurements-toggle"
                      className="cursor-pointer underline hover:text-khamco-secondary"
                    >
                      {ordersToggle ? "Hide Orders" : "Show Orders"}
                    </label>
                    <input
                      id="measurements-toggle"
                      type="checkbox"
                      onChange={() => setOrdersToggle(!ordersToggle)}
                      className="hidden"
                    />
                  </span>
                </div>
              </div>
            </>
          )}
        </section>
      </main>
    </>
  );
};

export default MeasurementDetails;
