import React, { useEffect, useState } from "react";
import Ribbon from "../components/Ribbon";
import { Link } from "react-router-dom";
import axios from "axios";

const Login = () => {
  const serverUrl = "http://localhost:5000";
  const [message, setMessage] = useState(
    "Login using your email and password."
  );
  const [checkMaster, setCheckMaster] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [passwordType, setPasswordType] = useState(false);
  const [isPending, setIsPending] = useState(false);
  const jwt = localStorage.getItem("jwt");

  useEffect(() => {
    // if (jwt) {
    //   window.location.replace("/");
    // }

    document.title = "Log in to Khamco Shop";

    const getLogin = async () => {
      await axios
        .get(`${serverUrl}/login`)
        .then((response) => {
          setMessage(response.data.message);
          setCheckMaster(response.data.checkMaster);
        })
        .catch((error) => {
          setMessage(error.data.message);
          console.error(error);
        });
    };

    getLogin();
  }, [jwt]);

  const handleLogin = async (event) => {
    setIsPending(true);
    event.preventDefault();
    if (!email || !password) {
      setMessage("Please enter the required fields");
      setIsPending(false);
      return;
    }
    await axios
      .post(`${serverUrl}/login`, {
        email: email,
        password: password,
      })
      .then((response) => {
        setMessage(response.data.message);
        setIsPending(false);
        if (response.status === 200) {
          localStorage.setItem("jwt", response.data.jwt);
        }
      })
      .catch((error) => {
        console.error(error);
        setMessage("Login error");
        setIsPending(false);
      });
  };

  return (
    <>
      <Ribbon />
      <main>
        <section className="p-[4rem] grid justify-center items-center">
          <h1 className="title">User Login</h1>
          <form
            action="/login"
            method="POST"
            className="flex flex-col justify-center items-center w-[90vw]"
            onSubmit={(e) => handleLogin(e)}
          >
            <div className="form-row">
              <p className="message">{message}</p>
            </div>
            {checkMaster && (
              <div className="form-row">
                <Link href="/users/create-master" className="alert flash">
                  Click here to register a new{" "}
                  <strong>Master Administrator</strong>. A user with these
                  privileges has access to all resources.{" "}
                  <i className="fas fa-user-plus"></i>
                </Link>
              </div>
            )}
            <div className="form-row">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                placeholder="Enter your email address"
                className="border focus:border-khamco-secondary"
                onChange={(e) => setEmail(e.target.value)}
              ></input>
            </div>
            <div className="form-row">
              <label htmlFor="accept">Password</label>
              <input
                type={passwordType ? "text" : "password"}
                name="password"
                id="password"
                placeholder="Enter your password"
                className="border focus:border-khamco-secondary"
                onChange={(e) => setPassword(e.target.value)}
              />
              <button
                type="button"
                className="show-hide"
                onClick={() => setPasswordType(!passwordType)}
              >
                <i
                  className={`fas ${passwordType ? "fa-eye-slash" : "fa-eye"}`}
                ></i>
              </button>
            </div>
            <div className="flex justify-center items-center my-[2rem] mx-auto flex-wrap w-full">
              <Link to="/users/forgot-password">
                Forgotten your password? Reset it now.
              </Link>
            </div>
            <div className="form-row">
              <button type="submit" disabled={isPending ? true : false}>
                {isPending ? (
                  <i className="fas fa-rotate animate-spin"></i>
                ) : (
                  "Submit"
                )}
              </button>
            </div>
          </form>
        </section>
      </main>
    </>
  );
};

export default Login;
