import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import { Link, useNavigate, useParams } from "react-router-dom";
import Footer from "../../components/Footer";
import axios from "axios";
import { toast } from "react-toastify";
import { useStoreState } from "easy-peasy";

const OrderDetails = () => {
  const { id } = useParams();
  const [writingDirection, setWritingDirection] = useState("ltr");
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [openDialog, setOpenDialog] = useState(false);
  const [order, setOrder] = useState({});
  const [customer, setCustomer] = useState({});
  const [users, setUsers] = useState([]);
  const [measurer, setMeasurer] = useState("");
  const [cutter, setCutter] = useState("");
  const [sewer, setSewer] = useState("");
  const [loading, setLoading] = useState(true);
  const [customerName, setCustomerName] = useState("");
  const [customerSurname, setCustomerSurname] = useState("");
  const [customerEmail, setCustomerEmail] = useState("");
  const [customerPhone, setCustomerPhone] = useState("");
  const [orderNotes, setOrderNotes] = useState("");
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000`
    : `https://khamco-shop-backend.onrender.com`;
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();

  useEffect(() => {
    let isMounted = true;

    document.title = "Order Details - Khamco Shop";

    const fetchOrder = async () => {
      try {
        const response = await axios.get(`${apiUrl}/orders/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        });

        if (isMounted) {
          setLoading(false);
          setOrder(response.data.order);
          setCustomer(response.data.customer);
          setCustomerName(response.data.order.customer_name);
          setUsers(response.data.users);
        }
      } catch (error) {
        if (isMounted) {
          setLoading(true);
          setOrder(null);
          setCustomer(null);
          setUsers(null);
          toast.error(error.response?.data?.message || error.message);
        }
      }
    };

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();

    fetchOrder();

    return () => {
      isMounted = false;
    };
  }, [apiUrl, authKey, id, userLanguage]);

  const deleteEntireOrder = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.delete(`${apiUrl}/orders/${order._id}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      });
      toast.success(response.data.message);
      navigate("/orders");
    } catch (error) {
      toast.error(error.response?.data?.message || error.message);
    }
  };

  const changeOrderStatus = async (e, status) => {
    e.preventDefault();
    await axios
      .post(
        `${apiUrl}/orders/${order._id}/${status}`,
        { id: order._id },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        toast.success(response.data.message);
        setOrder(response.data.order);
      })
      .catch((error) => {
        toast.error(error.response?.data?.message || error.message);
        console.log(error);
      });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    if (!customerName) {
      setLoading(false);
      toast.error("Customer name is required");
      return;
    }

    await axios
      .put(
        `${apiUrl}/orders/${order._id}`,
        {
          order_start_time: new Date(),
          customer_name: customerName,
          customer_surname: customerSurname,
          customer_email: customerEmail,
          customer_phone: customerPhone,
          measurer: measurer,
          cutter: cutter,
          sewer: sewer,
          order_notes: orderNotes,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        toast.success(response.data.message);
        window.location.replace(`/orders/${response.data.order._id}`);
      })
      .catch((error) => {
        console.error(error);
        toast.error(error.response?.data?.message || error.message);
        setLoading(false);
      });
  };

  return (
    <>
      <Navbar />
      <main>
        <section className="customer-details" dir={writingDirection}>
          <div className="utilities">
            <Link to="/orders">
              <i className="fas fa-arrow-left"></i> All Orders
            </Link>
          </div>
          {loading && <h1 className="title">Loading...</h1>}
          {order && customer && users && (
            <div className="khamco-card">
              <div className="utilities">
                <button
                  className="edit-btn"
                  id="openDialog"
                  onClick={() => setOpenDialog(true)}
                >
                  Edit
                </button>
                <button className="print-btn" onClick={() => window.print()}>
                  Print <i className="fas fa-print"></i>
                </button>
                <form onSubmit={deleteEntireOrder}>
                  <button type="submit" className="delete-btn">
                    Delete <i className="fas fa-times"></i>
                  </button>
                </form>
                <form onSubmit={(e) => changeOrderStatus(e, "in_progress")}>
                  <button
                    className={`py-[0.5rem] px-[1rem] outline-0 border-0 text-white rounded-[0.5rem] text-[1.2rem] cursor-pointer hover:bg-[goldenrod] ${
                      order.order_status === "In Progress"
                        ? "bg-[goldenrod]"
                        : "bg-[gray]"
                    }`}
                    type="submit"
                  >
                    In Progress
                  </button>
                </form>
                <form onSubmit={(e) => changeOrderStatus(e, "complete")}>
                  <button
                    type="submit"
                    className={`py-[0.5rem] px-[1rem] outline-0 border-0 text-white rounded-[0.5rem] text-[1.2rem] cursor-pointer hover:bg-[goldenrod] ${
                      order.order_status === "Complete"
                        ? "bg-[goldenrod]"
                        : "bg-[gray]"
                    }`}
                  >
                    Complete
                  </button>
                </form>
                <form onSubmit={(e) => changeOrderStatus(e, "awaiting")}>
                  <button
                    type="submit"
                    className={`py-[0.5rem] px-[1rem] outline-0 border-0 text-white rounded-[0.5rem] text-[1.2rem] cursor-pointer hover:bg-[goldenrod] ${
                      order.order_status === "Awaiting Delivery"
                        ? "bg-[goldenrod]"
                        : "bg-[gray]"
                    }`}
                  >
                    Awaiting Delivery
                  </button>
                </form>
                <form onSubmit={(e) => changeOrderStatus(e, "delivered")}>
                  <button
                    type="submit"
                    className={`py-[0.5rem] px-[1rem] outline-0 border-0 text-white rounded-[0.5rem] text-[1.2rem] cursor-pointer hover:bg-[goldenrod] ${
                      order.order_status === "Delivered"
                        ? "bg-[goldenrod]"
                        : "bg-[gray]"
                    }`}
                  >
                    {" "}
                    Delivered
                  </button>
                </form>
              </div>
              <h1 className="title">Order Details</h1>
              <div className="flex flex-col gap-[1rem]">
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Order Id:</strong>
                  <p>{order._id}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Customer Name:</strong>
                  <ul>
                    <li>
                      <Link to={`/customers/${customer._id}`} target="_blank">
                        {`${order.customer_name} ${order.customer_surname}`}
                      </Link>
                    </li>
                    {order.customers &&
                      order.customers.map((customer, index) => (
                        <li key={index}>{customer.customer_name}</li>
                      ))}
                  </ul>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Email:</strong>
                  <Link to={`mailto:${customer.email}`}>{customer.email}</Link>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Order Details:</strong>
                  <ul>
                    {order.name &&
                      order.name.map((order, index) => (
                        <li key={index}>{order.choice}</li>
                      ))}
                  </ul>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Order Status:</strong>
                  <p>{order.order_status}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Phone Number:</strong>
                  <Link to="tel:<%= customer.phone %>">{customer.phone}</Link>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Address:</strong>
                  <p>{customer.address}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Measurer:</strong>
                  <p>{order.measurer}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Cutter:</strong>
                  <p>{order.cutter}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Sewer:</strong>
                  <p>{order.sewer}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Other Details:</strong>
                  <p>{order.order_notes}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance">
                  <strong>Order Start Date:</strong>
                  <p>
                    {new Date(order.order_start_time).toLocaleDateString(
                      "en-us",
                      { month: "long", year: "numeric", day: "numeric" }
                    )}
                  </p>
                </span>
                {order.in_progress_duration && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>In Progress:</strong>
                    <p>
                      <strong>on</strong>{" "}
                      {new Date(order.in_progress_time).toLocaleDateString(
                        "en-us",
                        { month: "long", year: "numeric", day: "numeric" }
                      )}
                      <strong> after </strong>
                      {order.in_progress_duration.years} years,{" "}
                      {order.in_progress_duration.months} months,{" "}
                      {order.in_progress_duration.weeks} weeks,{" "}
                      {order.in_progress_duration.days} days,{" "}
                      {order.in_progress_duration.hours} hours,{" "}
                      {order.in_progress_duration.minutes} minutes,{" "}
                      {order.in_progress_duration.seconds} seconds.
                    </p>
                  </span>
                )}
                {order.delivered_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <p className="text-green-500">
                      Order has been delivered.{" "}
                      <i className="fas fa-thumbs-up"></i>
                    </p>
                  </span>
                )}
                {order.order_status === "Accepted" && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <p className="text-red-500 transform-none">
                      Order is not yet in progress.
                    </p>
                  </span>
                )}
                {order.completed_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Complete:</strong>{" "}
                    <p>
                      <strong>on </strong>
                      {new Date(order.completed_time).toLocaleDateString(
                        "en-us",
                        { month: "long", year: "numeric", day: "numeric" }
                      )}
                      <strong> after </strong> {order.completed_duration.years}{" "}
                      years, {order.completed_duration.months} months,{" "}
                      {order.completed_duration.weeks} weeks,{" "}
                      {order.completed_duration.days} days,{" "}
                      {order.completed_duration.hours} hours,{" "}
                      {order.completed_duration.minutes} minutes,{" "}
                      {order.completed_duration.seconds} seconds.
                    </p>
                  </span>
                )}

                {order.delivered_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <p className="text-green-500">
                      Order has been delivered.{" "}
                      <i className="fas fa-thumbs-up"></i>
                    </p>
                  </span>
                )}

                {!order.delivered_time && !order.completed_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <p className="text-red-500 transform:none">
                      Order is not yet complete.
                    </p>
                  </span>
                )}
                {order.awaiting_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Awaiting Delivery:</strong>{" "}
                    <p>
                      <strong>on </strong>
                      {new Date(order.awaiting_time).toLocaleDateString(
                        "en-us",
                        {
                          month: "long",
                          year: "numeric",
                          day: "numeric",
                        }
                      )}
                      <strong> after </strong> {order.awaiting_duration.years}{" "}
                      years, {order.awaiting_duration.months} months,{" "}
                      {order.awaiting_duration.weeks} weeks,{" "}
                      {order.awaiting_duration.days} days,{" "}
                      {order.awaiting_duration.hours} hours,{" "}
                      {order.awaiting_duration.minutes} minutes,{" "}
                      {order.awaiting_duration.second} seconds.
                    </p>
                  </span>
                )}

                {order.delivered_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <p className="text-green-500">
                      Order has been delivered.{" "}
                      <i className="fas fa-thumbs-up"></i>
                    </p>
                  </span>
                )}

                {!order.awaiting_time && !order.delivered_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <p className="text-red-500 transform:none">
                      Order is not yet ready for delivery.
                    </p>
                  </span>
                )}

                {order.delivered_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Delivered:</strong>{" "}
                    <p>
                      <strong>on </strong>
                      {new Date(order.delivered_time).toLocaleDateString(
                        "en-us",
                        { month: "long", year: "numeric", day: "numeric" }
                      )}
                      <strong> after </strong> {order.delivered_duration.years}{" "}
                      years, {order.delivered_duration.months} months,{" "}
                      {order.awaiting_duration.weeks} weeks,{" "}
                      {order.delivered_duration.days} days,{" "}
                      {order.delivered_duration.hours} hours,{" "}
                      {order.delivered_duration.minutes} minutes,{" "}
                      {order.delivered_duration.seconds} seconds.
                    </p>
                  </span>
                )}
                {!order.delivered_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <p className="text-red-500 transform-none">
                      Order has not been delivered.
                    </p>
                  </span>
                )}
                {order.order_end_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Order End Date:</strong>
                    <p>
                      {new Date(order.order_end_time).toLocaleDateString(
                        "en-us",
                        {
                          month: "long",
                          year: "numeric",
                          day: "numeric",
                        }
                      )}
                    </p>
                  </span>
                )}
                {order.order_end_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <strong>Order Duration:</strong>
                    <p>
                      {order.order_duration.years} years,{" "}
                      {order.order_duration.months} months,{" "}
                      {order.order_duration.weeks} weeks,{" "}
                      {order.order_duration.days} days,{" "}
                      {order.order_duration.hours} hours,{" "}
                      {order.order_duration.minutes} minutes,{" "}
                      {order.order_duration.seconds} seconds.
                    </p>
                  </span>
                )}
                {!order.order_end_time && (
                  <span className="inline-flex justify-start gap-[2rem] text-balance">
                    <p className="text-red-500 transform:none">
                      Order is still under <strong>{order.order_status}</strong>{" "}
                      status. The duration will be calculated after delivery.
                    </p>
                  </span>
                )}
              </div>
            </div>
          )}
        </section>
        <dialog
          className="fixed top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 w-2/4 rounded-[2rem] border-khamco-primary overflow-y-auto overflow-x-hidden max-h-[80vh] py-[2rem] px-0 fadeIn"
          open={openDialog}
        >
          <div className="p-[4rem] grid justify-center items-center">
            <form
              className="flex flex-col justify-center items-center w-[90vw]"
              onSubmit={handleSubmit}
            >
              <button
                className="delete-btn absolute top-[5%] left-[5%]"
                type="button"
                onClick={() => setOpenDialog(false)}
              >
                <i className="fas fa-times"></i>
              </button>
              <div className="form-row">
                <h2 className="title">Update Order Details</h2>
              </div>
              <div className="form-row">
                <label htmlFor="measurer">
                  Measurer {order && `(${order.measurer})`}
                </label>
                <select onChange={(e) => setMeasurer(e.target.value)}>
                  <option label="-- Select the Measurer--" value=""></option>
                  {users &&
                    users.map((user, index) => (
                      <option
                        key={index}
                        label={`${user.name} ${user.surname}`}
                        value={`${user.name} ${user.surname}`}
                      ></option>
                    ))}
                </select>
              </div>
              <div className="form-row">
                <label>Cutter {order && `(${order.cutter})`}</label>
                <select onChange={(e) => setCutter(e.target.value)}>
                  <option label="-- Select the Cutter --" value=""></option>
                  {users &&
                    users.map((user, index) => (
                      <option
                        key={index}
                        label={`${user.name} ${user.surname}`}
                        value={`${user.name} ${user.surname}`}
                      ></option>
                    ))}
                </select>
              </div>
              <div className="form-row">
                <label> Sewer {order && `(${order.sewer})`}</label>
                <select onChange={(e) => setSewer(e.target.value)}>
                  <option label="-- Select the Cutter--" value=""></option>
                  {users &&
                    users.map((user, index) => (
                      <option
                        key={index}
                        label={`${user.name} ${user.surname}`}
                        value={`${user.name} ${user.surname}`}
                      ></option>
                    ))}
                </select>
              </div>
              <div className="form-row">
                <label htmlFor="customer_name">
                  Customer Name ({order && order.customer_name})
                </label>
                <input
                  type="text"
                  placeholder="Customer Namer"
                  onChange={(e) => setCustomerName(e.target.value)}
                />
              </div>
              <div className="form-row">
                <label htmlFor="customer_surname">
                  Customer Surname ({order && order.customer_surname})
                </label>
                <input
                  type="text"
                  placeholder="Customer Surname"
                  onChange={(e) => setCustomerSurname(e.target.value)}
                />
              </div>
              <div className="form-row">
                <label htmlFor="customer_email">
                  Email ({order && order.customer_email})
                </label>
                <input
                  type="email"
                  placeholder="Customer Email"
                  onChange={(e) => setCustomerEmail(e.target.value)}
                />
              </div>
              <div className="form-row">
                <label htmlFor="customer_phone">
                  Phone Number ({order && order.customer_phone})
                </label>
                <input
                  type="tel"
                  placeholder="Customer Phone"
                  onChange={(e) => setCustomerPhone(e.target.value)}
                />
              </div>
              <div className="form-row">
                <label htmlFor="order_notes">Other Details</label>
                <textarea
                  value={order && order.order_notes}
                  onChange={(e) => setOrderNotes(e.target.value)}
                ></textarea>
              </div>
              <div className="form-row">
                <button type="submit">Update</button>
              </div>
            </form>
          </div>
        </dialog>
      </main>
      <Footer />
    </>
  );
};

export default OrderDetails;
