import React, { useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import { Link } from "react-router-dom";
import Footer from "../../components/Footer";

const AdminTailors = () => {
  const [message, setMessage] = useState("");
  const [users, setUsers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);

  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <section id="users-page" className="py-0 px-[2rem] grid justify-center items-center grid">
          <h1 className="title">All Tailors</h1>
          <div className="utilities">
            <a href="/admin/register-user">
              Add New Tailor <i className="fas fa-user-plus"></i>
            </a>
            <p className="message">{message}</p>
          </div>
          {isLoading && (
            <div className="flex-wrapper">
              <div className="metric bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="metric bg-[#ccc] shadow-none animate-pulse"></div>
              <div className="metric bg-[#ccc] shadow-none animate-pulse"></div>
            </div>
          )}
          {display && (
            <>
              {users &&
                users.map((user, index) => (
                  <div className="flex-wrapper" key={index}>
                    <p>
                      <strong>Name</strong>: {user.name}
                    </p>
                    <p>
                      <strong>Role</strong>: {user.role}
                    </p>
                    <p>
                      <strong>Email</strong>:{" "}
                      <a href={`mailto:${user.email}`}>{user.email}</a>
                    </p>
                    <Link to={`/admin/users/${user.id}`} className="edit-btn">
                      View
                    </Link>
                  </div>
                ))}
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminTailors;
