import { Link, useParams } from "react-router-dom";

const DependentsPrompt = () => {
  const { id } = useParams();
  return (
    <main>
      <dialog
        className="fixed top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 w-2/4 rounded-[2rem] border-khamco-primary overflow-y-auto overflow-x-hidden max-h-[80vh] py-[2rem] px-0 shadow-lg shadow-[#666] fadeIn"
        open={true}
      >
        <h2 className="title">Would you like to add a dependent?</h2>
        <div className="w-full flex justify-evenly flex-col items-center my-[1rem] gap-[2rem]">
          <Link to={`/customers/create-dependent/${id}`} className="flex flex-col items-center gap-[0.5rem] p-[2rem] rounded-[2rem] bg-[#0000ffcc] text-white text-[1.2rem] w-2/4">Yes</Link>
          <Link to={`/customers/${id}`} className="flex flex-col items-center gap-[0.5rem] p-[2rem] rounded-[2rem] bg-[#0000ffcc] text-white text-[1.2rem] w-2/4">No</Link>
        </div>
      </dialog>
    </main>
  );
};

export default DependentsPrompt;
