import { useEffect, useState } from "react";
import { toast } from "react-toastify";

const Selector = ({ label, price, type, quantity, setOptions }) => {
  const [isChecked, setIsChecked] = useState(false);

  useEffect(()=>{
  },[label, quantity])
  
  const handleCheck = () => {
    if(quantity <= 0){
      toast.warn(`${label} is finished`);
      setIsChecked(false);
      retur
    }
    if (isChecked) {
      setOptions((prevArray) =>
        prevArray.filter((item) => item.name !== label)
      );
    } else {
      setOptions((prevArray) => {
        const exists = prevArray.some((item) => item.name === label);
        if (!exists) {
          return [...prevArray, { name: label, price: price, quantity: 1, type }];
        }
        return prevArray;
      });
    }
  };

  return (
    <label
      className={`p-2 w-[10rem] md:w-[8rem] flex flex-col items-center gap-2 cursor-pointer rounded-xl border-2 border-khamco-primary text-center font-medium ${
        isChecked ? "bg-[#ccc]" : ""
      }`}
      onClick={handleCheck}
    >
      <p className="text-sm">
        {label} - ${price}
      </p>
      <input
        type="checkbox"
        checked={isChecked}
        onChange={() => {
          setIsChecked(!isChecked);
        }}
        className="accent-khamco-primary"
      />
    </label>
  );
};

export default Selector;
