import React, { useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import { Link } from "react-router-dom";
import Footer from "../../components/Footer";

const AdminTailors = () => {
  const [message, setMessage] = useState("");
  const [users, setUsers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);

  return (
    <>
      <Navbar />
      <main>
        <AdminPanel />
        <section id="users-page" class="khamco-section">
          <h1 class="title">All Tailors</h1>
          <div class="utilities">
            <a href="/admin/register-user">
              Add New Tailor <i class="fas fa-user-plus"></i>
            </a>
            <p class="message">{message}</p>
          </div>
          {<>
          {users &&
            users.map((user, index) => (
              <div class="flex-wrapper" key={index}>
                <p>
                  <strong>Name</strong>: {user.name}
                </p>
                <p>
                  <strong>Role</strong>: {user.role}
                </p>
                <p>
                  <strong>Email</strong>:{" "}
                  <a href={`mailto:${user.email}`}>{user.email}</a>
                </p>
                <Link to={`/admin/users/${user.id}`} class="edit-btn">
                  View
                </Link>
              </div>
            ))}
          </>}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminTailors;
