import { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import FormFields from "./Form_Fields";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { useStoreState } from "easy-peasy";

const CreateCustomer = () => {
  const [selectedGovernorate, setSelectedGovernorate] = useState("Al Aasima");
  const [defaultMilitary, setDefaultStatus] = useState("civilian");
  const [defaultMembership, setDefaultMembership] = useState("Basic");
  const [customerName, setCustomerName] = useState("");
  const [customerSurname, setCustomerSurname] = useState("");
  const [customerEmail, setCustomerEmail] = useState("");
  const [customerPhone, setCustomerPhone] = useState("");
  const [houseUnit, setHouseUnit] = useState("");
  const [governorate, setGovernorate] = useState("");
  const [area, setArea] = useState("");
  const [country, setCountry] = useState("");
  const [militaryNumber, setMilitaryNumber] = useState(false);
  const [militaryStatus, setMilitaryStatus] = useState(false);
  const [membershipStatus, setMembershipStatus] = useState(false);
  const [date, setDate] = useState(null);
  const [clothingOptions, setClothingOptions] = useState([]);
  const itemCount = useStoreState((state) => state.itemCount);
  const [areas, setAreas] = useState([
    "Abdulla Al-Salem",
    "Adailiya",
    "Bnaid Al-Qar",
    "Daʿiya",
    "Dasma",
    "Doha",
    "Doha Port",
    "Faiha'",
    "Failaka",
    "Granada",
    "Jaber Al-Ahmad City",
    "Jibla",
    "Kaifan",
    "Khaldiya",
    "Mansūriya",
    "Mirgab",
    "Nahdha",
    "North West Sulaibikhat",
    "Nuzha",
    "Qadsiya",
    "Qurtuba",
    "Rawda",
    "Shamiya",
    "Sharq",
    "Shuwaikh",
    "Shuwaikh Industrial Area",
    "Shuwaikh Port",
    "Sulaibikhat",
    "Surra",
    "Umm an Namil Island",
    "Yarmouk",
  ]);
  const apiUrl = window.location.origin.includes("localhost") ? "http://localhost:5000/customers" : "https://khamcotailor.com/customers";
  const authKey = localStorage.getItem("jwt");
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [message, setMessage] = useState("");
  const [customer, setCustomer] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    document.title = "New Customer | Khamco Shop";

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();
  }, [userLanguage]);

  const handleSubmit = async (event) => {
    event.preventDefault();

    await axios
      .post(apiUrl, {
        customer_name: customerName
      },{
        headers: { Authorization: `Bearer ${authKey}` },
      })
      .then((response) => {
        navigate(`/customers/dependents/dependents-prompt/${customer._id}`);
        setCustomer(response.data.customer);
      })
      .catch((error) => {
        setMessage(error.message);
      });
  };
  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section
          className="p-[4rem] grid justify-center items-center"
          dir={writingDirection}
        >
          <div className="row">
            <Link to="/customers">
              <i className="fas fa-arrow-left"></i> Customers
            </Link>
          </div>
          <h1 className="title">New Customer</h1>
          <p className="message">{message}</p>
          <form
            className="flex flex-col justify-center items-center w-[90vw]"
            onSubmit={(e) => handleSubmit(e.target)}
          >
            <FormFields selectedGovernorate={selectedGovernorate} setSelectedGovernorate={setSelectedGovernorate} defaultMilitary={defaultMilitary} setDefaultStatus={setDefaultStatus} defaultMembership={defaultMembership} setDefaultMembership={setDefaultMembership} customerName={customerName} setCustomerName={setCustomerName} customerSurname={customerSurname} setCustomerSurname={setCustomerSurname} customerEmail={customerEmail} setCustomerEmail={setCustomerEmail} customerPhone={customerPhone} setCustomerPhone={setCustomerPhone} houseUnit={houseUnit} setHouseUnit={setHouseUnit} governorate={governorate} setGovernorate={setGovernorate} area={area} setArea={setArea} country={country} setCountry={setCountry} militaryStatus={militaryStatus} setMilitaryStatus={setMilitaryStatus} militaryNumber={militaryNumber} setMilitaryNumber membershipStatus={membershipStatus} setMembershipStatus={setMembershipStatus} date={date} setDate={setDate} clothingOptions={clothingOptions} setClothingOptions={setClothingOptions} areas={areas} setAreas={setAreas} itemCount={itemCount} />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default CreateCustomer;
