import Navbar from "../../components/Navbar";
import { useEffect, useState } from "react";
import Footer from "../../components/Footer";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";
import AdminPanel from "../../components/AdminPanel";
import { apiUrl, authKey } from "../../utilities";

const AdminTailorDetails = () => {
  const { id } = useParams();
  const [user, setUser] = useState(null);
  const [fullName, setFullName] = useState(null);
  const [userOrders, setUserOrders] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [display, setDisplay] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState("Current Orders");
  const navigate = useNavigate();

  useEffect(() => {
    let isMounted = true;

    const getUserDetails = async () => {
      await axios
        .get(`${apiUrl}/admin/users/${id}`, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          if (isMounted) {
            document.title = `Admin Tailor Details - ${`${response.data.user.name} -`} Khamco Shop`;
            setUser(response.data.user);
            setFullName(
              `${response.data.user.name} ${response.data.user.surname}`
            );
            setUserOrders(response.data.user_orders);
            setIsLoading(false);
            setDisplay(true);
          }
        })
        .catch((error) => {
          if (isMounted) {
            setIsLoading(false);
            setDisplay(true);
            if (error.response.status === 403) {
              toast.warn("You are not authorized to access that page");
              navigate("/");
            } else {
              toast.error(error.response?.data?.message || error.message);
            }
          }
          return;
        });
    };

    getUserDetails();

    return () => {
      isMounted = false;
    };
  }, [id, navigate, user]);

  const renderImage = () => {
    if (user.picture && user.picture.data) {
      // Convert the binary data to base64
      const binary = new Uint8Array(user.picture.data).reduce(
        (acc, byte) => acc + String.fromCharCode(byte),
        ""
      );
      const base64String = btoa(binary);

      return (
        <img
          src={`data:${user.photoType};base64,${base64String}`}
          alt={user.name}
          loading="lazy"
          className="w-full md:w-2/4 h-[40vh] m-auto"
        />
      );
    } else {
      return (
        <img
          src="https://user-images.githubusercontent.com/11250/39013954-f5091c3a-43e6-11e8-9cac-37cf8e8c8e4e.jpg"
          alt="Generic user"
          className="w-full md:w-2/4 h-[40vh] m-auto bg-[#ccc]"
        />
      );
    }
  };

  function formatNumber(num) {
    return num % 1 === 0 ? num.toString() : num.toFixed(2);
  }

  return (
    <>
      <Navbar />
      <main className="p-[1rem] md:p-[4rem]">
        <AdminPanel />
        <section>
          {isLoading && (
            <>
              <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem]">
                <div className="flex justify-between gap-[2rem] items-center my-[2rem] mx-auto w-full flex-wrap">
                  {new Array(1).fill(null).map((_, index) => (
                    <Link
                      key={`placeholder-${index}`}
                      to={``}
                      className="py-[2rem] px-[4rem] outline-none border-none bg-[#666] rounded-[0.5rem] animate-pulse"
                    ></Link>
                  ))}
                  <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem] w-full bg-[#666] animate-pulse m-auto h-[100vh] my"></div>
                </div>
              </div>
            </>
          )}
          <div className="flex justify-end gap-8 items-center mx-auto my-8 w-full flex-wrap">
            <Link
              to="/admin/tailors"
              className="text-[blue] no-underline hover:underline cursor-pointer"
            >
              <i className="fas fa-arrow-left"></i> All Tailors
            </Link>
          </div>
          {display && user && (
            <>
              <div className="grid md:grid-cols-1 gap-[4rem] justify-center items-center w-[70vw] m-auto">
                <div className="grid gap-[2rem] justify-center items-center p-[2rem] rounded-[2rem] text-[1.2rem] w-full">
                  <div className="flex justify-between gap-8 items-center mx-auto my-8 w-full flex-wrap">
                    <Link
                      to={`/users/edit-user/${user._id}`}
                      className="py-[0.5rem] px-[1rem] outline-none border-none bg-[blue] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                    >
                      Edit
                    </Link>
                  </div>
                  {renderImage()}
                  <h1 className="text-center text-[2rem] font-minimoBold">
                    User Details {user.name}
                  </h1>
                  <div className="flex flex-col gap-[1rem]">
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="capitalize">ID</strong>
                      <p>{user._id}</p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="capitalize">Name</strong>
                      <p className="capitalize">{user.name}</p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="capitalize">Email</strong>
                      <p>{user.email}</p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="capitalize">Surname</strong>
                      <p className="capitalize">{user.surname}</p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="capitalize">Date Joined</strong>
                      <p className="capitalize">
                        {new Date(user.date).toLocaleDateString("en-us", {
                          month: "long",
                          year: "numeric",
                          day: "numeric",
                        })}
                      </p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="capitalize">Role:</strong>
                      <ul className="capitalize self-center">
                        {user.role.map((r, index) => (
                          <li className="list-disc" key={index}>
                            {r.type}
                          </li>
                        ))}
                      </ul>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong>Language</strong>
                      <p className="capitalize">
                        {user.language === "en" && "English"}
                        {user.language === "ur" && "Urdu"}
                        {user.language === "ar" && "Arabic"}
                        {user.language === "hi" && "Hindi"}
                      </p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="capitalize">Salary</strong>
                      <p className="capitalize">
                        {user.salary
                          ? new Intl.NumberFormat("kw-KW", {
                              style: "currency",
                              currency: "KWD",
                              minimumFractionDigits: 3,
                              maximumFractionDigits: 3,
                            }).format(user.salary)
                          : "--"}
                      </p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="capitalize">Measurements Taken</strong>
                      <p className="capitalize">{user.measurements || 0}</p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="capitalize">Orders Cut</strong>
                      <p className="capitalize">{user.cutting || 0}</p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="capitalize">QA/QC Orders</strong>
                      <p className="capitalize">{user.qa_qc || 0}</p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="capitalize">Orders Tailored</strong>
                      <p className="capitalize">{user.tailoring || 0}</p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="capitalize">Driving Orders</strong>
                      <p className="capitalize">{user.driving || 0}</p>
                    </span>
                    <span className="inline-flex justify-start gap-[2rem] text-balance">
                      <strong className="capitalize">
                        Number of Alterations
                      </strong>
                      <p className="capitalize">{user.alterations || 0}</p>
                    </span>
                  </div>
                </div>
              </div>
              <div className="w-full md:w-2/5 flex justify-between mb-8 mt-[4rem]">
                <select
                  className="text-[1.2rem] p-[0.5rem] w-[80%] m-0 border-t-0 outline-none border-b-2 border-b-[#444] transition-all duration-200 ease-in-out cursor-pointer focus:pb-[1rem] focus:border-b-khamco-primary"
                  onChange={(e) => setSelectedIndex(e.target.value)}
                >
                  <option disabled>-- Select Data Table --</option>
                  <option value="Current Orders">Current Orders</option>
                  <option value="Average Measuring Time">
                    Average Measuring Time
                  </option>
                  <option value="Average Cutting Time">
                    Average Cutting Time
                  </option>
                  <option value="Average Tailoring Time">
                    Average Tailoring Time
                  </option>
                  <option value="Total QA/QC Orders">Total QA/QC Orders</option>
                  <option value="Total Driving Orders">
                    Total Driving Orders
                  </option>
                </select>
              </div>
              {/* Current Orders Table */}
              {selectedIndex === "Current Orders" &&
                (userOrders?.length > 0 ? (
                  <table className="w-full border-collapse text-[1.2rem] overflow-auto">
                    <caption className="text-khamco-primary font-bold mb-4">
                      Current Orders
                    </caption>
                    <thead className="text-khamco-primary font-bold">
                      <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                        <td className="text-center p-2 bg-[#ddd]">Order No.</td>
                        <td className="text-center p-2 bg-[#ddd]">
                          Order Status
                        </td>
                        <td className="text-center p-2 bg-[#ddd]">Role</td>
                        <td className="text-center p-2 bg-[#ddd]">Date</td>
                        <td className="text-center p-2 bg-[#ddd]">Link</td>
                      </tr>
                    </thead>
                    <tbody>
                      {userOrders.map((order, index) => (
                        <tr
                          key={index}
                          className="border-b-2 border-r-2 border-l-2 border-[blue] relative"
                        >
                          <td className="text-center p-2">{order.uniqueId}</td>
                          <td className="text-center p-2">
                            {order.order_status}
                          </td>
                          <td className="text-center p-2">
                            ({order.measurer === fullName && "Measurer, "}
                            {order.cutter === fullName && "Cutter, "}
                            {order.tailor === fullName && "Tailor, "}
                            {order.qa_qc === fullName && "QA/QC, "}
                            {order.driver === fullName && "Driver"})
                          </td>
                          <td className="text-center p-2">
                            {new Date(order.order_date).toLocaleDateString("en-US", {
                              month: "long",
                              day: "numeric",
                              year: "numeric"
                            })}
                          </td>
                          <td className="text-center p-2">
                            <Link
                              to={`/orders/${order._id}`}
                              className="py-[0.5rem] px-[1rem] outline-none border-none bg-[blue] text-white text-lg rounded-md cursor-pointer hover:opacity-90"
                              target="_blank"
                            >
                              View <i className="fas fa-external-link"></i>
                            </Link>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                ) : (
                  <p className="text-center">No Orders at the moment</p>
                ))}
              {/* Measurements Table */}
              {selectedIndex === "Average Measuring Time" && (
                <table className="w-full border-collapse text-[1.2rem] overflow-auto">
                  <caption className="text-khamco-primary font-bold mb-4">
                    Average Measuring Time
                  </caption>
                  <thead className="text-khamco-primary font-bold">
                    <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                      <td className="text-center p-2 bg-[#ddd]">Seconds</td>
                      <td className="text-center p-2 bg-[#ddd]">Minutes</td>
                      <td className="text-center p-2 bg-[#ddd]">Hours</td>
                      <td className="text-center p-2 bg-[#ddd]">Days</td>
                      <td className="text-center p-2 bg-[#ddd]">Weeks</td>
                      <td className="text-center p-2 bg-[#ddd]">Months</td>
                      <td className="text-center p-2 bg-[#ddd]">Years</td>
                    </tr>
                  </thead>
                  <tbody>
                    <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                      <td className="text-center p-2">
                        {user.measuring_duration && user.measurements
                          ? formatNumber(
                              user.measuring_duration.seconds /
                                user.measurements
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.measuring_duration && user.measurements
                          ? formatNumber(
                              user.measuring_duration.minutes /
                                user.measurements
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.measuring_duration && user.measurements
                          ? formatNumber(
                              user.measuring_duration.hours / user.measurements
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.measuring_duration && user.measurements
                          ? formatNumber(
                              user.measuring_duration.days / user.measurements
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.measuring_duration && user.measurements
                          ? formatNumber(
                              user.measuring_duration.weeks / user.measurements
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.measuring_duration && user.measurements
                          ? formatNumber(
                              user.measuring_duration.months / user.measurements
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.measuring_duration && user.measurements
                          ? formatNumber(
                              user.measuring_duration.years / user.measurements
                            )
                          : "--"}
                      </td>
                    </tr>
                  </tbody>
                </table>
              )}
              {/* Cutting Table */}
              {selectedIndex === "Average Cutting Time" && (
                <table className="w-full border-collapse text-[1.2rem] overflow-auto">
                  <caption className="text-khamco-primary font-bold mb-4">
                    Average Cutting Time
                  </caption>
                  <thead className="text-khamco-primary font-bold">
                    <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                      <td className="text-center p-2 bg-[#ddd]">Seconds</td>
                      <td className="text-center p-2 bg-[#ddd]">Minutes</td>
                      <td className="text-center p-2 bg-[#ddd]">Hours</td>
                      <td className="text-center p-2 bg-[#ddd]">Days</td>
                      <td className="text-center p-2 bg-[#ddd]">Weeks</td>
                      <td className="text-center p-2 bg-[#ddd]">Months</td>
                      <td className="text-center p-2 bg-[#ddd]">Years</td>
                    </tr>
                  </thead>
                  <tbody>
                    <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                      <td className="text-center p-2">
                        {user.cutting_duration && user.cutting
                          ? formatNumber(
                              user.cutting_duration.seconds / user.cutting
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.cutting_duration && user.cutting
                          ? formatNumber(
                              user.cutting_duration.minutes / user.cutting
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.cutting_duration && user.cutting
                          ? formatNumber(
                              user.cutting_duration.hours / user.cutting
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.cutting_duration && user.cutting
                          ? formatNumber(
                              user.cutting_duration.days / user.cutting
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.cutting_duration && user.cutting
                          ? formatNumber(
                              user.cutting_duration.weeks / user.cutting
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.cutting_duration && user.cutting
                          ? formatNumber(
                              user.cutting_duration.months / user.cutting
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.cutting_duration && user.cutting
                          ? formatNumber(
                              user.cutting_duration.years / user.cutting
                            )
                          : "--"}
                      </td>
                    </tr>
                  </tbody>
                </table>
              )}
              {/* Tailoring Table */}
              {selectedIndex === "Total Tailoring Orders" && (
                <table className="w-full border-collapse text-[1.2rem] overflow-auto">
                  <caption className="text-khamco-primary font-bold mb-4">
                    Average Tailoring Time
                  </caption>
                  <thead className="text-khamco-primary font-bold">
                    <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                      <td className="text-center p-2 bg-[#ddd]">Seconds</td>
                      <td className="text-center p-2 bg-[#ddd]">Minutes</td>
                      <td className="text-center p-2 bg-[#ddd]">Hours</td>
                      <td className="text-center p-2 bg-[#ddd]">Days</td>
                      <td className="text-center p-2 bg-[#ddd]">Weeks</td>
                      <td className="text-center p-2 bg-[#ddd]">Months</td>
                      <td className="text-center p-2 bg-[#ddd]">Years</td>
                    </tr>
                  </thead>
                  <tbody>
                    <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                      <td className="text-center p-2">
                        {user.tailoring_duration && user.tailoring
                          ? formatNumber(
                              user.tailoring_duration.seconds / user.tailoring
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.tailoring_duration && user.tailoring
                          ? formatNumber(
                              user.tailoring_duration.minutes / user.tailoring
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.tailoring_duration && user.tailoring
                          ? formatNumber(
                              user.tailoring_duration.hours / user.tailoring
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.tailoring_duration && user.tailoring
                          ? formatNumber(
                              user.tailoring_duration.days / user.tailoring
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.tailoring_duration && user.tailoring
                          ? formatNumber(
                              user.tailoring_duration.weeks / user.tailoring
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.tailoring_duration && user.tailoring
                          ? formatNumber(
                              user.tailoring_duration.months / user.tailoring
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.tailoring_duration && user.tailoring
                          ? formatNumber(
                              user.tailoring_duration.years / user.tailoring
                            )
                          : "--"}
                      </td>
                    </tr>
                  </tbody>
                </table>
              )}
              {/* QA/QC Table */}
              {selectedIndex === "Total QA/QC Orders" && (
                <table className="w-full border-collapse text-[1.2rem] overflow-auto">
                  <caption className="text-khamco-primary font-bold mb-4">
                    Average QA/QC Time
                  </caption>
                  <thead className="text-khamco-primary font-bold">
                    <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                      <td className="text-center p-2 bg-[#ddd]">Seconds</td>
                      <td className="text-center p-2 bg-[#ddd]">Minutes</td>
                      <td className="text-center p-2 bg-[#ddd]">Hours</td>
                      <td className="text-center p-2 bg-[#ddd]">Days</td>
                      <td className="text-center p-2 bg-[#ddd]">Weeks</td>
                      <td className="text-center p-2 bg-[#ddd]">Months</td>
                      <td className="text-center p-2 bg-[#ddd]">Years</td>
                    </tr>
                  </thead>
                  <tbody>
                    <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                      <td className="text-center p-2">
                        {user.qa_qc_duration && user.qa_qc
                          ? formatNumber(
                              user.qa_qc_duration.seconds / user.qa_qc
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.qa_qc_duration && user.qa_qc
                          ? formatNumber(
                              user.qa_qc_duration.minutes / user.qa_qc
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.qa_qc_duration && user.qa_qc
                          ? formatNumber(user.qa_qc_duration.hours / user.qa_qc)
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.qa_qc_duration && user.qa_qc
                          ? formatNumber(user.qa_qc_duration.days / user.qa_qc)
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.qa_qc_duration && user.qa_qc
                          ? formatNumber(user.qa_qc_duration.weeks / user.qa_qc)
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.qa_qc_duration && user.qa_qc
                          ? formatNumber(
                              user.qa_qc_duration.months / user.qa_qc
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.qa_qc_duration && user.qa_qc
                          ? formatNumber(user.qa_qc_duration.years / user.qa_qc)
                          : "--"}
                      </td>
                    </tr>
                  </tbody>
                </table>
              )}
              {/* Driving Table */}
              {selectedIndex === "Total Driving Orders" && (
                <table className="w-full border-collapse text-[1.2rem] overflow-auto">
                  <caption className="text-khamco-primary font-bold mb-4">
                    Average Driving Time
                  </caption>
                  <thead className="text-khamco-primary font-bold">
                    <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                      <td className="text-center p-2 bg-[#ddd]">Seconds</td>
                      <td className="text-center p-2 bg-[#ddd]">Minutes</td>
                      <td className="text-center p-2 bg-[#ddd]">Hours</td>
                      <td className="text-center p-2 bg-[#ddd]">Days</td>
                      <td className="text-center p-2 bg-[#ddd]">Weeks</td>
                      <td className="text-center p-2 bg-[#ddd]">Months</td>
                      <td className="text-center p-2 bg-[#ddd]">Years</td>
                    </tr>
                  </thead>
                  <tbody>
                    <tr className="border-b-2 border-r-2 border-l-2 border-[blue] relative">
                      <td className="text-center p-2">
                        {user.driving_duration && user.driving
                          ? formatNumber(
                              user.driving_duration.seconds / user.driving
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.driving_duration && user.driving
                          ? formatNumber(
                              user.driving_duration.minutes / user.driving
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.driving_duration && user.driving
                          ? formatNumber(
                              user.driving_duration.hours / user.driving
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.driving_duration && user.driving
                          ? formatNumber(
                              user.driving_duration.days / user.driving
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.driving_duration && user.driving
                          ? formatNumber(
                              user.driving_duration.weeks / user.driving
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.driving_duration && user.driving
                          ? formatNumber(
                              user.driving_duration.months / user.driving
                            )
                          : "--"}
                      </td>
                      <td className="text-center p-2">
                        {user.driving_duration && user.driving
                          ? formatNumber(
                              user.driving_duration.years / user.driving
                            )
                          : "--"}
                      </td>
                    </tr>
                  </tbody>
                </table>
              )}
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default AdminTailorDetails;
