import { useEffect, useState } from "react";

const UserForm = ({
  setFirstName,
  setLastName,
  setRole,
  setSalary,
  setLanguage,
  setUserPhoto,
  setEmail,
  setOldPassword,
  setPassword,
  setConfirmPassword,
  loading,
  firstName,
  lastName,
  role,
  language,
  email,
  salary,
  page,
}) => {
  const [oldPasswordType, setOldPasswordType] = useState(false);
  const [passwordType, setPasswordType] = useState(false);
  const [password2Type, setPassword2Type] = useState(false);

  return (
    <>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          First Name
        </label>
        <input
          className="text-lg my-2 p-2 border border-gray-800 rounded-md outline-none font-sans focus:border-[blue]"
          type="text"
          placeholder="Enter your first name"
          value={firstName || undefined}
          onChange={(e) => setFirstName(e.target.value)}
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Last Name
        </label>
        <input
          className="text-lg my-2 p-2 border border-gray-800 rounded-md outline-none font-sans focus:border-[blue]"
          type="text"
          placeholder="Enter your last name"
          value={lastName || undefined}
          onChange={(e) => setLastName(e.target.value)}
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Select User Role
        </label>
        <div className="flex gap-4 w-full justify-start flex-wrap my-[2rem]">
          <UserRole
            label={"Administrator"}
            options={role}
            selected={role.some((r) => r.type === "Administrator")}
            setOptions={setRole}
          />
          <UserRole
            label={"Measurer"}
            options={role}
            selected={role.some((r) => r.type === "Measurer")}
            setOptions={setRole}
          />
          <UserRole
            label={"Cutter"}
            options={role}
            selected={role.some((r) => r.type === "Cutter")}
            setOptions={setRole}
          />
          <UserRole
            label={"Tailor"}
            options={role}
            selected={role.some((r) => r.type === "Tailor")}
            setOptions={setRole}
          />
          <UserRole
            label={"Driver"}
            options={role}
            selected={role.some((r) => r.type === "Driver")}
            setOptions={setRole}
          />
        </div>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Language
        </label>
        <select
          className="text-lg my-2 p-2 border border-gray-800 rounded-md outline-none font-sans focus:border-[blue]"
          onChange={(e) => setLanguage(e.target.value)}
          value={language || undefined}
        >
          <option value="">-- Select user language --</option>
          <option value="en" label="English"></option>
          <option value="hi" label="Hindi"></option>
          <option value="ur" label="Urdu"></option>
          <option value="ar" label="Arabic"></option>
        </select>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Salary
        </label>
        <input
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="number"
          onChange={(e) => setSalary(e.target.value)}
          placeholder="Salary"
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          User Photo (Optional)
        </label>
        <input
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          type="file"
          name="cover"
          onChange={(e) => setUserPhoto(e.target.files[0])}
        />
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Email
        </label>
        <input
          className="text-lg my-2 p-2 border border-gray-800 rounded-md outline-none font-sans focus:border-[blue]"
          type="email"
          placeholder="Enter your email address"
          value={email || undefined}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>
      {page === "edit" && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label className="text-lg my-2 select-none self-start font-medium">
            Old Password
          </label>
          <input
            className="text-lg my-2 p-2 border border-gray-800 rounded-md outline-none font-sans focus:border-[blue]"
            type={oldPasswordType ? "text" : "password"}
            placeholder="Enter your old password"
            onChange={(e) => setOldPassword(e.target.value)}
          />
          <button
            type="button"
            className="absolute w-[12%] right-0 top-[60%] translate-y-[-50%] cursor-pointer capitalize hover:text-khamco-primary my-2"
            onClick={() => setOldPasswordType(!oldPasswordType)}
          >
            {oldPasswordType ? "Hide" : "Show"}
          </button>
        </div>
      )}
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          New Password
        </label>
        <input
          className="text-lg my-2 p-2 border border-gray-800 rounded-md outline-none font-sans focus:border-[blue]"
          type={passwordType ? "text" : "password"}
          placeholder="Enter your desired password"
          onChange={(e) => setPassword(e.target.value)}
        />
        <button
          type="button"
          className="absolute w-[12%] right-0 top-[60%] translate-y-[-50%] cursor-pointer capitalize hover:text-khamco-primary my-2"
          onClick={() => setPasswordType(!passwordType)}
        >
          {passwordType ? "Hide" : "Show"}
        </button>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label className="text-lg my-2 select-none self-start font-medium">
          Confirm Password
        </label>
        <input
          className="text-lg my-2 p-2 border border-gray-800 rounded-md outline-none font-sans focus:border-[blue]"
          type={password2Type ? "text" : "password"}
          placeholder="Confirm your password"
          onChange={(e) => setConfirmPassword(e.target.value)}
        />
        <button
          type="button"
          className="absolute w-[12%] right-0 top-[60%] translate-y-[-50%] cursor-pointer capitalize hover:text-khamco-primary my-2"
          onClick={() => setPassword2Type(!password2Type)}
        >
          {password2Type ? "Hide" : "Show"}
        </button>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <button
          type="submit"
          className="bg-[blue] w-1/2 mx-auto p-4 text-white rounded-full border-none outline-none hover:bg-blue-900"
          disabled={loading}
        >
          {loading ? <i className="fas fa-spinner animate-spin"></i> : "Submit"}
        </button>
      </div>
    </>
  );
};

export const UserRole = ({ label, selected, options, setOptions }) => {
  // Controlled state for isChecked
  const [isChecked, setIsChecked] = useState(selected);

  useEffect(() => {
    // Sync the internal state with the prop if the prop changes
    setIsChecked(selected);
    console.log(options);
  }, [options, selected]);

  // Generate a unique ID for each UserRole component
  const inputId = `name_${label.replace(/\s+/g, "_").toLowerCase()}`;

  const handleRoleToggle = () => {
    if (isChecked) {
      // Remove the role if it's currently selected
      setOptions((prevArray) =>
        prevArray.filter((item) => item.type !== label)
      );
    } else {
      // Add the role if it's not currently selected
      setOptions((prevArray) => {
        const exists = prevArray.some((item) => item.type === label);
        if (!exists) {
          if (label === "Measurer") {
            return [...prevArray, { type: label }];
          }
          return [...prevArray, { type: label }];
        }
        return prevArray;
      });
    }
    console.log(options);
  };

  return (
    <>
      <label
        className={`border-2 border-[black] p-2 w-[10rem] md:w-[8rem] flex flex-col items-center gap-2 cursor-pointer rounded-xl text-center font-medium ${
          isChecked ? "border-khamco-primary bg-[#ccc]" : ""
        }`}
        htmlFor={inputId}
        onClick={handleRoleToggle}
      >
        {label}
      </label>
      <input
        className="hidden"
        id={inputId}
        type="checkbox"
        checked={isChecked}
        onChange={() => setIsChecked(!isChecked)}
      />
    </>
  );
};

export default UserForm;
