import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import { Link, useLocation, useNavigate, useParams } from "react-router-dom";
import Footer from "../../components/Footer";
import { useStoreState } from "easy-peasy";
import axios from "axios";
import { toast } from "react-toastify";

const CustomerInvoices = () => {
  const { id } = useParams();
  const [invoices, setInvoices] = useState(null);
  const [customer, setCustomer] = useState(null);
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [writingDirection, setWritingDirection] = useState("ltr");
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/customers/${id}/invoices`
    : `https://khamco-shop-backend.onrender.com/customers/${id}/invoices`;
  const authKey = localStorage.getItem("jwt");
  const navigate = useNavigate();
  const location = useLocation();
  const [loading, setLoading] = useState(true);
  const [display, setDisplay] = useState(false);

  const fetchData = async (queryParams = "") => {
    await axios
      .get(`${apiUrl}${queryParams}`, {
        headers: { Authorization: `Bearer ${authKey}` },
      })
      .then((response) => {
        setCustomer(response.data.customer);
        setInvoices(response.data.invoices);
        setLoading(false);
        setDisplay(true);
      })
      .catch((error) => {
        toast.error(error.response?.data?.message || error.message);
        setCustomer(null);
        setLoading(true);
        setLoading(false);
        setInvoices(null);
      });
  };

  useEffect(() => {
    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      } else {
        setWritingDirection("ltr");
      }
    };
    determineWritingDirection();
    fetchData(location.search);
  }, [apiUrl, authKey, userLanguage, location.search]);

  const handleSearch = (e) => {
    e.preventDefault();
    const form = new FormData(e.target);
    const searchParams = new URLSearchParams(form).toString();
    navigate(`?${searchParams}`);
  };

  return (
    <>
      <Navbar />
      <main>
        <section
          id="customer-page"
          className="py-0 px-[2rem] grid justify-center items-center grid-cols-1 gap-[1rem]"
          dir={writingDirection}
        >
          {loading &&
            new Array(3)
              .fill(null)
              .map((_, index) => (
                <div
                  key={`placeholder-${index}`}
                  className="w-full bg-[#ccc] animate-pulse py-[4rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] flex justify-evenly items-center gap-[2rem]"
                  dir={writingDirection}
                ></div>
              ))}
          {display && invoices && customer && (
            <>
              <div className="utilities">
                <Link to={`/customers/${customer._id}`}>
                  <i className="fas fa-arrow-left"></i>
                  {` Customer ${customer.name} ${customer.surname}`}
                </Link>
              </div>
              <div className="utilities">
                <form onSubmit={handleSearch} className="search-engine">
                  <input type="date" name="date" className="search-input" />
                  <button type="submit" className="search-button">
                    <i className="fas fa-check"></i>
                  </button>
                </form>
              </div>
              <h1 className="title">{`All Invoices for ${customer.name} ${customer.surname} (${invoices.length})`}</h1>
              {invoices && invoices.length > 0 ? (
                invoices.map((invoice, index) => (
                  <div
                    className="w-full bg-[#bebebe80] p-[2rem] shadow-lg shadow-[#333] rounded-[0.5rem] flex justify-evenly items-center gap-[2rem] my-[1rem]"
                    key={index}
                  >
                    <p className="capitalize">
                      <strong className="text-khamco-secondary text-lg">
                        Date
                      </strong>
                      :{" "}
                      {new Date(invoice.date).toLocaleDateString("en-US", {
                        month: "long",
                        day: "numeric",
                        year: "numeric",
                      })}
                    </p>
                    <p className="capitalize">
                      <strong className="text-khamco-secondary text-lg">
                        Payment Status:
                      </strong>{" "}
                      {invoice.payment_status}
                    </p>
                    <p className="capitalize">
                      <strong className="text-khamco-secondary text-lg">
                        Membership:
                      </strong>{" "}
                      {customer.membership_status === "VIP" ? (
                        <i className="fas fa-star blink">VIP</i>
                      ) : (
                        "Basic"
                      )}
                    </p>
                    <Link to={`/invoices/${invoice._id}`} className="edit-btn">
                      View
                    </Link>
                  </div>
                ))
              ) : (
                <p className="text-center">This customer has no invoices.</p>
              )}
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default CustomerInvoices;
