import { useEffect, useState } from "react";
import Clothing from "../../../components/Clothing";
import {
  clothingList,
  dishdashaLength,
  dressLength,
  garmentLength,
  hasDressSleeve,
  hasOther,
  hasShirt,
  hasShirtSleeve,
  hasSleepwearSleeve,
  hasSpecialOrder,
  upperMeasurements,
  needsSleeve,
  shortsLength,
  lowerMeasurements,
  isBottom,
  hasBackWidth,
  hasAlterations,
  isCoverall,
} from "../../../utilities";
import { useTranslation } from "react-i18next";
import UnitInput from "../../../components/UnitInput";

const MeasurementsForm = ({
  authorizedUser,
  clothingOptions,
  setClothingOptions,
  setDishdashaLength,
  setDishdashaFrontHeight,
  setDishdashaBackHeight,
  shirtType,
  setShirtType,
  setGarmentLength,
  // setCoverallLength,
  setPantsLength,
  setShortsLength,
  setDressLength,
  setCollar,
  setShoulders,
  setSleeve,
  setShirtSleeve,
  setDressSleeve,
  setDescription,
  setSleepwearSleeve,
  setChest,
  setBelly,
  setWaist,
  setHips,
  setArmHole,
  setBicepTricep,
  loading,
  setElbow,
  setWrist,
  setInseam,
  setThigh,
  setCalf,
  setLegFoot,
  setBackWidth,
  setBackHeight,
  setFrontChestWidth,
  setSpecialOrder,
  setAlterations,
  setNotes,
}) => {
  const { t } = useTranslation();
  const [unitType, setUnitType] = useState(false);
  const [values, setValues] = useState({});

  const handleInputChange = (name, value) => {
    setValues((prevValues) => ({
      ...prevValues,
      [name]: value,
    }));
  };

  useEffect(() => {
    const numberInputs = document.querySelectorAll("input [type='number']");

    numberInputs.forEach((element) => {
      if (unitType === "Inches") {
        element.value *= 2.54;
      } else {
        element.value /= 2.54;
      }
    });
  }, [unitType]);

  return (
    <>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <h1 className="text-center text-[2rem] font-minimoBold mb-8">
          Click to select/deselect an item
        </h1>
        <div className="relative flex justify-center md:justify-start gap-[1rem] md:gap-[0.5rem] flex-wrap h-[70vh] overflow-y-scroll">
          {clothingList &&
            clothingList.map((item, index) => (
              <Clothing
                key={index}
                label={item.label}
                imageUrl={item.imageUrl}
                setOptions={setClothingOptions}
              />
            ))}
        </div>
      </div>
      {clothingOptions.length > 0 && (
        <>
          <p className="bg-khamco-primary text-white text-[1.2rem] rounded-full py-1 px-2 w-10 text-center">
            {clothingOptions.length}
          </p>
          <p className="my-4 text-[#444] text-lg">Enter measurements below</p>
          <button
            type="button"
            className="p-2 my-4 bg-blue-500 text-white rounded-md"
            onClick={() =>
              setUnitType(unitType === "Inches" ? "Centimeters" : "Inches")
            }
          >
            Change to {unitType === "Inches" ? "Centimeters" : "Inches"}
          </button>
        </>
      )}
      {clothingOptions.length > 0 && hasShirt(clothingOptions) && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <h1 className="text-center text-[1.5rem] font-minimoBold mb-8">
            Select Shirt Type
          </h1>
          <div className="flex gap-4 w-full justify-center flex-wrap">
            <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
              <label
                className="text-xl my-2 select-none self-start font-medium cursor-pointer"
                htmlFor="long-sleeve"
              >
                Long Sleeve{" "}
              </label>
              <input
                className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
                type="radio"
                id="long-sleeve"
                name="shirt-type"
                value="Long Sleeve"
                checked={shirtType === "Long Sleeve"}
                onChange={() => {
                  setShirtType(() => "Long Sleeve");
                }}
              />
            </span>
            <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
              <label
                className="text-xl my-2 select-none self-start font-medium cursor-pointer"
                htmlFor="short-sleeve"
              >
                Short Sleeve{" "}
              </label>
              <input
                className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
                type="radio"
                id="short-sleeve"
                name="shirt-type"
                checked={shirtType === "Short Sleeve"}
                onChange={() => {
                  setShirtType(() => "Short Sleeve");
                }}
              />
            </span>
            <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
              <label
                className="text-xl my-2 select-none self-start font-medium cursor-pointer"
                htmlFor="polo-shirt"
              >
                Polo{" "}
              </label>
              <input
                className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
                id="polo-shirt"
                name="shirt-type"
                type="radio"
                checked={shirtType === "Polo"}
                onChange={() => {
                  setShirtType(() => "Polo");
                }}
              />
            </span>
            <span className="border border-[gray] py-[0.1rem] px-[0.5rem] rounded-[1rem]">
              <label
                className="text-xl my-2 select-none self-start font-medium cursor-pointer"
                htmlFor="t-shirt"
              >
                T-Shirt{" "}
              </label>
              <input
                className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue] cursor-pointer"
                type="radio"
                value="T-Shirt"
                id="t-shirt"
                name="shirt-type"
                checked={shirtType === "T-Shirt"}
                onChange={() => {
                  setShirtType(() => "T-Shirt");
                }}
              />
            </span>
          </div>
        </div>
      )}
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <div className="flex w-full justify-between flex-wrap">
          {dishdashaLength(clothingOptions) && (
            <>
              <UnitInput
                label="Dishdasha Length"
                name="dishdasha_garment_length"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <UnitInput
                label="Dishdasha Front Height"
                name="dishdasha_front_height"
                unitType={unitType}
                onChange={handleInputChange}
              />
              <UnitInput
                label="Dishdasha Back Height"
                name="dishdasha_back_height"
                unitType={unitType}
                onChange={handleInputChange}
              />
            </>
          )}
          {garmentLength(clothingOptions) && (
            <UnitInput
              label="Garment Length"
              name="garment_length"
              unitType={unitType}
              onChange={handleInputChange}
            />
          )}
          {isCoverall(clothingOptions) && (
            <UnitInput
              label="Coverall length"
              name="coverall_length"
              unitType={unitType}
              onChange={handleInputChange}
            />
          )}
          {isBottom(clothingOptions) && (
            <>
            <UnitInput
              label="Pants length"
              name="pants_garment_length"
              unitType={unitType}
              onChange={handleInputChange}
            />
              <div className="flex flex-col">
                <label className="text-center"></label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Pants length"
                  onChange={(e) => setPantsLength(e.target.value)}
                />
              </div>
              <div className="flex flex-col">
                <label className="text-center">Inseam</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Inseam"
                  onChange={(e) => setInseam(e.target.value)}
                />
              </div>
              <div className="flex flex-col">
                <label className="text-center">Thigh</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Thigh"
                  onChange={(e) => setThigh(e.target.value)}
                />
              </div>
              <div className="flex flex-col">
                <label className="text-center">Calf</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Calf"
                  onChange={(e) => setCalf(e.target.value)}
                />
              </div>
              <div className="flex flex-col">
                <label className="text-center">Leg-Foot</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Leg-Foot"
                  onChange={(e) => setLegFoot(e.target.value)}
                />
              </div>
            </>
          )}
          {shortsLength(clothingOptions) && (
            <div className="flex flex-col">
              <label className="text-center">Shorts length</label>
              <input
                className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                type="number"
                step={0.1}
                placeholder="Shorts length"
                onChange={(e) => setShortsLength(e.target.value)}
              />
            </div>
          )}
          {dressLength(clothingOptions) && (
            <div className="flex flex-col">
              <label className="text-center">Dress length</label>
              <input
                className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                type="number"
                step={0.1}
                placeholder="Dress length"
                onChange={(e) => setDressLength(e.target.value)}
              />
            </div>
          )}
          {hasDressSleeve(clothingOptions) && (
            <div className="flex flex-col">
              <label className="text-center">Dress sleeve</label>
              <input
                className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                type="number"
                step={0.1}
                placeholder="Dress sleeve"
                onChange={(e) => setDressSleeve(e.target.value)}
              />
            </div>
          )}
          {needsSleeve(clothingOptions) && (
            <div className="flex flex-col">
              <label className="text-center">Sleeve</label>
              <input
                className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                type="number"
                step={0.1}
                placeholder="Sleeve"
                onChange={(e) => setSleeve(e.target.value)}
              />
            </div>
          )}
          {hasShirtSleeve(clothingOptions) && (
            <div className="flex flex-col">
              <label className="text-center">Shirt sleeve</label>
              <input
                className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                type="number"
                step={0.1}
                placeholder="Shirt sleeve"
                onChange={(e) => setShirtSleeve(e.target.value)}
              />
            </div>
          )}
          {hasSleepwearSleeve(clothingOptions) && (
            <div className="flex flex-col">
              <label className="text-center">Sleepwear sleeve</label>
              <input
                className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                type="number"
                step={0.1}
                placeholder="Sleepwear sleeve"
                onChange={(e) => setSleepwearSleeve(e.target.value)}
              />
            </div>
          )}
          {upperMeasurements(clothingOptions) && (
            <>
              <div className="flex flex-col">
                <label className="text-center">Collar</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Collar"
                  onChange={(e) => setCollar(e.target.value)}
                />
              </div>
              <div className="flex flex-col">
                <label className="text-center">Shoulders</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Shoulders"
                  onChange={(e) => setShoulders(e.target.value)}
                />
              </div>
              <div className="flex flex-col">
                <label className="text-center">Chest</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Chest"
                  onChange={(e) => setChest(e.target.value)}
                />
              </div>
              <div className="flex flex-col">
                <label className="text-center">Belly</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Belly"
                  onChange={(e) => setBelly(e.target.value)}
                />
              </div>
              <div className="flex flex-col">
                <label className="text-center">Arm hole</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Arm hole"
                  onChange={(e) => setArmHole(e.target.value)}
                />
              </div>
              <div className="flex flex-col">
                <label className="text-center">Bicep/Tricep</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Bicep/Tricep"
                  onChange={(e) => setBicepTricep(e.target.value)}
                />
              </div>
              <div className="flex flex-col">
                <label className="text-center">Elbow</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Elbow"
                  onChange={(e) => setElbow(e.target.value)}
                />
              </div>
              <div className="flex flex-col">
                <label className="text-center">Wrist</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Wrist"
                  onChange={(e) => setWrist(e.target.value)}
                />
              </div>
            </>
          )}
          {lowerMeasurements(clothingOptions) && (
            <>
              <div className="flex flex-col">
                <label className="text-center">Waist</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Waist"
                  onChange={(e) => setWaist(e.target.value)}
                />
              </div>
              <div className="flex flex-col">
                <label className="text-center">Hips</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Hips"
                  onChange={(e) => setHips(e.target.value)}
                />
              </div>
            </>
          )}
          {hasBackWidth(clothingOptions) && (
            <>
              <div className="flex flex-col">
                <label className="text-center">Back width</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Back width"
                  onChange={(e) => setBackWidth(e.target.value)}
                />
              </div>
              <div className="flex flex-col">
                <label className="text-center">Back height</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Back height"
                  onChange={(e) => setBackHeight(e.target.value)}
                />
              </div>
              <div className="flex flex-col">
                <label className="text-center">Front-Chest-Width</label>
                <input
                  className="text-center p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
                  type="number"
                  step={0.1}
                  placeholder="Front-Chest-Width"
                  onChange={(e) => setFrontChestWidth(e.target.value)}
                />
              </div>
            </>
          )}
        </div>
      </div>
      {hasSpecialOrder(clothingOptions) && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label>Special order details</label>
          <textarea
            className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
            placeholder="Special order details"
            onChange={(e) => setSpecialOrder(e.target.value)}
          ></textarea>
        </div>
      )}
      {hasAlterations(clothingOptions) && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label>Alterations</label>
          <textarea
            className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
            placeholder="Alterations"
            onChange={(e) => setAlterations(e.target.value)}
          ></textarea>
        </div>
      )}
      {hasOther(clothingOptions) && (
        <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
          <label>Other</label>
          <textarea
            className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
            placeholder="Enter a description"
            onChange={(e) => setDescription(e.target.value)}
          ></textarea>
        </div>
      )}
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <label>Extra notes</label>
        <textarea
          className="p-2 border border-gray-800 rounded-md outline-none font-sans text-lg my-2 focus:border-[blue]"
          placeholder="Extra notes"
          onChange={(e) =>
            setNotes({
              user: authorizedUser,
              text: e.target.value,
              timestamp: new Date(),
            })
          }
        ></textarea>
      </div>
      <div className="w-full md:w-1/2 flex flex-col mx-auto my-4 relative">
        <button
          type="submit"
          className="text-center bg-[blue] w-full md:w-1/2 mx-auto p-4 text-white text-lg rounded-full border-none outline-none hover:bg-blue-900 cursor-pointer"
          disabled={loading}
        >
          {loading ? (
            <i className="fas fa-spinner animate-spin"></i>
          ) : (
            `${t("Submit")}`
          )}
        </button>
      </div>
    </>
  );
};

export default MeasurementsForm;
