import tshirt from "../../../assets/images/tshirt.svg";
import poloShirt from "../../../assets/images/polo-shirt.png";
import shirt from "../../../assets/images/shirt.png";
import longShirt from "../../../assets/images/long-shirt.png";
import jacket from "../../../assets/images/jacket.png";
import hoodie from "../../../assets/images/hoodie.png";
import suit from "../../../assets/images/suit.png";
import pants from "../../../assets/images/pants.png";
import shorts from "../../../assets/images/shorts.png";
import dress from "../../../assets/images/dress.png";
import shoes from "../../../assets/images/shoes.png";
import socks from "../../../assets/images/socks.png";
import tanktop from "../../../assets/images/tanktop.png";
import briefs from "../../../assets/images/briefs.png";
import bra from "../../../assets/images/bra.png";
import backpack from "../../../assets/images/backpack.png";
import snapback from "../../../assets/images/snapback.png";
import thawb from "../../../assets/images/thawb.png";
import mukasar from "../../../assets/images/mukasar.png";
import bisht from "../../../assets/images/bisht.png";
import sleepwear from "../../../assets/images/sleepwear.png";
import waistcoat from "../../../assets/images/waistcoat.png";

import {
  hasBottom,
  hasHeadWear,
  hasTop,
  isFootWear,
  isVest
} from "../../../functions";
import Clothing from "../Clothing";

const MeasurementsForm = ({
  clothingOptions,
  setClothingOptions,
  setWaist,
  setNaturalWaist,
  setGarmentLength,
  setHalfAcross,
  setChest,
  setTrouserWaistPosition,
  setSeat,
  setNeck,
  setShoulder,
  setSleeveLength,
  setSideSeamLength,
  setBodyRise,
  setInsideLeg,
  setWrist,
  setTopArm,
  setScyeDepth,
  setArmSleeveHole,
  setFrontalCollar,
  setHip,
  setFrontRise,
  setBackRise,
  setThigh,
  setKnee,
  setHem,
  setOutseam,
  setHeadSize,
  setFootSize,
  setDescription,
  loading,
}) => {
  const clothingList = [
    { label: "Dish Dasha", imageUrl: thawb },
    { label: "T-Shirt", imageUrl: tshirt },
    { label: "Polo Shirt", imageUrl: poloShirt },
    { label: "Shirt", imageUrl: shirt },
    { label: "Long Shirt", imageUrl: longShirt },
    { label: "Jacket", imageUrl: jacket },
    { label: "Hoodie", imageUrl: hoodie },
    { label: "Suit", imageUrl: suit },
    { label: "Pants", imageUrl: pants },
    { label: "Shorts", imageUrl: shorts },
    { label: "Dress", imageUrl: dress },
    { label: "Shoes", imageUrl: shoes },
    { label: "Socks", imageUrl: socks },
    { label: "Tank Top", imageUrl: tanktop },
    { label: "Briefs", imageUrl: briefs },
    { label: "Bra", imageUrl: bra },
    { label: "Bag", imageUrl: backpack },
    { label: "Hat", imageUrl: snapback },
    { label: "Mukasar", imageUrl: mukasar },
    { label: "Bisht", imageUrl: bisht },
    { label: "Sleepwear", imageUrl: sleepwear },
    { label: "Waist Coat", imageUrl: waistcoat },
  ];

  return (
    <>
      <div className="form-row">
        <h1 className="title mb-8">Click to select an item</h1>
        <div className="relative flex justify-center md:justify-start md:gap-[0.5rem] flex-wrap h-[50vh] overflow-y-scroll">
          {clothingList &&
            clothingList.map((item, index) => (
              <Clothing
                key={index}
                label={item.label}
                imageUrl={item.imageUrl}
                setClothingOptions={setClothingOptions}
              />
            ))}
        </div>
      </div>
      {clothingOptions.length > 0 && (
        <>
          <p className="bg-khamco-primary text-white text-[1.2rem] rounded-full py-1 px-2 w-10 text-center fadeIn">
            {clothingOptions.length}
          </p>
          <p className="my-4 text-[#444] text-lg">Enter measurements below</p>
        </>
      )}
      {clothingOptions.length > 0 && (
        <>
          {/* Tops and Pants */}
          {(hasTop(clothingOptions) ||
            hasBottom(clothingOptions) ||
            isVest(clothingOptions)) && (
            <div className="form-row">
              <input
                type="number"
                step={0.1}
                placeholder="Waist"
                onChange={(e) => setWaist(e.target.value)}
              />
            </div>
          )}
          {/* Shirts (long sleeve and short) */}
          {(hasTop(clothingOptions) || isVest(clothingOptions)) && (
            <>
              <div className="form-row">
                <input
                  type="number"
                  step={0.1}
                  placeholder="Natural Waist Length"
                  onChange={(e) => setNaturalWaist(e.target.value)}
                />
              </div>
              <div className="form-row">
                <div className="inline">
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Garment Length"
                    onChange={(e) => setGarmentLength(e.target.value)}
                  />
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Half Across Back"
                    onChange={(e) => setHalfAcross(e.target.value)}
                  />
                </div>
                <div className="inline">
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Chest"
                    onChange={(e) => setChest(e.target.value)}
                  />
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Trouser Waist Position"
                    onChange={(e) => setTrouserWaistPosition(e.target.value)}
                  />
                </div>
                <div className="inline">
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Seat"
                    onChange={(e) => setSeat(e.target.value)}
                  />
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Neck"
                    onChange={(e) => setNeck(e.target.value)}
                  />
                </div>
                <div className="inline">
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Shoulder"
                    onChange={(e) => setShoulder(e.target.value)}
                  />
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Sleeve Length"
                    onChange={(e) => setSleeveLength(e.target.value)}
                  />
                </div>
                <div className="inline">
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Side Seam Length"
                    onChange={(e) => setSideSeamLength(e.target.value)}
                  />
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Body Rise"
                    onChange={(e) => setBodyRise(e.target.value)}
                  />
                </div>
                <div className="inline">
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Inside Leg"
                    onChange={(e) => setInsideLeg(e.target.value)}
                  />
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Wrist"
                    onChange={(e) => setWrist(e.target.value)}
                  />
                </div>
                <div className="inline">
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Top Arm"
                    onChange={(e) => setTopArm(e.target.value)}
                  />
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Scye Depth"
                    onChange={(e) => setScyeDepth(e.target.value)}
                  />
                </div>
                {/* Vests only */}
                <div className="inline">
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Sleeve Hole"
                    onChange={(e) => setArmSleeveHole(e.target.value)}
                  />
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Collar Opening"
                    onChange={(e) => setFrontalCollar(e.target.value)}
                  />
                </div>
              </div>
            </>
          )}
          {/* Pants/Trousers: */}
          {hasBottom(clothingOptions) && (
            <>
              <div className="form-row">
                <input
                  type="number"
                  step={0.1}
                  placeholder="Hip (Inches)"
                  onChange={(e) => setHip(e.target.value)}
                />
              </div>
              <div className="form-row">
                <div className="inline">
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Front Rise"
                    onChange={(e) => setFrontRise(e.target.value)}
                  />
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Back Rise"
                    onChange={(e) => setBackRise(e.target.value)}
                  />
                </div>
                <div className="inline">
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Thigh"
                    onChange={(e) => setThigh(e.target.value)}
                  />
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Knee"
                    onChange={(e) => setKnee(e.target.value)}
                  />
                </div>
                <div className="inline">
                  <input
                    type="number"
                    step={0.1}
                    placeholder="Hem"
                    onChange={(e) => setHem(e.target.value)}
                  />
                  <input
                    type="number"
                    step={0.1}
                    placeholder=" Outseam"
                    onChange={(e) => setOutseam(e.target.value)}
                  />
                </div>
              </div>
            </>
          )}
          {/* Hats */}
          {hasHeadWear(clothingOptions) && (
            <div className="form-row">
              <input
                type="number"
                step={0.1}
                placeholder="Head Size"
                onChange={(e) => setHeadSize(e.target.value)}
              />
            </div>
          )}
          {/* Footwear */}
          {isFootWear(clothingOptions) && (
            <div className="form-row">
              <input
                type="number"
                step={0.1}
                placeholder="Foot Size"
                onChange={(e) => setFootSize(e.target.value)}
              />
            </div>
          )}
        </>
      )}
      <div className="form-row">
        <textarea
          placeholder="Extra notes (optional)"
          onChange={(e) => setDescription(e.target.value)}
        ></textarea>
      </div>
      <div className="form-row">
        <button type="submit">
          {loading ? <i className="fas fa-spinner animate-spin"></i> : "Submit"}
        </button>
      </div>
    </>
  );
};

export default MeasurementsForm;
