import React, { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useParams } from "react-router-dom";
import axios from "axios";
import { toast } from "react-toastify";

const ServicesView = () => {
  const { id } = useParams();
  const [service, setService] = useState({});
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/services/${id}`
    : `https://khamcotailor.com/services/${id}`;
  const authKey = localStorage.getItem("jwt");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    document.title = "Service Details - Khamco Shop";

    const fetchService = async () => {
      try {
        const response = await axios.get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        });
        setService(response.data.service);
        setLoading(false);
      } catch (error) {
        setLoading(false);
        setService({});
        toast.error(error.message);
      }
    };

    fetchService();
  }, [apiUrl, authKey]);

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <section>
          <div className="flex justify-between gap-[2rem] items-center my-[2rem] m-auto w-full flex-wrap">
            <Link to="/services">
              <i className="fas fa-arrow-left"></i> Services
            </Link>
          </div>
          {loading && <div>Loading...</div>}
          {service && (
            <div className="khamco-card">
              <div className="utilities">
                <Link to={`/services/edit/${service.id}`} className="edit-btn">
                  Edit
                </Link>
                <form
                  action="/services/<%= service.id %>?_method=DELETE"
                  method="POST"
                  className="d-inline"
                >
                  <button type="submit" className="delete-btn">
                    Delete
                  </button>
                </form>
              </div>
              <h1 className="title">{service.service}</h1>
              <div className="flex flex-col gap-[1rem]">
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap capitalize">
                  <strong>Price</strong>
                  <p>KWD {service.price || "--"}</p>
                </span>
                <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap capitalize">
                  <strong>Date Entered</strong>
                  <p className="capitalize">
                    {service.date.toLocaleDateString("en-us", {
                      month: "long",
                      year: "numeric",
                      day: "numeric",
                    }) || "--"}
                  </p>
                </span>
              </div>
            </div>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default ServicesView;
