import { useEffect, useRef } from "react";

const AssignedOrders = ({ isOpen, setIsOpen, users, orders }) => {
  const assigned_orders = useRef(0);

  useEffect(() => {
    const countOrders = () => {
      orders.forEach((order) => {
        if (
          order.measurer ===
          users.some((user) => `${user.name} ${user.surname}`)
        ) {
          assigned_orders.current += 1;
        }

        if (
          order.cutter === users.some((user) => `${user.name} ${user.surname}`)
        ) {
          assigned_orders.current += 1;
        }

        if (
          order.tailor === users.some((user) => `${user.name} ${user.surname}`)
        ) {
          assigned_orders.current += 1;
        }

        if (
          order.driver === users.some((user) => `${user.name} ${user.surname}`)
        ) {
          assigned_orders.current += 1;
        }
      });
    };

    countOrders();
  }, [orders, users]);

  return (
    <dialog
      className="fixed top-2/4 left-2/4 -translate-x-2/4 -translate-y-2/4 w-[90%] md:w-2/4 rounded-[2rem] shadow-md shadow-[#444] overflow-y-auto overflow-x-hidden max-h-[80vh] py-[2rem] px-0"
      open={isOpen}
    >
      <button
        className="py-[0.5rem] px-[1rem] outline-none border-none bg-[red] text-white text-lg rounded-md cursor-pointer hover:opacity-90 absolute top-[5%] left-[5%]"
        type="button"
        onClick={() => setIsOpen(false)}
      >
        <i className="fas fa-times"></i>
      </button>
      <ul className="my-[2rem]">
        {users &&
          users.map((user, index) => (
            <li key={index}>
              {`${user.name} ${user.surname}`} ({assigned_orders} Orders)
            </li>
          ))}
      </ul>
    </dialog>
  );
};

export default AssignedOrders;
