import { useEffect, useState } from "react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import { Link, useNavigate } from "react-router-dom";
import { useStoreState } from "easy-peasy";
import CustomerForm from "./CustomerForm";
import axios from "axios";
import { toast } from "react-toastify";

const CreateCustomer = () => {
  const [customerName, setCustomerName] = useState("");
  const [customerSurname, setCustomerSurname] = useState("");
  const [customerEmail, setCustomerEmail] = useState("");
  const [customerPhone, setCustomerPhone] = useState("");
  const [houseUnit, setHouseUnit] = useState("");
  const [governorate, setGovernorate] = useState("Al Aasima");
  const [area, setArea] = useState("Abdulla Al-Salem");
  const [country, setCountry] = useState("Kuwait");
  const [militaryStatus, setMilitaryStatus] = useState("Civilian");
  const [membershipStatus, setMembershipStatus] = useState("Basic");
  const [militaryNumber, setMilitaryNumber] = useState("");
  const [militaryRank, setMilitaryRank] = useState("");
  const [militaryDivision, setMilitaryDivision] = useState("");
  const [date, setDate] = useState(new Date());
  // Measurement fields
  const [clothingOptions, setClothingOptions] = useState([]);
  const [description, setDescription] = useState("");
  const [waist, setWaist] = useState(0);
  const [naturalWaist, setNaturalWaist] = useState(0);
  const [garmentLength, setGarmentLength] = useState(0);
  const [halfAcross, setHalfAcross] = useState(0);
  const [chest, setChest] = useState(0);
  const [trouserWaistPosition, setTrouserWaistPosition] = useState(0);
  const [seat, setSeat] = useState(0);
  const [neck, setNeck] = useState(0);
  const [shoulder, setShoulder] = useState(0);
  const [sleeveLength, setSleeveLength] = useState(0);
  const [sideSeamLength, setSideSeamLength] = useState(0);
  const [bodyRise, setBodyRise] = useState(0);
  const [insideLeg, setInsideLeg] = useState(0);
  const [wrist, setWrist] = useState(0);
  const [topArm, setTopArm] = useState(0);
  const [scyeDepth, setScyeDepth] = useState(0);
  const [armSleeveHole, setArmSleeveHole] = useState(0);
  const [frontalCollar, setFrontalCollar] = useState(0);
  const [hip, setHip] = useState(0);
  const [frontRise, setFrontRise] = useState(0);
  const [backRise, setBackRise] = useState(0);
  const [thigh, setThigh] = useState(0);
  const [knee, setKnee] = useState(0);
  const [hem, setHem] = useState(0);
  const [outseam, setOutseam] = useState(0);
  const [footSize, setFootSize] = useState(0);
  const [headSize, setHeadSize] = useState(0);
  const [areas, setAreas] = useState([
    "Abdulla Al-Salem",
    "Adailiya",
    "Bnaid Al-Qar",
    "Daʿiya",
    "Dasma",
    "Doha",
    "Doha Port",
    "Faiha'",
    "Failaka",
    "Granada",
    "Jaber Al-Ahmad City",
    "Jibla",
    "Kaifan",
    "Khaldiya",
    "Mansūriya",
    "Mirgab",
    "Nahdha",
    "North West Sulaibikhat",
    "Nuzha",
    "Qadsiya",
    "Qurtuba",
    "Rawda",
    "Shamiya",
    "Sharq",
    "Shuwaikh",
    "Shuwaikh Industrial Area",
    "Shuwaikh Port",
    "Sulaibikhat",
    "Surra",
    "Umm an Namil Island",
    "Yarmouk",
  ]);
  const apiUrl = window.location.origin.includes("localhost")
    ? "http://localhost:5000/customers"
    : "https://khamco-shop-backend.onrender.com/customers";
  const authKey = localStorage.getItem("jwt");
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [writingDirection, setWritingDirection] = useState("ltr");
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    document.title = "New Customer | Khamco Shop";

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      }
    };
    determineWritingDirection();
  }, [clothingOptions, userLanguage]);

  const submitCustomer = async (event) => {
    event.preventDefault();
    setLoading(true);
    if (!customerName) {
      toast.error("Customer name is required");
      return;
    }
    await axios
      .post(
        apiUrl,
        {
          name: customerName,
          surname: customerSurname,
          email: customerEmail,
          phone: customerPhone,
          house_unit: houseUnit,
          governorate: governorate,
          area: area,
          country: country,
          military_status: militaryStatus,
          membership_status: membershipStatus,
          date_created: date,
          military_idnumber: militaryNumber,
          military_rank: militaryRank,
          military_department: militaryDivision,
          description: description,
          clothing_options: clothingOptions,
          waist: waist,
          natural_waist: naturalWaist,
          garment_length: garmentLength,
          half_across: halfAcross,
          chest: chest,
          trouser_waist_position: trouserWaistPosition,
          seat: seat,
          neck: neck,
          shoulder: shoulder,
          sleeve_length: sleeveLength,
          side_seam_length: sideSeamLength,
          body_rise: bodyRise,
          inside_leg: insideLeg,
          wrist: wrist,
          top_arm: topArm,
          scye_depth: scyeDepth,
          arm_sleeve_hole: armSleeveHole,
          frontal_collar: frontalCollar,
          hip: hip,
          front_rise: frontRise,
          back_rise: backRise,
          thigh: thigh,
          knee: knee,
          hem: hem,
          outseam: outseam,
          foot_size: footSize,
          head_size: headSize,
        },
        {
          headers: { Authorization: `Bearer ${authKey}` },
        }
      )
      .then((response) => {
        toast.success(response.data.message);
        if (apiUrl.includes("create-dependent")) {
          navigate(`/customers/${response.data.customer._id}`);
        } else {
          navigate(
            `/customers/dependents/dependents-prompt/${response.data.customer._id}`
          );
        }
        setLoading(false);
      })
      .catch((error) => {
        setLoading(false);
        toast.error(error.message);
      });
  };

  return (
    <>
      <Navbar />
      <main>
        <section
          className="p-[4rem] grid justify-center items-center"
          dir={writingDirection}
        >
          <div className="row">
            <Link to="/customers">
              <i className="fas fa-arrow-left"></i> Customers
            </Link>
          </div>
          <h1 className="title">New Customer</h1>
          <form
            className="flex flex-col justify-center items-center w-[90vw]"
            onSubmit={submitCustomer}
          >
            <CustomerForm
              customer={null}
              membershipStatus={membershipStatus}
              setCustomerName={setCustomerName}
              setCustomerSurname={setCustomerSurname}
              setCustomerEmail={setCustomerEmail}
              setCustomerPhone={setCustomerPhone}
              setHouseUnit={setHouseUnit}
              setGovernorate={setGovernorate}
              setArea={setArea}
              setCountry={setCountry}
              militaryStatus={militaryStatus}
              setMilitaryStatus={setMilitaryStatus}
              setMilitaryNumber={setMilitaryNumber}
              setMilitaryRank={setMilitaryRank}
              setMilitaryDivision={setMilitaryDivision}
              setMembershipStatus={setMembershipStatus}
              setDate={setDate}
              clothingOptions={clothingOptions}
              setClothingOptions={setClothingOptions}
              setDescription={setDescription}
              areas={areas}
              setAreas={setAreas}
              setWaist={setWaist}
              setNaturalWaist={setNaturalWaist}
              setGarmentLength={setGarmentLength}
              setHalfAcross={setHalfAcross}
              setChest={setChest}
              setTrouserWaistPosition={setTrouserWaistPosition}
              setSeat={setSeat}
              setNeck={setNeck}
              setShoulder={setShoulder}
              setSleeveLength={setSleeveLength}
              setSideSeamLength={setSideSeamLength}
              setBodyRise={setBodyRise}
              setInsideLeg={setInsideLeg}
              setWrist={setWrist}
              setTopArm={setTopArm}
              setScyeDepth={setScyeDepth}
              setArmSleeveHole={setArmSleeveHole}
              setFrontalCollar={setFrontalCollar}
              setHip={setHip}
              setFrontRise={setFrontRise}
              setBackRise={setBackRise}
              setThigh={setThigh}
              setKnee={setKnee}
              setHem={setHem}
              setOutseam={setOutseam}
              setHeadSize={setHeadSize}
              setFootSize={setFootSize}
              loading={loading}
            />
          </form>
        </section>
      </main>
      <Footer />
    </>
  );
};

export default CreateCustomer;
