import { useState } from "react";
import { useStoreActions, useStoreState } from "easy-peasy";

const Clothing = ({ label, imageUrl }) => {
  const [isChecked, setIsChecked] = useState(false);
  // Generate a unique ID for each Clothing component
  const inputId = `clothing_item_${label.replace(/\s+/g, "_").toLowerCase()}`;
  const itemCount = useStoreState((state) => state.itemCount);
  const setItemCount = useStoreActions((actions) => actions.setItemCount);
  const add = useStoreActions((actions) => actions.addItemCount);
  const remove = useStoreActions((actions) => actions.addItemCount);

  return (
    <>
      <label
        htmlFor={inputId}
        className={`w-[10rem] md:w-[8rem] flex flex-col items-center gap-2 cursor-pointer rounded-xl border-2 ${
          isChecked ? "border-2 border-khamco-primary bg-[#ccc]" : ""
        }`}
        onClick={add}
      >
        <img src={imageUrl} alt={label} />
        <p className="text-sm">{label}</p>
        <input
          type="checkbox"
          id={inputId}
          onChange={() => {
            setIsChecked(!isChecked);
            if (isChecked) {
              setItemCount((itemCount) => itemCount + 1);
            }
          }}
          className="accent-khamco-primary"
        />
      </label>
    </>
  );
};

export default Clothing;
