import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import Navbar from "../../../components/Navbar";
import Footer from "../../../components/Footer";
import axios from "axios";
import { useStoreState } from "easy-peasy";
import { toast } from "react-toastify";
import MeasurementList from "./MeasurementList";

const Measurements = () => {
  const { id } = useParams();
  const userLanguage = useStoreState((state) => state.userLanguage);
  const [writingDirection, setWritingDirection] = useState("ltr");
  const [customer, setCustomer] = useState(null);
  const [measurements, setMeasurements] = useState(null);
  const apiUrl = window.location.origin.includes("localhost")
    ? `http://localhost:5000/customers/${id}/measurements`
    : `https://khamco-shop-backend.onrender.com/customers/${id}/measurements`;
  const authKey = localStorage.getItem("jwt");

  useEffect(() => {
    const fetchData = async () => {
      await axios
        .get(apiUrl, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setCustomer(response.data.customer);
          setMeasurements(response.data.measurements);
          document.title = `All Measurements for ${response.data.customer.name}`;
        })
        .catch((error) => {
          toast.error(error.message);
        });
    };

    const determineWritingDirection = () => {
      if (userLanguage === "ar" || userLanguage === "ur") {
        setWritingDirection("rtl");
      } else {
        setWritingDirection("ltr");
      }
    };
    determineWritingDirection();
    return () => {
      fetchData();
    };
  }, [apiUrl, authKey, userLanguage]);

  return (
    <>
      <Navbar />
      <main>
        <section className="customer-details" dir={writingDirection}>
          {customer && measurements && (
            <>
              <div className="utilities">
                <Link to={`/customers/${customer._id}`}>
                  <i className="fas fa-arrow-left"></i> Customer{" "}
                  {`${customer.name} ${customer.surname}`}
                </Link>
              </div>
              <h1 className="title">
                {`Measurements for ${`${customer.name} ${customer.surname}`}`}
              </h1>
              <div className="khamco-card grid-cols-1">
                <div className="utilities">
                  <Link
                    to={`/customers/${customer._id}/create-measurement`}
                    className="edit-btn m-auto"
                  >
                    New Measurement <i className="fas fa-plus"></i>
                  </Link>
                </div>
                {measurements && measurements.length > 0 ? (
                  measurements.map((measurement, index) => (
                    <div
                      className="flex flex-col gap-[1rem] bg-red-400 w-2/4 m-auto p-[1rem] rounded-[1rem]"
                      key={index}
                    >
                      <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                        <strong>Date</strong>
                        {new Date(measurement.date_created).toLocaleDateString(
                          "en-us",
                          {
                            month: "long",
                            year: "numeric",
                            day: "numeric",
                          }
                        )}
                      </span>
                      <span className="inline-flex justify-start gap-[2rem] text-balance flex-wrap">
                        <strong>Notes</strong>
                        <p>{measurement.description}</p>
                      </span>
                      <div
                        // className="utilities"
                        style={{
                          // borderBottom: "0.2rem solid gray",
                          // paddingBottom: "1rem",
                        }}
                      >
                        <Link
                          to={`/customers/${customer._id}/measurements/${measurement._id}`}
                          className="edit-btn"
                        >
                          View
                        </Link>
                      </div>
                    </div>
                  ))
                ) : (
                  <p>No measurements for this customer</p>
                )}
              </div>
            </>
          )}
        </section>
      </main>
      <Footer />
    </>
  );
};

export default Measurements;
