import React, { useEffect, useRef, useState } from "react";
import Navbar from "../../components/Navbar";
import AdminPanel from "../../components/AdminPanel";
import Footer from "../../components/Footer";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { Chart, registerables } from "chart.js";
import { toast } from "react-toastify";
Chart.register(...registerables);

const CustomersData = () => {
  const [militaryCustomers, setMilitaryCustomers] = useState([]);
  const [civilianCustomers, setCivilianCustomers] = useState([]);
  const [averageCollarSize, setAverageCollarSize] = useState(0);
  const [averageHeight, setHeight] = useState(0);
  const [popularAreas, setPopularAreas] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [invoices, setInvoices] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState("Geography Report");
  const apiURL = window.location.origin.includes("localhost")
    ? "http://localhost:5000/admin/customers_data"
    : "https://khamco-shop-backend.onrender.com/admin/customers_data";
  const chartRef = useRef(null);
  const authKey = localStorage.getItem("jwt");
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    document.title = "Admin Customer Data - Khamco Shop";
    const fetchCustomerData = async () => {
      await axios
        .get(apiURL, {
          headers: { Authorization: `Bearer ${authKey}` },
        })
        .then((response) => {
          setCustomers(response.data.customers);
          setMilitaryCustomers(response.data.militaryCustomers);
          setCivilianCustomers(response.data.civilianCustomers);
          setInvoices(response.data.invoices);
          setPopularAreas(response.data.popularAreas);
          setAverageCollarSize("Coming Soon");
          setHeight("Coming Soon");
          setIsLoading(false);
        })
        .catch((error) => {
          if (error.response.status === 403) {
            toast.warn("You are not authorized to access that page");
            navigate("/");
          } else {
            toast.error("There was a problem with the fetch operation.");
          }
          return;
        });
    };

    fetchCustomerData();
  }, [apiURL, authKey, navigate]);

  useEffect(() => {
    const ctx = chartRef.current.getContext("2d");

    const chart = new Chart(ctx, {
      type: "scatter",
      data: {
        datasets: [
          {
            label: "Customers",
            data:
              customers &&
              customers.map((customer) => ({
                x: customer.height,
                y: customer.collar,
                name: `${customer.name} ${customer.surname}`,
              })),
            backgroundColor: "#283688",
            borderColor: "#EC1664",
            borderWidth: 1,
            pointRadius: 10,
          },
        ],
      },
      options: {
        scales: {
          y: {
            title: {
              display: true,
              text: "Height in Inches",
              color: "black",
              font: {
                size: 16,
              },
            },
            grid: {
              color: "#EC1664",
            },
            ticks: {
              color: "blue",
              font: {
                size: 14,
              },
            },
          },
          x: {
            title: {
              display: true,
              text: "Collar Diameter in Inches",
              color: "black",
              font: {
                size: 16,
              },
            },
            grid: {
              color: "#EC1664",
            },
            ticks: {
              color: "blue",
              font: {
                size: 14,
              },
            },
          },
        },
        plugins: {
          tooltip: {
            callbacks: {
              label: function (context) {
                return context.dataset.data[context.dataIndex].name;
              },
              afterLabel: function (context) {
                return `Height: ${context.parsed.x}, Collar: ${context.parsed.y}`;
              },
            },
          },
          legend: {
            display: true,
            labels: {
              color: "black",
              font: {
                size: 20,
              },
            },
          },
        },
      },
    });

    const chartElement = chartRef.current;

    // Event listener for click events on the chart
    const handleClick = (event) => {
      const clickedPoint = chart.getElementsAtEventForMode(
        event,
        "nearest",
        { intersect: true },
        true
      );
      if (clickedPoint.length > 0) {
        const clickedCustomerIndex = clickedPoint[0].index;
        const clickedCustomer = customers[clickedCustomerIndex];
        window.open(`/customers/${clickedCustomer._id}`, "_blank");
      }
    };

    chartElement.addEventListener("click", handleClick);

    return () => {
      chartElement.removeEventListener("click", handleClick);
      chart.destroy();
    };
  }, [customers]);

  return (
    <>
      <Navbar />
      <main className="p-[4rem]">
        <AdminPanel />
        {isLoading &&
          new Array(3).fill(
            <div className="flex items-center my-[2rem] m-auto flex-wrap w-full bg-[#bebebe80] py-[4rem] px-[2rem] shadow-md shadow-[#333] rounded-[0.5rem] justify-evenly gap-[2rem] animate-pulse"></div>
          )}
        <section className="admin-dashboard font-arial">
          <div className="grid md:grid-cols-2 gap-[4rem] justify-center items-center w-[70vw] m-auto">
            <div className="metric">
              {militaryCustomers && (
                <>
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    Military Customers
                  </h2>
                  <h2 className="text-xl font-semibold text-green-500">
                    {militaryCustomers.length}
                  </h2>
                </>
              )}
            </div>
            <div className="metric">
              {civilianCustomers && (
                <>
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    Civilian Customers
                  </h2>
                  <h2 className="text-xl font-semibold text-green-500">
                    {civilianCustomers.length}
                  </h2>
                </>
              )}
            </div>
            <div className="metric">
              {averageCollarSize && (
                <>
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    Average Collar Size
                  </h2>
                  <p>Inches</p>
                  <h2 className="text-xl font-semibold text-green-500">
                    {averageCollarSize}
                  </h2>
                </>
              )}
            </div>
            <div className="metric">
              {averageHeight && (
                <>
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    Average Height
                  </h2>
                  <p>Inches</p>
                  <h2 className="text-xl font-semibold text-green-500">
                    {averageHeight}
                  </h2>
                </>
              )}
            </div>
            <div className="metric">
              {popularAreas[0] && (
                <>
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    Popular Governorates
                  </h2>
                  <p>{popularAreas[0].total} customers</p>
                  <h2 className="text-xl font-semibold text-green-500">
                    {popularAreas[0]._id}
                  </h2>
                </>
              )}
            </div>
            <div className="metric">
              {popularAreas[0] && (
                <>
                  <h2 className="text-khamco-secondary text-2xl font-bold">
                    Popular Areas
                  </h2>
                  <p>{popularAreas[0].areas[0].count} customers</p>
                  <h2 className="text-xl font-semibold text-green-500">
                    {popularAreas[0].areas[0].area}
                  </h2>
                </>
              )}
            </div>
          </div>
          <div className="search-engine mt-[4rem]">
            <select
              className="search-input"
              onChange={(e) => setSelectedIndex(e.target.value)}
            >
              <option disabled>-- Select a report --</option>
              <option value="Geography Report">Geography Report</option>
              <option value="Customer Report">Customer Report</option>
              <option value="Invoice Report">Invoice Report</option>
              <option value="Data Chart">Data Chart</option>
            </select>
          </div>
          {selectedIndex === "Geography Report" && (
            <table className="fadeIn">
              <caption>Geography Report</caption>
              <thead>
                <tr>
                  <td>Governorate</td>
                  <td>Country</td>
                  <td>Total Customers</td>
                </tr>
              </thead>
              <tbody>
                {popularAreas &&
                  popularAreas.map((area, index) => (
                    <tr key={index}>
                      <td>{area._id}</td>
                      <td>Kuwait</td>
                      <td>{area.total} customer(s)</td>
                    </tr>
                  ))}
              </tbody>
            </table>
          )}
          {selectedIndex === "Customer Report" && (
            <table className="fadeIn">
              <caption>Customer Data</caption>
              <thead>
                <tr>
                  <td>Customer</td>
                  <td>Status</td>
                  <td>Creation Date</td>
                  <td>Membership</td>
                  <td>Total Dependents</td>
                  <td>Total Spent</td>
                </tr>
              </thead>
              <tbody>
                {customers &&
                  customers.map((customer, index) => (
                    <tr key={index}>
                      <td>
                        <Link to={`/customers/${customer.id}`} target="_blank">
                          {customer.name} {customer.surname}{" "}
                          <i className="fas fa-external-link"></i>
                        </Link>
                      </td>
                      <td className="capitalize">{customer.status}</td>
                      <td>
                        {new Date(customer.date_created).toLocaleDateString(
                          "en-US",
                          {
                            month: "long",
                            year: "numeric",
                            day: "numeric",
                          }
                        )}
                      </td>
                      {customer.membership_status === "VIP" ? (
                        <td>
                          {customer.membership_status}{" "}
                          <i className="fas fa-star blink"></i>
                        </td>
                      ) : (
                        <td>{customer.membership_status}</td>
                      )}
                      <td>{customer.dependents.length}</td>
                      <td>
                        KWD{" "}
                        {new Intl.NumberFormat("en-US").format(
                          customer.amount_spent
                        )}
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          )}
          {selectedIndex === "Invoice Report" && (
            <table className="fadeIn">
              <caption>Recent Activity</caption>
              <thead>
                <tr>
                  <td>Invoice ID</td>
                  <td>Customer</td>
                  <td>Date</td>
                  <td>Contact</td>
                  <td>Payment Status</td>
                  <td>Amount Paid</td>
                </tr>
              </thead>
              <tbody>
                {invoices &&
                  invoices.map((invoice, index) => (
                    <tr key={index}>
                      <td>
                        <a
                          href={`/invoices/${invoice.id}`}
                          target="_blank"
                          rel="noreferrer"
                        >
                          {invoice.uniqueId}{" "}
                          <i className="fas fa-external-link"></i>
                        </a>
                      </td>
                      <td>
                        {invoice.customer_name} {invoice.customer_surname}
                      </td>
                      <td>
                        {new Date(invoice.date).toLocaleDateString("en-US", {
                          month: "long",
                          day: "numeric",
                          year: "numeric",
                        })}
                      </td>
                      <td>
                        <a href={`tel:${invoice.customer_phone}`}>
                          {invoice.customer_phone}
                        </a>
                      </td>
                      <td
                        className={`${
                          invoice.payment_status === "Paid"
                            ? "text-green-500"
                            : invoice.payment_status === "Partially Paid"
                            ? "text-orange-500"
                            : "text-red-500"
                        }`}
                      >
                        {invoice.payment_status}
                      </td>
                      <td>KWD {invoice.amount_paid}</td>
                    </tr>
                  ))}
              </tbody>
            </table>
          )}
          <div
            className={`fadeIn ${
              selectedIndex === "Data Chart" ? "block" : "hidden"
            }`}
          >
            <canvas
              id="customerChart"
              width="400"
              height="150"
              ref={chartRef}
            ></canvas>
          </div>
        </section>
      </main>
      <Footer />
      <script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
    </>
  );
};

export default CustomersData;
