import React, { useState } from 'react'
import Navbar from '../../components/Navbar'
import AdminPanel from '../../components/AdminPanel'
import UserForm from './UserForm';
import Footer from '../../components/Footer';

const EditUser = () => {
    const [message, setMessage] = useState("");
    const [checkMaster, setCheckMaster] = useState(null);
    const [authorizedUser, setAuthorizedUser] = useState(null);

  return (
    <><Navbar /><main>
          <AdminPanel />
          <p class="message">
              {message}
          </p>
          <section class="khamco-wrapper">
              <h1 class="title">Edit Tailor Details</h1>
              <form action="/admin/<%= user.id %>?_method=PUT" method="POST" className="flex flex-col justify-center items-center w-[90vw]">
                  <UserForm checkMaster={checkMaster} authorizedUser={authorizedUser} />
              </form>
          </section>
      </main><Footer /></>
  )
}

export default EditUser
