import React from 'react'

const MeasurementItem = ({icon, metric, currentUnit}) => {
  return (
    <span className="flex flex-col justify-start gap-[2rem] text-balance flex-wrap border-2 border-[black] rounded-lg">
          <img src={icon} alt="" className="w-[12rem] border-2 border-khamco-primary" />
          <div className="flex justify-between">
          <strong>Dishdasha Garment Length: </strong>
          {metric}{" "}
          {currentUnit === "Centimeters" ? "cm" : "in."}
          </div>
        </span>
  )
}

export default MeasurementItem
