import React, { useState } from 'react'
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import FormFields from "./FormFields";

const Edit = () => {
    const [writingDirection, setWritingDirection] = useState("ltr");
    const product = {
        name: "Vans"
    }
    const [message, setMessage] = useState("Hello World");
  return (
    <><Navbar /><main>
          <section class="khamco-wrapper" dir={writingDirection}>
              <div class="utilities">
                  <a href="/products/<%= product.id %>"><i class="fas fa-arrow-left"></i> {product.name}</a>
              </div>
              <h1 class="title">Edit Product Details</h1>
              <p class="message">
                  {message}
              </p>
              <form action="/products/<%= product.id %>?_method=PUT" method="POST">
                  <FormFields />
              </form>
          </section>
      </main><Footer /></>
  )
}

export default Edit
